$sql[] = "INSERT INTO `{$database['prefix']}functions` (`id`, `name`, `code`, `type`, `filetouse`) VALUES (33, 'Online Users', '<div  style=\"text-align:center\">\r\n<strong>{\$userlist[0]}</strong> Registered {if \$userlist[0] != 1}Users{else}User{/if}<br /><strong>{\$userlist[1]}</strong> {if \$userlist[1] != 1}Guests{else}Guest{/if}\r\n{if \$userlist[0] > 0}<br /><br />\r\n<strong>Users Online:</strong><br />\r\n{section name=users loop=\$userlist[0]+2 start=2}\r\n {if \$userlist[users].uname != \$usersname && \$usersname != \"Guest\"}<a href=\"index.php?page=pmmain&amp;action=typepm&amp;user={\$userlist[users].uname}\" title=\"Send a PM to {\$userlist[users].uname}\">{\$userlist[users].uname}</a>{else}{\$userlist[users].uname}{/if}{if \$smarty.section.users.index == \$userlist[0]+1 || \$userlist[0] <= 1}{else}, {/if}\r\n {if \$smarty.section.users.iteration % 2 == 0}<br />{/if}\r\n{/section}{/if}\r\n</div>', 1, 'onlineusers');";
