$sql[] = "INSERT INTO `{$database['prefix']}functions` (`id`, `name`, `code`, `type`, `filetouse`) VALUES (36, 'Scouts Seach Box', '<!-- Start Of Scouts Search Code -->\r\n<div>\r\n<form method=\"get\" action=\"http://www.scoutsearch.org.uk\">\r\n<img src=\"{\$templateinfo.imagedir}logo_mini_exsite.gif\" style=\"width:115px; height:71px\" alt=\"Scouts Search\" /><br />\r\n<input type=\"text\" name=\"qry\" size=\"28\" class=\"inputbox\" />\r\n<input type=\"submit\" value=\"Go\" class=\"button\" />\r\n<input type=\"hidden\" name=\"from\" value=\"{\$config.siteaddress}\" />\r\n<input type=\"hidden\" name=\"mod\" value=\"search\" />\r\n<input type=\"hidden\" name=\"loc\" value=\"World-Wide\" />\r\n</form>\r\n<a href=\"http://www.scoutsearch.org.uk\"></a>\r\n</div>\r\n<!-- End of Scouting Search Code -->', 1, '');";

$sql[] = "INSERT INTO `{$database['prefix']}help` (`page`, `title`, `help`, `id`) VALUES ('static_content', 'Static Pages', 'This is a non-interactive page. As such there is nothing that you as a user needs to know about this page, except that all you can do with it is read it.<br />', 1);";
$sql[] = "INSERT INTO `{$database['prefix']}help` (`page`, `title`, `help`, `id`) VALUES ('calender', 'The Calendar', 'The calendar is rather easy to use.At the top there are 5 buttons and two drop down boxes.The buttons do exactly as they say. They take you to the next/previous month or year. There is also a button labeled \"Goto Today\" which will take you to the calendar entry for today.The two drop down boxes can be used to select a month/year without needing to scroll through all the months/years between where you are on the calendar and where you want to be.Clicking on a item on the calendar that is a link will take you to a page that contains alot more information about that particular event.', 2);";
$sql[] = "INSERT INTO `{$database['prefix']}help` (`page`, `title`, `help`, `id`) VALUES ('patrolarticle', 'Articles', 'This page shows you a list of articles.Clicking on the articles title will take you to the article its self.On the article page you can read the article, look at the photos for the article (If there are any) and post a comment about the article (If your allowed to that is)', 3);";
$sql[] = "INSERT INTO `{$database['prefix']}help` (`page`, `title`, `help`, `id`) VALUES ('photos', 'Photo Albums', 'The drop down box is used to select what photo album you want to view.Just select the photo album and you will be taken to it. You can then use the links under the drop down box to browse through the album.', 4);";
$sql[] = "INSERT INTO `{$database['prefix']}help` (`page`, `title`, `help`, `id`) VALUES ('downloads', 'Downloads', 'The downloads page shows you all the different categories available.Clicking on a category will take you to it where you will be able to download the file that you want by clicking on \"download\"', 5);";
$sql[] = "INSERT INTO `{$database['prefix']}help` (`page`, `title`, `help`, `id`) VALUES ('search', 'Search', 'Simply type what you want to search for in the box and click \"Search\"This will return all items where your search term appear.', 6);";
$sql[] = "INSERT INTO `{$database['prefix']}help` (`page`, `title`, `help`, `id`) VALUES ('patrolpages', 'Group Home Pages', 'The group home pages are a bit hard to describe as they can be different for each and every group depending on what that group has made their site look like.But one thing that might be in every on of them is the Articles and Photos', 7);";
$sql[] = "INSERT INTO `{$database['prefix']}help` (`page`, `title`, `help`, `id`) VALUES ('profile', 'Your Profile', 'This page allows you to change your profile.You can change your password, your email address and any other personal information.You can also change your theme.A theme changes the look of the entire site.', 8);";
$sql[] = "INSERT INTO `{$database['prefix']}help` (`page`, `title`, `help`, `id`) VALUES ('mythings', 'My Things', 'This pages allows you to (Depending on the permisions of your group) add articles, notices, photo albums, downloads, calendar events and news items.You can also edit/remove any items that you currently own. By clicking on the change ownership icon (<img src=\"images/owner.gif\" alt=\"Ownership Icon\" title=\"Ownership Icon\" />) you can change who the owner of the item is.', 9);";
$sql[] = "INSERT INTO `{$database['prefix']}help` (`page`, `title`, `help`, `id`) VALUES ('addphotoalbum', 'Add Photo Album', 'This page allows you to add a photo album.Simply type in the name of the album and select where it will appear and click on \"Add Album\"You will then be taken to a page where you can add your photos to the album', 10);";
$sql[] = "INSERT INTO `{$database['prefix']}help` (`page`, `title`, `help`, `id`) VALUES ('addarticle', 'Add Article', 'This page allows you to add a article.Just fill in the fields and write your article.You can attach a existing album by selecting it from the drop down box. You can also attach a title picture using the picture field.', 11);";
$sql[] = "INSERT INTO `{$database['prefix']}help` (`page`, `title`, `help`, `id`) VALUES ('addevent', 'Add Event', 'This page allows you to add a event.Simply fill in the required fields and click on \"Add item\"', 13);";
$sql[] = "INSERT INTO `{$database['prefix']}help` (`page`, `title`, `help`, `id`) VALUES ('mypatrol', 'My Patrol', 'This page displays a list of all the people in your group. You can email people, or send a personal message to them. You can also send a email, or personal message to everyone that is in your group. Any emails that you send will also be forwarded to the webmaster for security purposes.', 14);";
$sql[] = "INSERT INTO `{$database['prefix']}help` (`page`, `title`, `help`, `id`) VALUES ('record', 'Scout Record', 'This page simply shows what items of your advancement badges you still need to achieve.If something is incorrectly marked as incompleted please contact the webmaster.It also shows all the badges that you have achieved,', 15);";
$sql[] = "INSERT INTO `{$database['prefix']}help` (`page`, `title`, `help`, `id`) VALUES ('register', 'Registration', 'Simply fill in all the required fields and click on next. Once you are done the system will tell you.And it will email you your username and password to the email address that you specified.', 16);";
$sql[] = "INSERT INTO `{$database['prefix']}help` (`page`, `title`, `help`, `id`) VALUES ('forgot', 'Forgotten password or username', 'Simply type in the email address that you registered with.You will then recieve a email address with your username and a new randomly generated password. Please logon and change your password to something that you can easily remember, but that can not be easily broken or guessed by someone else.', 17);";


$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (1, 1, '{if \$number_of_albums > 0}\r\n    <table width=\"100%\" cellspacing=\"0\" cellpadding=\"2\">\r\n        <tr style=\"height:155px\">\r\n        {section name=albums loop=\$number_of_albums}\r\n            {if \$patrolpages != 1}<td width=\"50%\"><div align=\"center\"><a href=\"index.php?page=photos&amp;album={\$albums[albums].ID}\" title=\"View {\$albums[albums].album_name}\"><img border=\"0\" src=\"thumbnail.php?pic={\$albums[albums].randomphoto}\" alt=\"View {\$albums[albums].album_name}\" /><br />\r\n            {\$albums[albums].album_name}</a>\r\n            </div></td>\r\n            {else}<td width=\"50%\"><div align=\"center\"><a href=\"index.php?page=patrolpages&amp;patrol={\$patrolname}&amp;content=photos&amp;album={\$albums[albums].ID}\" title=\"View {\$albums[albums].album_name}\"><img border=\"0\" src=\"thumbnail.php?pic={\$albums[albums].randomphoto}\" alt=\"View {\$albums[albums].album_name}\" /><br />\r\n            {\$albums[albums].album_name}</a>\r\n            </div></td>\r\n            {/if}\r\n        {if (\$smarty.section.albums.iteration % 2 == 0)}</tr><tr>{/if}\r\n        {/section}\r\n        {if \$number_of_albums % 2 != 0}<td width=\"50%\">&nbsp;</td>{/if}\r\n        </tr>\r\n    </table>\r\n{else}\r\n<div style=\"text-align:left\">There are no photo albums here yet.</div>\r\n {/if}', 1);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (3, 1, '{if \$showart == ''no''}\r\n{if \$patrol != '''' && \$patrol != ''general''}\r\n	<h1>Articles written by {\$patrol}</h1>\r\n{else}\r\n	<h1>General articles</h1>\r\n{/if}\r\n{if (\$numarts) } \r\n{if \$patrol != '''' && \$patrol != ''general''}\r\n	<h2>This patrol has {\$numarts} {\$art}</h2>\r\n{else}\r\n	<h2>The troop has {\$numarts} general {\$art}</h2>\r\n{/if}\r\n		<table border=\"0\" cellspacing=\"1\" cellpadding=\"3\" class=\"table\" width = \"100%\">\r\n		  <tr>\r\n			<th>Title of article</th>\r\n			<th>Date posted</th>\r\n			<th>Author of article</th>\r\n		  </tr>\r\n		{section name=patrolarticles loop=\$numarts}\r\n			 <tr>\r\n				<td class=\"row\">{if \$patrol != '''' && \$patrol != ''general''}<a href=\"index.php?page=patrolpages&amp;patrol={\$patrolname}&amp;content=patrolarticle&amp;id={\$patrolarts[patrolarticles].ID}\">{\$patrolarts[patrolarticles].title}</a>{else}<a href=\"index.php?page=patrolarticle&amp;id={\$patrolarts[patrolarticles].ID}\">{\$patrolarts[patrolarticles].title}</a>{/if}</td>\r\n				<td class=\"row\">{\$patrolarts[patrolarticles].date_post+\$timeoffset|date_format:\"%B %e, %Y\"}&nbsp;</td>\r\n				<td class=\"row\">{\$patrolarts[patrolarticles].author}&nbsp;</td>\r\n		   </tr>\r\n		 {/section}\r\n		</table>\r\n	 {/if}\r\n{else}	\r\n<a name=\"top\"></a>\r\n	<table width=\"100%\" border=\"0\" align=\"center\"  class=\"content\"> \r\n	  <tr>\r\n		<th colspan=\"3\">{\$article.title}</th>\r\n	  </tr> \r\n	  <tr>\r\n		<td width=\"20%\" align=\"center\"><div align=\"center\"><strong>Date Posted</strong><br />{\$article.date_post|date_format:\"%B %e, %Y\"}<br />\r\n		 {if (\$article.date_happen)}<strong>Date of event</strong><br />{\$article.date_happen|date_format:\"%B %e, %Y\"}</div></td>{/if}\r\n		<td align=\"center\">{if \$article.pic != \"\"}<div style=\"text-align:center\"><img src=\"getphoto.php?pic={\$article.ID}&amp;where=article&amp;maxsizex={\$config.photox}&amp;maxsizey={\$config.photoy}\" /></div>{else}&nbsp;{/if}</td>\r\n		<td width =\"20%\" align=\"center\"><strong>Author</strong><br />{\$article.author}<br />\r\n	</td>\r\n    <tr>\r\n        <td colspan=\"3\" align=\"center\">{if \$article.album_id && \$number_of_photos > 0}<a href=\"#photos\">Photos</a>&nbsp;&nbsp;&nbsp;{/if}{if (\$comviewallowed && \$numcom > 0) || \$compostallowed}<a href=\"#comments\">View/Post Comments</a>{/if}</td>\r\n    </tr>\r\n	  <tr><td height=\"25\"></td></tr>\r\n	  <tr>\r\n		<td class=\"bignewgray\" colspan=\"3\">\r\n		 {\$article.detail}\r\n		 <br /><br />\r\n	</td>\r\n	  </tr>\r\n		{if \$article.album_id && \$number_of_photos > 0} \r\n		 <tr>\r\n	 <th colspan=\"3\">\r\n	 <a name=\"photos\"></a>Photo''s for article\r\n	  </th></tr>\r\n<tr>\r\n    <td align=\"center\" colspan=\"3\">				\r\n		<input value=\"&lt;&lt;Back\" name=\"B2\" onclick=\"backward()\" type=\"button\" class=\"button\"  /> <input value=\"Next&gt;&gt;\" name=\"B1\" onclick=\"forward()\" type=\"button\" class=\"button\" /> <br />			  <input value=\"Start Over\" name=\"B2\" onclick=\"which=1;backward();return false\" type=\"button\" class=\"button\" /><br />\r\n\r\n    </td>\r\n</tr>\r\n<tr style=\"height:{\$config.photoy+5}px\" valign=\"middle\">\r\n<td align=\"center\" colspan=\"3\">\r\n<img src=\"getphoto.php?pic={\$photo[0].ID}&amp;maxsizex={\$config.photox}&amp;maxsizey={\$config.photoy}\" name=\"photoslider\" alt=\"{\$photo[0].caption}\" title=\"{\$photo[0].caption}\" />\r\n    </td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\" colspan=\"3\">\r\n        <div id=\"lay\" align=\"center\"><strong>Date Posted: {\$photo[0].date+\$timeoffset|date_format:\"%B %e, %Y\"}</strong><br />{if \$photo[0].caption != \"\"}{\$photo[0].caption}{else}No Caption{/if}</div>\r\n				<!--Show album-->\r\n			  <div style=\"text-align:center\">		  \r\n<script type=\"text/javascript\">\r\nvar photos=new Array()\r\nvar captions=new Array()\r\nvar dates=new Array()\r\nvar which=0\r\nvar whatphoto=1\r\nvar num = {\$number_of_photos}\r\n/*Change the below variables to reference your own images. You may have as many images in \r\nthe slider as you wish*/\r\n{section name=pics loop=\$number_of_photos}\r\nphotos[{\$smarty.section.pics.index}]=\"getphoto.php?pic={\$photo[pics].ID}&maxsizex={\$config.photox}&maxsizey={\$config.photoy}\"\r\ncaptions[{\$smarty.section.pics.index}]=\"{if \$photo[pics].caption != \"\"}{\$photo[pics].caption}{else}No Caption{/if}\"\r\ndates[{\$smarty.section.pics.index}] = \"{\$photo[pics].date+\$timeoffset|date_format:\"%B %e, %Y\"}\"\r\n{/section}\r\npreload(photos)\r\n\r\n{literal}\r\nfunction numphoto() \r\n{\r\n    whatphoto = which + 1\r\n    window.status=''Picture '' + whatphoto + '' of ''  + num\r\n}\r\n\r\nfunction backward()\r\n{\r\n    if (which>0)\r\n    {\r\n        window.status=''''\r\n        which--\r\n        numphoto()\r\n        document.images.photoslider.src=photos[which]\r\n        document.images.photoslider.alt=captions[which]\r\n        document.images.photoslider.title=captions[which]\r\n        document.getElementById(\"lay\").innerHTML = \"<strong>Date Posted: \" + dates[which] + \"<\\/strong><br \\/>\" +captions[which];\r\n    }\r\n}\r\nfunction forward()\r\n{\r\n    if (which<photos.length-1)\r\n    {\r\n        which++\r\n        numphoto()\r\n        document.images.photoslider.src=photos[which]\r\n        document.images.photoslider.alt=captions[which]\r\n        document.images.photoslider.title=captions[which]\r\n        document.getElementById(\"lay\").innerHTML = \"<strong>Date Posted: \" + dates[which] + \"<\\/strong><br \\/>\" +captions[which];\r\n    }\r\n    else \r\n    {\r\n        window.status=''End of photos'';\r\n    }\r\n}\r\n{/literal}\r\n</script> \r\n              <a href=\"#top\"><small>Back to Top</small></a>\r\n			 </div>\r\n		</td></tr> {/if}\r\n        {if (\$comviewallowed && \$numcom > 0) || \$compostallowed}\r\n        <tr>\r\n            <th colspan=\"3\"><a name=\"comments\"></a>Comments</th>\r\n        </tr>\r\n            {if \$comviewallowed}\r\n                {section name=comments loop=\$numcom}\r\n                <tr>\r\n                    <td colspan=\"3\" align = \"left\">\r\n                     <div class=\"commentname\"><span style=\"font-weight:bold;\">Name:</span> {\$com[comments].uname}</div>\r\n                     <div class=\"commentdate\"><span style=\"font-weight:bold;\">Date posted:</span> {\$com[comments].date+\$timeoffset|date_format:\"%B %e, %Y %H:%M\"}</div>\r\n                     <div class=\"commentself\"><span style=\"font-weight:bold;\">Comment:</span> {\$com[comments].comment}</div>\r\n                    </td>\r\n                </tr>\r\n                <tr><td>&nbsp;</td></tr>\r\n                {/section}\r\n            {/if}\r\n            {if \$compostallowed}\r\n                <tr>\r\n                    <td colspan=\"3\" align=\"center\">\r\n                    <form name=\"form1\" action=\"{\$editFormAction}\" method=\"post\">\r\n                     Post your comment: <br />\r\n                     <textarea name=\"comment\" cols=\"50\" rows=\"5\" class=\"inputbox\"></textarea><br /> \r\n                     <input type=\"submit\" name=\"submit\" id=\"submit\" value=\"Post Comment\" class=\"button\" />&nbsp;<input type=\"reset\" value=\"Cancel Comment\" id=\"reset\" class=\"button\" />\r\n                    </form>\r\n                    </td>\r\n                </tr>\r\n            {/if}\r\n            <tr>\r\n            <td colspan=\"3\" align=\"center\"><a href=\"#top\"><small>Back to Top</small></a></td>\r\n            </tr>\r\n        {/if}\r\n	</table>\r\n{/if}', 3);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (5, 1, '<a name=\"top\"></a><h1>Your scouting record</h1>\r\n<div align=\"left\">\r\n{section name=num loop=\$numadva}\r\n - <a href=\"#{\$advan[num].advancement|replace:'' '':''''}\">{\$advan[num].advancement}</a><br />\r\n{/section}\r\n{if \$numbadge > 0} - <a href=\"#badges\">Badges</a><br />{/if}</div>\r\n{if \$numadva > 0}\r\n<table width=\"100%\"  border=\"0\" cellspacing=\"1\" cellpadding=\"4\" class=\"table\">\r\n	{section name=num loop=\$numadva}\r\n	  {if \$advan[num].numitems > 0}\r\n      <tr><th colspan=\"2\" class=\"bighead\"><a name=\"{\$advan[num].advancement|replace:'' '':''''}\"></a>{\$advan[num].advancement}</th></tr>\r\n	  <tr>\r\n        <th></th>\r\n		<th width=\"83%\" scope=\"col\"  class=\"smallhead\">Requirement</th>\r\n	  </tr>\r\n	  {section name=numreq loop=\$advan[num].numitems}\r\n		  <tr valign=\"top\" align=\"center\">\r\n            {if \$advan[num].items[numreq].done}<td class=\"complete\">Completed</td>{else}<td class=\"notcomplete\">Not Complete</td>{/if}\r\n			<td class=\"row\" title=\"{\$advan[num].items[numreq].description}\"><strong>{\$advan[num].items[numreq].item}</strong>{if \$advan[num].items[numreq].comment != ''''}<br />\r\n            <span class=\"comment\">{\$advan[num].items[numreq].comment}</span>{/if}</td>\r\n		  </tr>\r\n	  {/section}\r\n      <tr>\r\n      <td colspan=\"2\" class=\"row\">\r\n        <a href=\"#top\">Back to top</a>\r\n      </td>\r\n      </tr>\r\n      {/if}\r\n	 {/section}\r\n</table>\r\n{else}\r\nNo Advancements\r\n{/if}\r\n{if \$numbadge > 0}\r\n<p>&nbsp;</p>\r\n<table width=\"100%\"  border=\"0\" cellspacing=\"1\" cellpadding=\"4\" class=\"table\">\r\n  <tr>\r\n    <th scope=\"col\" colspan=\"2\" class=\"bighead\"><a name=\"badges\"></a>Badges Earned</th>\r\n  </tr>\r\n  <tr>\r\n    <th width=\"21%\" class=\"smallhead\">Date</th>\r\n    <th width=\"79%\" class=\"smallhead\">Badge</th>\r\n  </tr>\r\n{section name=badgenum loop=\$numbadge}\r\n  <tr>\r\n    <td class=\"row\">{\$badges[badgenum].date}</td>\r\n    <td class=\"row\">{\$badges[badgenum].badge}</td>\r\n  </tr>\r\n {/section}\r\n      <tr>\r\n      <td colspan=\"2\" class=\"row\">\r\n        <a href=\"#top\">Back to top</a>\r\n      </td>\r\n      </tr>\r\n</table>\r\n{else}\r\n<div align=\"center\">You have not recieved any badges yet</div>\r\n{/if}', 4);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (4, 1, '<h1>Your Profile</h1>\r\n{if \$errors != \"\"}\r\n{\$errors}\r\n{/if}\r\n				 <form method=\"post\" name=\"scoutadd\" action=\"{\$editFormAction}\" enctype=\"multipart/form-data\">\r\n				   <table width=\"100%\" align=\"center\" border=\"0\" cellpadding=\"4\" cellspacing=\"1\" class=\"table\">\r\n					 <tr><th colspan=\"2\" align=\"center\" class=\"bighead\">Website Information</th></tr>\r\n					 <tr valign=\"baseline\">\r\n					  <td width=\"50%\" align=\"right\"  class=\"row\">User Name:</td>\r\n					  <td align=\"left\" class=\"row\">{\$uinfo.uname}</td>\r\n				     </tr>\r\n					  <tr valign=\"baseline\">\r\n					   <td align=\"right\"  class=\"row\">New Password:</td>\r\n					   <td align=\"left\" class=\"row\"><input name=\"password\" type=\"password\" size=\"25\" maxlength=\"50\" class=\"inputbox\" /></td>\r\n					  </tr>		\r\n					  <tr valign=\"baseline\">\r\n					   <td align=\"right\"  class=\"row\">Retype New Password:</td>\r\n					   <td align=\"left\" class=\"row\"><input name=\"repassword\" type=\"password\" size=\"25\" maxlength=\"50\" class=\"inputbox\" /></td>\r\n					  </tr>\r\n                      {if \$config.allowtemplate == 1 && \$numthemes > 1}\r\n					  <tr valign=\"baseline\">\r\n					    <td align=\"right\"  class=\"row\">Template:</td>\r\n					    <td align=\"left\" class=\"row\"><select name=\"theme\" id=\"theme\" class=\"inputbox\">\r\n						{section name=themes loop=\$numthemes}\r\n							<option value=\"{\$theme[themes].id}\" {if (\$uinfo.theme_id == \$theme[themes].id)}selected{/if}>{\$theme[themes].name}</option>\r\n						{/section}\r\n				        </select></td>\r\n				     </tr>			\r\n{/if}                     \r\n                    <tr>\r\n                      <td align=\"right\" class=\"row\"><div align=\"right\">Timezone </div></td>\r\n                      <td align=\"left\" class=\"row\"><div align=\"left\">\r\n                        <select name=\"zone\" id=\"zone\" class=\"inputbox\">\r\n                            {section name=zones loop=\$numzones}\r\n                                <option value=\"{\$zone[zones].id}\" {if (\$uinfo.timezone == \$zone[zones].id)}selected{/if}>{\$zone[zones].offset} Hours</option>\r\n                            {/section}\r\n                        </select>\r\n                      </div></td>\r\n                    </tr>\r\n                    <tr valign=\"baseline\">\r\n					   <td align=\"right\" class=\"row\">Is the {\$config.troopname} website allowed to send you notification emails?</td>\r\n					   <td align=\"left\" class=\"row\"><input type=\"radio\" name=\"allowemail\" id=\"allowemail\" value=\"1\" {if \$personal.allowemail != 0}checked=\"checked\"{/if} />Yes&nbsp;<input type=\"radio\" name=\"allowemail\" id=\"allowemail\" value=\"0\" {if \$personal.allowemail != 1}checked=\"checked\"{/if} />No</td>\r\n					 </tr>      \r\n					 <tr><th colspan=\"2\" align=\"center\" class=\"bighead\">Personal Information</th></tr>\r\n					 <tr valign=\"baseline\">\r\n					   <td  align=\"right\" class=\"row\">First Name:</td>\r\n					   <td align=\"left\" class=\"row\"><input name=\"firstname\" type=\"text\" size=\"40\" maxlength=\"50\" value=\"{\$personal.firstname}\" class=\"inputbox\" /></td>\r\n					 </tr>\r\n					 <tr valign=\"baseline\">\r\n					   <td  align=\"right\" class=\"row\">Last Name:</td>\r\n					   <td align=\"left\" class=\"row\"><input name=\"lastname\" type=\"text\" size=\"40\" maxlength=\"50\" value=\"{\$personal.lastname}\" class=\"inputbox\" /></td>\r\n					 </tr>\r\n					 <tr valign=\"baseline\">\r\n					   <td  align=\"right\" class=\"row\">Date of birth:</td>\r\n					   <td align=\"left\" class=\"row\"><input name=\"dob\" id=\"dob\" type=\"text\" size=\"20\" maxlength=\"20\" value=\"{\$personal.dob|date_format:\"%Y-%m-%d\"}\" class=\"inputbox\" /><button id=\"opencal\" name=\"opencal\" class=\"button\">...</button></td>\r\n					 </tr>\r\n					 <tr valign=\"baseline\">\r\n					   <td  align=\"right\" class=\"row\">Home Telephone:</td>\r\n					   <td align=\"left\" class=\"row\"><input name=\"tel\" type=\"text\" size=\"20\" maxlength=\"20\" value=\"{\$personal.tel}\" class=\"inputbox\" /></td>\r\n					 </tr>\r\n					 <tr valign=\"baseline\">\r\n					   <td  align=\"right\" class=\"row\">Cellphone Number:</td>\r\n					   <td align=\"left\" class=\"row\"><input name=\"cell\" type=\"text\" size=\"20\" maxlength=\"20\" value=\"{\$personal.cell}\" class=\"inputbox\" /></td>\r\n					 </tr>\r\n					 <tr valign=\"baseline\">\r\n					   <td align=\"right\" valign=\"top\"  class=\"row\">Address:</td>\r\n					   <td align=\"left\" class=\"row\"><textarea name=\"address\" cols=\"40\" rows=\"4\" class=\"inputbox\">{\$personal.address}</textarea></td>\r\n					 </tr>\r\n					 <tr valign=\"baseline\">\r\n					   <td  align=\"right\" class=\"row\">Email:</td>\r\n					   <td align=\"left\" class=\"row\"><input name=\"email\" type=\"text\"  size=\"40\" maxlength=\"100\" value=\"{\$personal.email}\" class=\"inputbox\" /></td>\r\n					 </tr>\r\n        {if \$personal.troopuser == 1 && \$numschemes >0}                     \r\n                    <tr>\r\n                      <td align=\"right\" class=\"row\"><div align=\"right\">Award Scheme to use:</div></td>\r\n                      <td align=\"left\" class=\"row\"><div align=\"left\">\r\n                        <select name=\"scheme\" id=\"scheme\" class=\"inputbox\">\r\n                            {section name=awards loop=\$numschemes}\r\n                                <option value=\"{\$schemes[awards].id}\" {if (\$personal.scheme == \$schemes[awards].id)}selected{/if}>{\$schemes[awards].name} Award Scheme</option>\r\n                            {/section}\r\n                        </select>\r\n                      </div></td>\r\n                    </tr>  \r\n{/if}                    \r\n					 <tr><th colspan=\"2\" align=\"center\" class=\"bighead\">Forum Settings</th></tr>\r\n                     {if \$config.avyx > 0 && \$config.avyy > 0}\r\n                    {if \$personal.avyfile != ''''}<tr valign=\"baseline\">\r\n					   <td  align=\"right\" class=\"row\" valign=\"top\">Current Avatar: </td>\r\n					   <td align=\"left\" class=\"row\"><img src=\"avatars/{\$personal.avyfile}\" alt=\"Your Avatar\" /></td>\r\n					 </tr>{/if}\r\n                     <tr valign=\"baseline\">\r\n					   <td  align=\"right\" class=\"row\">Avatar (Max size: {\$config.avyx}x{\$config.avyy}) - Leave blank if you don''t want to change it.: </td>\r\n					   <td align=\"left\" class=\"row\"><input name=\"avy\" type=\"file\" size=\"40\" maxlength=\"100\" class=\"inputbox\" /></td>\r\n					 </tr>\r\n                     {/if}\r\n					 {if \$config.sigsize > 0}<tr valign=\"baseline\">\r\n					   <td  align=\"right\" class=\"row\" valign=\"top\">Signature: </td>\r\n					   <td align=\"left\" class=\"row\"><textarea name=\"sig\" cols=\"40\" rows=\"4\" class=\"inputbox\">{\$personal.sig}</textarea></td>\r\n					 </tr>\r\n                     {/if}\r\n					 <tr valign=\"baseline\">\r\n					   <td  align=\"right\" class=\"row\">Do you want to be emailed when a new topic is posted? </td>\r\n					   <td align=\"left\" class=\"row\"><input type=\"radio\" name=\"newtopic\" id=\"newtopic\" value=\"1\" {if \$personal.newtopic != 0}checked=\"checked\"{/if} />Yes&nbsp;<input type=\"radio\" name=\"newtopic\" id=\"newtopic\" value=\"0\" {if \$personal.newtopic != 1}checked=\"checked\"{/if} />No</td>\r\n					 </tr> \r\n					 <tr valign=\"baseline\">\r\n					   <td  align=\"right\" class=\"row\">Do you want to be emailed when you recieve a new PM?</td>\r\n					   <td align=\"left\" class=\"row\"><input type=\"radio\" name=\"newpm\" id=\"newpm\" value=\"1\" {if \$personal.newpm != 0}checked=\"checked\"{/if} />Yes&nbsp;<input type=\"radio\" name=\"newpm\" id=\"newpm\" value=\"0\" {if \$personal.newpm != 1}checked=\"checked\"{/if} />No</td>\r\n					 </tr>      \r\n					 <tr valign=\"baseline\">\r\n					   <td colspan=\"2\" align=\"center\" class=\"row\"><input type=\"submit\" value=\"Update record\" class=\"button\" name=\"submit\" id=\"submit\" />&nbsp;&nbsp;<input type=\"reset\" value=\"Reset form\" class=\"button\" /></td>\r\n					 </tr>\r\n				   </table> 	\r\n</form>\r\n<script type=\"text/javascript\">\r\n  {literal}\r\n  Calendar.setup(\r\n    {\r\n      inputField  : \"dob\",         // ID of the input field\r\n      ifFormat    : \"%Y-%m-%d\",    // the date format\r\n      button      : \"opencal\",       // ID of the button\r\n      {/literal}\r\n      date         : \"{\$personal.dob}\"\r\n      {literal}\r\n    }\r\n  );\r\n  {/literal}\r\n</script>', 5);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (6, 1, '<h1>Add Article</h1>\r\n<form action=\"{\$editFormAction}\" method=\"post\" name=\"noteadd\" id=\"noteadd\" enctype=\"multipart/form-data\">\r\n				   <table width=\"100%\"  border=\"0\" cellspacing=\"2\" cellpadding=\"2\" class=\"content\">\r\n                    <tr>\r\n                      <td><div align=\"right\"><span class=\"subtitle\">Title: </span></div></td>\r\n                      <td>\r\n                        <div align=\"left\"><input name=\"title\" type=\"text\" id=\"title\" size=\"50\" value=\"\" class=\"inputbox\" /></div>\r\n                      </td>\r\n                    </tr>\r\n                    <tr>\r\n                      <td><div align=\"right\"><span class=\"subtitle\">Date of event (Leave blank if unsure) :</span></div></td>\r\n                      <td>\r\n                        <div align=\"left\"><input name=\"dh\" type=\"text\" id=\"dh\" size=\"16\" value=\"\" class=\"inputbox\" /><button id=\"opencal\" name=\"opencal\" class=\"button\">...</button></div>\r\n                      </td>\r\n                    </tr>\r\n                    <tr>\r\n                      <td><div align=\"right\"><span class=\"subtitle\">Picture: </span></div></td>\r\n                      <td>\r\n                      <div align=\"left\"><input name=\"file\" type=\"file\" size=\"50\" class=\"inputbox\" /></div>\r\n</td>\r\n                    </tr>\r\n					<tr>\r\n                      <td><div align=\"right\"><span class=\"subtitle\">Author:</span></div></td>\r\n                      <td>\r\n                       <div align=\"left\"> <input name=\"auth\" type=\"text\" id=\"auth\" size=\"50\" value=\"{\$name}\" class=\"inputbox\" /></div>\r\n                      </td>\r\n                    </tr>	\r\n					<tr>\r\n					 <td><div align=\"right\"><span class=\"subtitle\">Album to use (You may need to add the album):</span></div></td>\r\n					 <td>\r\n						<div align=\"left\"><select name=\"cat\" class=\"inputbox\">\r\n						  {if \$numalbum > 0}\r\n                            <option value=\"no\" selected>None</option>\r\n                           {else}\r\n                            <option value=\"no\" selected>You do not own any albums</option>\r\n                            {/if}\r\n                          {if \$numalbum > 0}\r\n						  {section name=albums loop=\$numalbum}\r\n						  	<option value=\"{\$albums[albums].ID}\">{\$albums[albums].album_name}</option>\r\n						  {/section}\r\n                         {/if}\r\n						</select></div>\r\n					</td>\r\n				  </tr>\r\n                  </table>\r\n				  <br />\r\n				   <h2 align=\"center\" class=\"subtitle\">Article: </h2> \r\n                    <textarea id=\"story\" name=\"story\" style=\"width: 100%; height: 50em;\"></textarea>	                   \r\n				   <div align=\"center\">\r\n					<input type=\"submit\" name=\"Submit\" value=\"Submit\" class=\"button\" />\r\n                	<input type=\"button\" name=\"Submit2\" value=\"Cancel\" onClick=\"window.location=''index.php?page=mythings''\" class=\"button\" />\r\n				  </div>\r\n</form>	 \r\n<script type=\"text/javascript\">\r\n  {literal}\r\n  Calendar.setup(\r\n    {\r\n      inputField  : \"dh\",         // ID of the input field\r\n      ifFormat    : \"%Y-%m-%d\",    // the date format\r\n      button      : \"opencal\",       // ID of the button\r\n    }\r\n  );\r\n  {/literal}\r\n</script>', 6);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (8, 1, '{if !\$success}\r\n    <h1>Add a photo album</h1>\r\n    <form action=\"{\$editFormAction}\" method=\"post\" name=\"photos\">\r\n      <table width=\"100%\"  border=\"0\" cellspacing=\"2\" cellpadding=\"2\"  class=\"content\">\r\n        <tr>\r\n          <th colspan=\"2\" align=\"center\"><div align=\"center\">Album Information </div></th>\r\n        </tr>\r\n        <tr>\r\n          <td width=\"50%\" align=\"right\">Name of album: </td>\r\n          <td align=\"left\"><div align=\"left\">\r\n            <input type=\"text\" name=\"album_name\" class=\"inputbox\" />\r\n    </div></td>\r\n        </tr>\r\n        <tr>\r\n          <td width=\"50%\" align=\"center\"><div align=\"right\">Patrol: </div></td>\r\n          <td align=\"center\"><div align=\"left\"><select name=\"patrol\" class=\"inputbox\">\r\n               {if (\$userteam == \"Admin\") || (\$userteam == \"Scouters\") || (\$userteam == \"TLs\")}\r\n                   <option value=\"All\" selected >Troop Photos</option>\r\n                    {section name=team loop=\$numteams}\r\n                        <option value=\"{\$teamname[team]}\">{\$teamname[team]}</option>\r\n                    {/section}\r\n                {else}\r\n                   {section name=team loop=\$numteams}\r\n                        {if \$userteam == \$teamname[team]} <option value=\"{\$teamname[team]}\" selected>{\$teamname[team]}</option>{/if}\r\n                    {/section}\r\n                {/if}\r\n                   <option value=\"hidden\">Hidden (So that it can only be used for articles)</option>\r\n         </select>\r\n          </div></td>\r\n        </tr>\r\n        <tr>\r\n          <td align=\"center\" colspan=\"2\"><input type=\"submit\" name=\"submit\" value=\"Add Album\" class=\"button\" />	\r\n          <input type=\"button\" name=\"Submit2\" value=\"Cancel\" onClick=\"window.location=''index.php?page=mythings''\" class=\"button\" /></td>\r\n        </tr>\r\n      </table>\r\n    </form>\r\n{else}\r\n<h1>Success</h1>\r\nYour photo album {\$album_name} has been created, click <a href=\"index.php?page=mythings&amp;cat=album&amp;id={\$albumid}\">here</a> to start adding photos to it.\r\n{/if}', 8);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (9, 1, '{\$errors}\r\n<form name=\"form\"  method=\"post\" action=\"{\$editFormAction}&amp;pagenum={\$pagenum}\">\r\n		  <table width=\"100%\" border=\"0\" align=\"center\"  class=\"table\" cellpadding=\"4\" cellspacing=\"1\">\r\n			<tr>\r\n			  <th colspan=\"2\">Step 1 - Personal Information</th>\r\n			</tr>\r\n            <tr>\r\n            <td colspan=\"2\" class=\"row\"><div align=\"center\">Items marked with a <span style=\"color: rgb(255, 0, 0);\">*</span> are compulsory<br />\r\nAll personal information supplied will only be available to the administrators of this website, and where applicable the group leader.</div>\r\n</td>\r\n            </tr>\r\n			<tr>\r\n			  <td width=\"50%\" class=\"row\" title=\"The username that you will use to log in. Please try to make this different to your real name\"><div align=\"right\">Username<span style=\"color: rgb(255, 0, 0);\">*</span>:</div></td>\r\n			  <td align=\"left\" class=\"row\" title=\"The username that you will use to log in. Please try to make this different to your real name\">\r\n                <div align=\"left\"><input name=\"usernames\" type=\"text\" id=\"usernames\" size=\"30\" maxlength=\"30\" value=\"{\$info.username}\" class=\"inputbox\" title=\"The username that you will use to log in. Please try to make this different to your real name\" /></div>			\r\n              </td>\r\n			</tr>\r\n				<tr>\r\n					<td class=\"row\"><div align=\"right\" title=\"The password that you will use to log in. Must be longer than 6 characters.\">Password<span style=\"color: rgb(255, 0, 0);\">*</span>: </div></td>\r\n					<td align=\"left\" class=\"row\" title=\"The password that you will use to log in. Must be longer than 6 characters.\"><input name=\"passwords\" type=\"password\" id=\"passwords\" size=\"30\" maxlength=\"30\" class=\"inputbox\" title=\"The password that you will use to log in. Must be longer than 6 characters.\" /></td>\r\n				</tr>\r\n				<tr>\r\n					<td class=\"row\" title=\"Retype the same password that you typed in above\"><div align=\"right\">Retype Password<span style=\"color: rgb(255, 0, 0);\">*</span>: </div></td>\r\n					<td align=\"left\" class=\"row\" title=\"Retype the same password that you typed in above\"><input name=\"repass\" type=\"password\" id=\"repass\" size=\"30\" maxlength=\"30\" class=\"inputbox\" title=\"Retype the same password that you typed in above\" /></td>\r\n				</tr>\r\n				<tr>\r\n				  <td class=\"row\" title=\"A valid and preferably unique email address.\"><div align=\"right\">Email Address<span style=\"color: rgb(255, 0, 0);\">*</span>: </div></td>\r\n				  <td align=\"left\" class=\"row\" title=\"A valid and preferably unique email address.\"><div align=\"left\">\r\n					<input name=\"email\" type=\"text\" id=\"email\" size=\"60\" value=\"{\$info.email}\" class=\"inputbox\" title=\"A valid and preferably unique email address.\" />\r\n				  </div></td>\r\n			</tr>\r\n            <tr>\r\n              <td align=\"right\" class=\"row\"><div align=\"right\">Your Timezone </div></td>\r\n              <td align=\"left\" class=\"row\"><div align=\"left\">\r\n                <select name=\"zone\" id=\"zone\" class=\"inputbox\">\r\n                    {section name=zones loop=\$numzones}\r\n                        <option value=\"{\$zone[zones].id}\" {if (\$zone[zones].offset == 0)}selected{/if}>{\$zone[zones].offset} Hours</option>\r\n                    {/section}\r\n                </select>\r\n              </div></td>\r\n            </tr>\r\n				{if \$config.allowtroop == 1}\r\n                <tr>\r\n				  <td class=\"row\" title=\"If you are a member (or your son/daugter is a member) of {\$config.troopname} then select yes\"><div align=\"right\">Are you a member of {\$config.troopname}<span style=\"color: rgb(255, 0, 0);\">*</span>?</div></td>\r\n				  <td align=\"left\" class=\"row\" title=\"If you are a member (or your son is a member) of {\$config.troopname} then select yes\">\r\n					<label><input type=\"radio\" name=\"member\" value=\"1\" {if \$info.member == 1}checked=\"checked\"{/if} />Yes</label>\r\n					<label><input type=\"radio\" name=\"member\" value=\"0\" {if \$info.member != 1}checked=\"checked\"{/if} />No</label>\r\n                </td>\r\n    			</tr>\r\n                {else}\r\n                <tr>\r\n                <td class=\"row\" colspan=\"2\">\r\n                <input type=\"hidden\" name=\"member\" value=\"0\"  />\r\n                </td>\r\n                </tr>\r\n                {/if}\r\n                <tr>\r\n		      <td width=\"50%\" class=\"row\" title=\"Your first name. Please put your real first name here as it makes the Scout Masters job easier\"><div align=\"right\" class=\"required\">First Name<span style=\"color: rgb(255, 0, 0);\">*</span>:</div></td>\r\n              <td align=\"left\" class=\"row\" title=\"Your first name. Please put your real first name here as it makes the Scout Masters job easier\"><input name=\"firstname\" type=\"text\" size=\"40\" value=\"{\$info.first}\" class=\"inputbox\" title=\"Your first name. Please put your real first name here as it makes the Scout Masters job easier\" /></td>\r\n	    </tr>\r\n	    <tr>\r\n		  <td class=\"row\"><div align=\"right\" class=\"required\" title=\"Your last name. Please put your real last name here as it makes the Scout Masters job easier\">Surname<span style=\"color: rgb(255, 0, 0);\">*</span>:</div></td>\r\n		  <td align=\"left\" class=\"row\" title=\"Your last name. Please put your real last name here as it makes the Scout Masters job easier\"><div align=\"left\">\r\n		    <input name=\"lastname\" type=\"text\" size=\"40\" value=\"{\$info.last}\" class=\"inputbox\" title=\"Your last name. Please put your real last name here as it makes the Scout Masters job easier\" />\r\n		  </div></td>\r\n	    </tr>\r\n		<tr>\r\n		  <td width=\"50%\" class=\"row\"><div align=\"right\" class=\"required\" title=\"Your date of birth. Format: YYYY-MM-DD. You can use the ... button to open a calendar\">Date Of Birth<span style=\"color: rgb(255, 0, 0);\">*</span>:</div></td>\r\n		  <td align=\"left\" class=\"row\" title=\"Your date of birth. Format: YYYY-MM-DD. You can use the ... button to open a calendar\"><input name=\"dob\" id=\"dob\" type=\"text\" size=\"20\" maxlength=\"20\" value=\"{\$info.dob|date_format:\"%Y-%m-%d\"}\" class=\"inputbox\" /><button id=\"opencal\" name=\"opencal\" class=\"button\">...</button></td>\r\n		</tr>\r\n		<tr>\r\n		  <td width=\"50%\" class=\"row\"><div align=\"right\" title=\"A valid telephone number. This is not required.\">Telephone Number:</div></td>\r\n		  <td align=\"left\" class=\"row\" title=\"A valid telephone number. This is not required.\"><input name=\"tel\" type=\"text\" size=\"40\" value=\"{\$info.tel}\" class=\"inputbox\" title=\"A valid telephone number. This is not required.\" /></td>\r\n		</tr>\r\n		<tr>\r\n		  <td width=\"50%\" class=\"row\" title=\"A valid telephone number. This is not required.\"><div align=\"right\">Cellphone Number:</div></td>\r\n		  <td align=\"left\" class=\"row\" title=\"A valid cellphone number. This is not required.\"><input name=\"cell\" type=\"text\" size=\"40\" value=\"{\$info.cell}\" class=\"inputbox\" title=\"A valid cellphone number. This is not required.\" /></td>\r\n		</tr>\r\n		<tr>\r\n		  <td width=\"50%\" class=\"row\" title=\"A valid address. This is not required.\"><div align=\"right\">Address:</div></td>\r\n		  <td align=\"left\" class=\"row\" title=\"A valid address. This is not required.\"><textarea name=\"address\" cols=\"50\" rows=\"5\" class=\"inputbox\" title=\"A valid address. This is not required.\">{\$info.address}</textarea></td>\r\n		</tr>\r\n        <tr>\r\n				  <th colspan=\"2\">                \r\n                  <div>\r\n					<input name=\"Clear\" type=\"reset\" id=\"Clear2\" value=\"Reset Form\" class=\"button\" />\r\n				  &nbsp;&nbsp;\r\n				  <input name=\"next\" type=\"submit\" id=\"next\" value=\"Next\" class=\"button\" />\r\n				  <input name=\"pagenum\" type=\"hidden\" id=\"pagenum\" value=\"1\" />\r\n	            </div>	          \r\n                </th>\r\n			</tr>\r\n	  </table>\r\n</form>\r\n<script type=\"text/javascript\">\r\n  {literal}\r\n  Calendar.setup(\r\n    {\r\n      inputField  : \"dob\",         // ID of the input field\r\n      ifFormat    : \"%Y-%m-%d\",    // the date format\r\n      button      : \"opencal\",       // ID of the button\r\n{/literal}\r\n      date         : \"{\$info.dob}\"\r\n      {literal}\r\n    }\r\n  );\r\n  {/literal}\r\n</script>', 9);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (9, 3, ' {\$errors}\r\n<form name=\"form\" method=\"post\" action=\"{\$editFormAction}&amp;pagenum={\$pagenum}\">\r\n		 <table width=\"100%\" border=\"0\" align=\"center\"  class=\"table\" cellpadding=\"4\" cellspacing=\"1\">	\r\n		<tr>\r\n		  <th colspan=\"2\"><div align=\"center\">Step 3 - Confirmation</div></th>\r\n	    </tr>\r\n		<tr>\r\n		  <td width=\"50%\" class=\"row\" title=\"Retype your password here. This is to make sure that you actually know what it is\"><div align=\"right\">Password</div></td>\r\n		  <td class=\"row\" title=\"Retype your password here. This is to make sure that you actually know what it is\"><div align=\"left\">\r\n		    <input name=\"repass\" type=\"password\" id=\"repass\" size=\"30\" maxlength=\"30\" class=\"inputbox\" title=\"Retype your password here. This is to make sure that you actually know what it is\" />\r\n		  </div></td>\r\n	    </tr>\r\n		<tr>\r\n		  <td class=\"row\" title=\"Retype your email address. This is to make sure that you really are a person, and your email address is valid\"><div align=\"right\">Email Address </div></td>\r\n		  <td class=\"row\" title=\"Retype your email address. This is to make sure that you really are a person, and your email address is valid\"><div align=\"left\">\r\n		    <input name=\"reemail\" type=\"text\" id=\"reemail\" size=\"60\" class=\"inputbox\" title=\"Retype your email address. This is to make sure that you really are a person, and your email address is valid\" />\r\n		  </div></td>\r\n	    </tr>\r\n		<tr>\r\n		  <td colspan=\"2\" class=\"row\"><div align=\"right\"></div>		    \r\n		    <div align=\"center\">\r\n  <input name=\"back\" type=\"submit\" id=\"back\" value=\"Back\" class=\"button\" />\r\n&nbsp;&nbsp;\r\n  <input name=\"Clear22\" type=\"reset\" id=\"Clear22\" value=\"Reset Form\" class=\"button\" />\r\n&nbsp;&nbsp;\r\n  <input name=\"next\" type=\"submit\" id=\"next\" value=\"Next\" class=\"button\" />\r\n  <input name=\"pagenum\" type=\"hidden\" id=\"pagenum3\" value=\"3\" />\r\n            </div></td>\r\n	    </tr>\r\n  </table>\r\n</form>', 11);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (9, 2, ' {\$errors}\r\n<form name=\"form\" method=\"post\" action=\"{\$editFormAction}&amp;pagenum={\$pagenum}\">\r\n		 <table width=\"100%\" border=\"0\" align=\"center\"  class=\"table\" cellpadding=\"4\" cellspacing=\"1\">\r\n		<tr>\r\n		  <th colspan=\"2\">Step 2 - Scouting Information </th>\r\n	    </tr>\r\n		<tr>\r\n		  <td colspan=\"2\" class=\"row\"><div align=\"center\">You indicated that your a member of {\$config.troopname}. Please fill in this form with your scouting details.</div></td>\r\n	    </tr>\r\n		<tr>\r\n		  <td width=\"50%\" class=\"row\" title=\"Select your group. If your group does not appear here then leave as is. The administrator will check this when he sets your account up correctly\"><div align=\"right\">Group</div></td>\r\n	      <td class=\"row\" title=\"Select your group. If your group does not appear here then leave as is. The administrator will check this when he sets your account up correctly\"><div align=\"left\">\r\n	        <select name=\"patrol\" id=\"patrol\" class=\"inputbox\" title=\"Select your group. If your group does not appear here then leave as is. The administrator will check this when he sets your account up correctly\">\r\n	          <option value=\"0\" selected>Select Group</option>\r\n			  {section name=loop loop=\$numgroups}\r\n			  <option value=\"{\$groups[loop].teamname}\" >{\$groups[loop].teamname}</option>	          \r\n			  {/section}\r\n	        </select>\r\n	      </div></td>\r\n		</tr>\r\n		<tr>\r\n		  <td class=\"row\" title=\"Select your position in your group, if you are unsure what to select just choose ''Scout''\"><div align=\"right\">Postion in troop </div></td>\r\n	      <td class=\"row\" title=\"Select your position in your group, if you are unsure what to select just choose ''Scout''\"><div align=\"left\">\r\n	        <select name=\"pos\" id=\"pos\" class=\"inputbox\" title=\"Select your position in your troop, if you are unsure what to select just choose ''Scout''\">\r\n	          <option value=\"0\" selected>Select Postition (If unsure select \"Scout\")</option>\r\n	          <option value=\"PL\">Group Leader (Patrol Leader)</option>\r\n	          <option value=\"2nd\">2nd</option>\r\n	          <option value=\"Scout\">Scout</option>\r\n	          <option value=\"TL\">TL</option>\r\n	          <option value=\"Scouter\">Scouter</option>\r\n			</select>\r\n	      </div></td>\r\n		</tr>\r\n		<tr>\r\n		  <td class=\"row\" title=\"Select the award scheme that you are fall under. Leave it as it is if you are unsure.\"><div align=\"right\">What award scheme do you use?</div></td>\r\n	      <td class=\"row\" title=\"Select the award scheme that you are fall under. Leave it as it is if you are unsure.\"><div align=\"left\">\r\n            <select name=\"scheme\" id=\"scheme\" class=\"inputbox\">\r\n                {section name=awards loop=\$numschemes}\r\n                    <option value=\"{\$schemes[awards].id}\" {if (\$personal.scheme == \$schemes[awards].id)}selected{/if}>{\$schemes[awards].name} Award Scheme</option>\r\n                {/section}\r\n            </select>\r\n	      </div></td>\r\n		</tr>\r\n		<tr>\r\n		  <td colspan=\"2\" class=\"row\">\r\n            <div align=\"center\">\r\n  <input name=\"back\" type=\"submit\" id=\"back\" value=\"Back\" class=\"button\" />\r\n&nbsp;&nbsp;\r\n  <input name=\"Clear222\" type=\"reset\" id=\"Clear222\" value=\"Reset Form\" class=\"button\" />\r\n&nbsp;&nbsp;\r\n  <input name=\"next\" type=\"submit\" id=\"next\" value=\"Next\" class=\"button\" />\r\n          </div></td>\r\n	    </tr>\r\n		</table>\r\n</form>', 12);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (9, 4, '		 <table width=\"100%\" border=\"0\" align=\"center\"  class=\"content\" cellpadding=\"2\" cellspacing=\"0\">\r\n		<tr>\r\n		  <th colspan=\"2\"><div align=\"center\">Step 4 - Registration Complete</div></th>\r\n	    </tr>\r\n		<tr>\r\n		  <td colspan=\"2\"><div align=\"center\">\r\n		    <p>Thank you for registering. You can now use your username and password to access the main site. Please logon at least once to complete the registration process<br />You username and password has also been emailed to you.</p>\r\n		    <p><em>Please keep your password a secret.</em></p>\r\n		    <p><em><a href=\"index.php\">Back to main </a></em></p>\r\n		  </div></td>\r\n	    </tr>\r\n	  </table>', 13);";
