$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (11, 1, '{if \$action != ''cat'' && \$action != ''down''}	\r\n{if \$numcats > 0}\r\n	<h1>Download Categories</h1>\r\n	<br /><div align=\"center\">\r\n<ul>\r\n		{section name=cats loop=\$numcats}\r\n		<li><a href=\"index.php?page=downloads&amp;catid={\$cats[cats].id}&amp;action=cat\">{\$cats[cats].name}</a></li>\r\n		 {/section}\r\n		 </ul></div>\r\n         {else}\r\n         <div>There are no categories with downloads at the moment</div>\r\n        {/if}\r\n{else}	\r\n{if \$numdown > 0}\r\n<table width=\"100%\" border=\"0\" cellpadding=\"4\" cellspacing=\"1\" class=\"table\">\r\n	  <tr> \r\n	  <th colspan=\"6\" class=\"bighead\">Downloads for {\$catinfo.name}</th>\r\n	</tr>\r\n	  <tr>\r\n		<th width=\"10%\" class=\"smallhead\">Action</th>\r\n		<th width=\"26%\" class=\"smallhead\">Name</th>\r\n		<th class=\"smallhead\">Descripton</th>\r\n		<th width=\"5%\" class=\"smallhead\">Downloads</th>\r\n		<th width=\"10%\" class=\"smallhead\">Size</th>\r\n	  </tr> \r\n	 {section name=downloadloop loop=\$numdown}\r\n		 <tr>\r\n			<td class=\"row\"><a href=\"index.php?page=downloads&amp;id={\$downloads[downloadloop].id}&amp;action=down&amp;catid={\$catinfo.id}\">Download</a></td>\r\n			<td class=\"row\">{\$downloads[downloadloop].name}</td>\r\n			<td class=\"row\">{\$downloads[downloadloop].descs}</td>\r\n			<td class=\"row\">{\$downloads[downloadloop].numdownloads}</td>\r\n			<td class=\"row\">{\$downloads[downloadloop].size} Kb</td>\r\n	  </tr>\r\n	  {/section}\r\n	</table>\r\n{/if}\r\n{/if}', 17);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (13, 1, '<form action=\"{\$editFormAction}\" method=\"post\" name=\"sear\" id=\"sear\">\r\n    <div align=\"center\">Search for <input type=\"text\" name=\"search\" id=\"search\" {if \$search != \"\"}value=''{\$search}''{else}value=\"Enter search item...\"{/if} size=50 class=\"inputbox\" /><br /> inside&nbsp;\r\n    <select name=\"what\" id=\"what\" class=\"inputbox\">\r\n        <option value=\"1\" {if \$option==1 || \$option==0}selected=\"selected\"{/if}>Articles</option>\r\n        <option value=\"3\" {if \$option==3}selected=\"selected\"{/if}>News Items</option>\r\n        <option value=\"4\" {if \$option==4}selected=\"selected\"{/if}>Forum Posts</option>\r\n        {if \$uname != \"Guest\"}<option value=\"5\" {if \$option==5}selected=\"selected\"{/if}>Personal Messages</option>{/if}\r\n        <option value=\"6\" {if \$option==6}selected=\"selected\"{/if}>Website Content</option>\r\n        <option value=\"7\" {if \$option==7}selected=\"selected\"{/if}>Patrol Pages</option>\r\n        <option value=\"8\" {if \$option==8}selected=\"selected\"{/if}>Sub Site Content</option>\r\n    </select>\r\n    <br />     \r\n     <select name=\"type\" id=\"type\" class=\"inputbox\">\r\n        <option value=\"1\" {if \$type==1 || \$type==0}selected=\"selected\"{/if}>All</option>\r\n        <option value=\"2\" {if \$type==2}selected=\"selected\"{/if}>Any</option>\r\n    </select> of the search terms needs to appear.\r\n    <br /><input type=\"submit\" name=\"Submit\" id=\"Submit\"  value=\"Search\" class=\"button\" /><br />\r\n    <span class=\"comment\">To search for a phrase place it into double quotation marks (Eg. \"Scouts is fun\")</span>\r\n</div>\r\n</form>\r\n{if \$searched == 1}\r\n{if \$tfield != \"\"}\r\n<h3 align=\"center\">Searching in all {\$searching}</h3>\r\n<div style=\"font-weight:bold\">You search terms ({\$term_list}) matched the following items:</div>\r\n{section name=results loop=\$results}\r\n <div><a href=\"index.php?page={eval var=\$whatpage}{\$results[results].\$ifield}&amp;highlight={\$terms}\">{\$results[results].\$tfield|strip_tags:false}</a><br />\r\n<span class=\"comment\">{if \$showdate == 1}<strong>Date Posted:</strong> {\$results[results].\$datefield+\$timeoffset|date_format:\"%B %e, %Y %H:%M\"}{/if}<br />{\$results[results].\$dfield|strip_tags:false|truncate:200}</span></div>\r\n <br />\r\n{/section}\r\n{else}\r\n<h2>No matches for {\$term_list} where found in {\$searching}</h2>\r\n{/if}\r\n{/if}', 19);";
