$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (17, 1, '{literal}\r\n<script type=\"text/javascript\">\r\n<!--\r\nfunction confirmDelete(type, id) {\r\nif (confirm(\"This will delete the item. Continue?\"))\r\ndocument.location = \"index.php?page=mythings&action=delete&cat=\" + type + \"&id=\" + id;\r\n}\r\n\r\nfunction changeowner(type, id) {\r\n	WindowObjectReference = window.open(\"index.php?page=mythings&ex=nomenu&cat=\"+type+\"&action=owner&id=\" + id, \"ChangeOwnership\",\r\n\"menubar=no,location=no,resizable=yes,scrollbars=yes,status=no,height=190,width=500\");\r\n}\r\n\r\n\r\nfunction adddownload() {\r\n	WindowObjectReference = window.open(\"index.php?page=mythings&ex=nomenu&action=adddown\",  \"Adddown\",\r\n\"menubar=no,location=no,resizable=yes,scrollbars=yes,status=no,height=360,width=550\");\r\n}\r\n\r\nfunction addnews() {\r\n	WindowObjectReference = window.open(\"index.php?page=mythings&ex=nomenu&action=addnews\",  \"addnews\",\r\n\"menubar=no,location=no,resizable=yes,scrollbars=yes,status=no,height=480,width=640\");\r\n}\r\n//-->\r\n</script>\r\n{/literal}\r\n<a name=\"top\"></a>\r\n<h1>My things</h1>\r\n<h3>Hi {\$uname}, click on one of these links if you''d like to add something</h3>\r\n<div style=\"text-align:center\">\r\n{if \$auth.album == 1}<a href=\"index.php?page=addphotoalbum\">Add Photo Album</a> | {/if}\r\n{if \$auth.article == 1}<a href=\"index.php?page=addarticle\">Add Article</a> | {/if}\r\n{if \$auth.event == 1}<a href=\"index.php?page=addevent\">Add Event</a> | {/if}\r\n{if \$auth.down == 1}<a href=\"javascript:adddownload()\">Add Download</a> | {/if}\r\n{if \$auth.news == 1}<a href=\"javascript:addnews()\">Add News</a>{/if}\r\n<br />\r\n{if \$auth.album == 1 || \$numalbums > 0}<a href=\"#album\">View Photo Album List</a> | {/if}\r\n{if \$auth.article == 1 || \$numart > 0}<a href=\"#article\">View Article List</a> | {/if}\r\n{if \$auth.event == 1 || \$numevents > 0}<a href=\"#event\">View Event List</a> | {/if}\r\n{if \$auth.down == 1 || \$numdown > 0}<a href=\"#down\">View Download List</a> | {/if}\r\n{if \$auth.news == 1 || \$numnews > 0}<a href=\"#news\">View News List</a>{/if}\r\n</div>\r\n<br />\r\n{if \$auth.album == 1 || \$numalbums > 0}\r\n{if \$numalbums > 0}\r\n<table width = \"100%\" border=\"0\" cellspacing=\"1\" cellpadding=\"4\" class=\"table\">\r\n    <tr>\r\n        <th colspan=\"3\" class=\"bighead\"><a name=\"album\"></a>Photo Albums</th>\r\n    </tr>\r\n    <tr>\r\n        <th class=\"smallhead\" width=\"10%\">Action</th>\r\n        <th class=\"smallhead\" width=\"10%\">Published?</th>\r\n        <th class=\"smallhead\" width=\"65%\">Title</th>\r\n    </tr>\r\n    {section name=album loop=\$numalbums}\r\n    <tr>\r\n        <td align=\"center\" class=\"row\"><a href=\"index.php?page=mythings&amp;cat=album&amp;action=edit&amp;id={\$albums[album].ID}\"><img border=\"0\" src=\"{\$templateinfo.imagedir}edit.gif\" title=\"Edit\" alt=\"Edit\"/></a> - <a href=\"javascript:confirmDelete(''album'',''{\$albums[album].ID}'')\"><img border=\"0\" src=\"{\$templateinfo.imagedir}delete.gif\" title=\"Delete\" alt=\"Delete\"/></a> - <a href=\"javascript:changeowner(''album'',''{\$albums[album].ID}'')\"><img border=\"0\" src=\"{\$templateinfo.imagedir}owner.gif\" title=\"Transfer Ownership\" alt=\"Transfer Ownership\"/></a></td>\r\n        <td align=\"center\" class=\"row\">{if \$albums[album].allowed == 1}<img src=\"{\$templateinfo.imagedir}published.png\" alt=\"Published\" title=\"Published\" border=\"0\" />{else}<img src=\"{\$templateinfo.imagedir}notpublished.png\" alt=\"Not Published\" title=\"Not Published\" border=\"0\" />{/if}</td>\r\n        <td class=\"row\">{\$albums[album].album_name}</td>\r\n    </tr>\r\n    {/section}\r\n</table>\r\n<div style=\"text-align:center\"><a href=\"#top\">Back to top</a></div>\r\n{else}\r\n    <div style=\"text-align:center\">You have posted no Photo Albums yet</div>\r\n{/if}\r\n<br /><br />\r\n{/if}\r\n{if \$auth.article == 1 || \$numart > 0}\r\n{if \$numart > 0}\r\n<table width = \"100%\" border=\"0\" cellspacing=\"1\" cellpadding=\"4\" class=\"table\">\r\n    <tr>\r\n        <th colspan=\"3\" class=\"bighead\"><a name=\"article\"></a>Articles</th>\r\n    </tr>\r\n    <tr>\r\n        <th class=\"smallhead\" width=\"10%\">Action</th>\r\n        <th class=\"smallhead\" width=\"10%\">Published?</th>\r\n        <th class=\"smallhead\" width=\"65%\">Title</th>\r\n    </tr>\r\n    {section name=album loop=\$numart}\r\n    <tr>\r\n        <td align=\"center\" class=\"row\"><a href=\"index.php?page=mythings&amp;cat=articles&amp;action=edit&amp;id={\$articles[album].ID}\"><img border=\"0\" src=\"{\$templateinfo.imagedir}edit.gif\" title=\"Edit\" alt=\"Edit\"/></a> - <a href=\"javascript:confirmDelete(''articles'',''{\$articles[album].ID}'')\"><img border=\"0\" src=\"{\$templateinfo.imagedir}delete.gif\" title=\"Delete\" alt=\"Delete\"/></a> - <a href=\"javascript:changeowner(''articles'',''{\$articles[album].ID}'')\"><img border=\"0\" src=\"{\$templateinfo.imagedir}owner.gif\" title=\"Transfer Ownership\" alt=\"Transfer Ownership\"/></a></td>\r\n        <td align=\"center\" class=\"row\">{if \$articles[album].allowed == 1}<img src=\"{\$templateinfo.imagedir}published.png\" alt=\"Published\" title=\"Published\" border=\"0\" />{else}<img src=\"{\$templateinfo.imagedir}notpublished.png\" alt=\"Not Published\" title=\"Not Published\" border=\"0\" />{/if}</td>\r\n        <td class=\"row\">{\$articles[album].title}</td>\r\n    </tr>\r\n    {/section}\r\n</table>\r\n<div style=\"text-align:center\"><a href=\"#top\">Back to top</a></div>\r\n{else}\r\n    <div style=\"text-align:center\">You have posted no Articles yet</div>\r\n{/if}\r\n<br /><br />\r\n{/if}\r\n{if \$auth.event == 1 || \$numevents > 0}\r\n{if \$numevents > 0}\r\n<table width = \"100%\" border=\"0\" cellspacing=\"1\" cellpadding=\"4\" class=\"table\">\r\n    <tr>\r\n        <th colspan=\"4\" class=\"bighead\"><a name=\"event\"></a>Events</th>\r\n    </tr>\r\n    <tr>\r\n        <th class=\"smallhead\" width=\"10%\">Action</th>\r\n        <th class=\"smallhead\" width=\"10%\">Published?</th>\r\n        <th class=\"smallhead\">Title</th>\r\n        <th class=\"smallhead\">Event Date</th>\r\n    </tr>\r\n    {section name=album loop=\$numevents}\r\n    <tr>\r\n        <td align=\"center\" class=\"row\"><a href=\"index.php?page=mythings&amp;cat=events&amp;action=edit&amp;id={\$events[album].id}\"><img border=\"0\" src=\"{\$templateinfo.imagedir}edit.gif\" title=\"Edit\" alt=\"Edit\"/></a> - <a href=\"javascript:confirmDelete(''events'',''{\$events[album].id}'')\"><img border=\"0\" src=\"{\$templateinfo.imagedir}delete.gif\" title=\"Delete\" alt=\"Delete\"/></a> - <a href=\"javascript:changeowner(''events'',''{\$events[album].id}'')\"><img border=\"0\" src=\"{\$templateinfo.imagedir}owner.gif\" title=\"Transfer Ownership\" alt=\"Transfer Ownership\"/></a></td>\r\n        <td align=\"center\" class=\"row\">{if \$events[album].allowed == 1}<img src=\"{\$templateinfo.imagedir}published.png\" alt=\"Published\" title=\"Published\" border=\"0\" />{else}<img src=\"{\$templateinfo.imagedir}notpublished.png\" alt=\"Not Published\" title=\"Not Published\" border=\"0\" />{/if}</td>\r\n        <td class=\"row\">{\$events[album].summary}</td>\r\n        <td class=\"row\">{\$events[album].startdate} to {\$events[album].enddate}</td>\r\n    </tr>\r\n    {/section}\r\n</table>\r\n<div style=\"text-align:center\"><a href=\"#top\">Back to top</a></div>\r\n{else}\r\n    <div style=\"text-align:center\">You have posted no events yet</div>\r\n{/if}\r\n<br /><br />\r\n{/if}\r\n{if \$auth.down == 1 || \$numdown > 0}\r\n{if \$numdown > 0}\r\n<table width = \"100%\" border=\"0\" cellspacing=\"1\" cellpadding=\"4\" class=\"table\">\r\n    <tr>\r\n        <th colspan=\"4\" class=\"bighead\"><a name=\"down\"></a>Downloads</th>\r\n    </tr>\r\n    <tr>\r\n        <th class=\"smallhead\" width=\"10%\">Action</th>\r\n        <th class=\"smallhead\" width=\"10%\">Published?</th>\r\n        <th class=\"smallhead\">Filename</th>\r\n        <th class=\"smallhead\">Title</th>\r\n    </tr>\r\n    {section name=album loop=\$numdown}\r\n    <tr>\r\n        <td align=\"center\" class=\"row\"><a href=\"index.php?page=mythings&amp;cat=downloads&amp;action=edit&amp;id={\$downloads[album].id}\"><img border=\"0\" src=\"{\$templateinfo.imagedir}edit.gif\" title=\"Edit\" alt=\"Edit\"/></a> - <a href=\"javascript:confirmDelete(''downloads'',''{\$downloads[album].id}'')\"><img border=\"0\" src=\"{\$templateinfo.imagedir}delete.gif\" title=\"Delete\" alt=\"Delete\"/></a> - <a href=\"javascript:changeowner(''downloads'',''{\$downloads[album].id}'')\"><img border=\"0\" src=\"{\$templateinfo.imagedir}owner.gif\" title=\"Transfer Ownership\" alt=\"Transfer Ownership\"/></a></td>\r\n        <td align=\"center\" class=\"row\">{if \$downloads[album].allowed == 1}<img src=\"{\$templateinfo.imagedir}published.png\" alt=\"Published\" title=\"Published\" border=\"0\" />{else}<img src=\"{\$templateinfo.imagedir}notpublished.png\" alt=\"Not Published\" title=\"Not Published\" border=\"0\" />{/if}</td>\r\n        <td class=\"row\">{\$downloads[album].file}</td>\r\n        <td class=\"row\">{\$downloads[album].name}</td>\r\n    </tr>\r\n    {/section}\r\n</table>\r\n<div style=\"text-align:center\"><a href=\"#top\">Back to top</a></div>\r\n{else}\r\n    <div style=\"text-align:center\">You have posted no downloads yet</div>\r\n{/if}\r\n<br /><br />\r\n{/if}\r\n{if \$auth.news == 1 || \$numnews > 0}\r\n{if \$numnews > 0}\r\n<table width = \"100%\" border=\"0\" cellspacing=\"1\" cellpadding=\"4\" class=\"table\">\r\n    <tr>\r\n        <th colspan=\"4\" class=\"bighead\"><a name=\"news\"></a>News Items</th>\r\n    </tr>\r\n    <tr>\r\n        <th class=\"smallhead\" width=\"10%\">Action</th>\r\n        <th class=\"smallhead\" width=\"10%\">Published?</th>\r\n        <th class=\"smallhead\" width=\"60%\">Title</th>\r\n        <th class=\"smallhead\" width=\"20%\">Date Posted</th>\r\n    </tr>\r\n    {section name=album loop=\$numnews}\r\n    <tr>\r\n        <td align=\"center\" class=\"row\"><a href=\"index.php?page=mythings&amp;cat=newsitems&amp;action=edit&amp;id={\$newsitems[album].id}\"><img border=\"0\" src=\"{\$templateinfo.imagedir}edit.gif\" title=\"Edit\" alt=\"Edit\"/></a> - <a href=\"javascript:confirmDelete(''newsitems'',''{\$newsitems[album].id}'')\"><img border=\"0\" src=\"{\$templateinfo.imagedir}delete.gif\" title=\"Delete\" alt=\"Delete\"/></a> - <a href=\"javascript:changeowner(''newsitems'',''{\$newsitems[album].id}'')\"><img border=\"0\" src=\"{\$templateinfo.imagedir}owner.gif\" title=\"Transfer Ownership\" alt=\"Transfer Ownership\"/></a></td>\r\n        <td align=\"center\" class=\"row\">{if \$newsitems[album].allowed == 1}<img src=\"{\$templateinfo.imagedir}published.png\" alt=\"Published\" title=\"Published\" border=\"0\" />{else}<img src=\"{\$templateinfo.imagedir}notpublished.png\" alt=\"Not Published\" title=\"Not Published\" border=\"0\" />{/if}</td>\r\n        <td class=\"row\">{\$newsitems[album].title}</td>\r\n        <td class=\"row\">{\$newsitems[album].event+\$timeoffset|date_format:\"%B %e, %Y %H:%M\"}</td>\r\n    </tr>\r\n    {/section}\r\n</table>\r\n<div style=\"text-align:center\"><a href=\"#top\">Back to top</a></div>\r\n{else}\r\n    <div style=\"text-align:center\">You have posted no news items yet</div>\r\n{/if}\r\n{/if}', 23);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (17, 2, '{literal}\r\n<script language=\"javascript\" type=\"text/javascript\"> \r\n function upload(aid) {\r\n	var place = \"index.php?page=mythings&ex=nomenu&cat=album&action=add&id=\" + aid;\r\n	WindowObjectReference = window.open(place, \"AddPhoto\",\r\n\"menubar=no,location=no,resizable=yes,scrollbars=yes,status=no,height=190,width=500\");\r\n }\r\n\r\nfunction deletephoto(photoid, albumid) {\r\nif (confirm(\"This will remove the photo from the album. Continue?\"))\r\ndocument.location = \"index.php?page=mythings&cat=album&action=delphoto&pid=\" + photoid + \"&id=\"+ albumid;\r\n}\r\n\r\nfunction editphoto(photoid, albumid)\r\n{\r\n	var url = \"index.php?page=mythings&ex=nomenu&cat=album&action=modify&pid=\" + photoid + \"&id=\" + albumid;\r\n	window.open(url, ''EditPhoto'', ''HEIGHT=400,resizable=yes,WIDTH=500'');\r\n}\r\n//-->\r\n</script>\r\n{/literal}\r\n\r\n<h1>{\$album.album_name}</h1>\r\n\r\nAdd, remove or change a photo in this album\r\n\r\n<div align=\"center\"><a href=\"javascript:upload(''{\$album.ID}'')\">Add Photo</a></div>\r\n{if \$numphotos > 0}\r\n<table width=\"100%\" border=\"0\" cellspacing=\"1\" cellpadding=\"4\" class=\"table\">\r\n    <tr>\r\n        <th width=\"10%\">Action</th>\r\n        <th width=\"10%\">Published?</th>\r\n        <th width=\"40%\">Photo Caption</th>\r\n        <th width=\"40%\">Photo Preview</th>\r\n    </tr>\r\n    {section name=photo loop=\$numphotos}\r\n    <tr>\r\n        <td align=\"center\" class=\"row\"><a href=\"javascript:editphoto(''{\$photos[photo].ID}'',''{\$album.ID}'')\" style=\"text-align:center\"><img border=\"0\" src=\"{\$templateinfo.imagedir}edit.gif\" title=\"Edit\" alt=\"Edit\"/></a> - <a href=\"javascript:deletephoto({\$photos[photo].ID},{\$album.ID})\" style=\"text-align:center\"><img border=\"0\" src=\"{\$templateinfo.imagedir}delete.gif\" title=\"Delete\" alt=\"Delete\"/></a></td>\r\n        <td align=\"center\" class=\"row\">{if \$photos[photo].allowed == 1}<img src=\"{\$templateinfo.imagedir}published.png\" alt=\"Published\" title=\"Published\" border=\"0\" />{else}<img src=\"{\$templateinfo.imagedir}unpublished.png\" alt=\"Not Published\" title=\"Not Published\" border=\"0\" />{/if}</td>\r\n        <td width=\"40%\" class=\"row\">{\$photos[photo].caption}</td>\r\n        <td width=\"40%\" class=\"row\"><img src=\"thumbnail.php?pic={\$photos[photo].ID}\" /></td>\r\n    </tr>\r\n    {/section}\r\n</table>\r\n{else}\r\n<div align=\"center\">No photos in album</div>\r\n{/if}\r\n<br /><a href=\"index.php?page=mythings\">Back</a>', 24);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (17, 3, '<form action=\"{\$editFormAction}\" method=\"post\" enctype=\"multipart/form-data\" name=\"form1\">\r\n  <table width=\"100%\"  border=\"0\" cellspacing=\"1\" cellpadding=\"2\">\r\n    <tr>\r\n      <th colspan=\"2\">Upload Photo</th>\r\n    </tr>\r\n	<tr>\r\n	  <td width=\"50%\"><div align=\"right\">Filename:</div></td>\r\n      <td><input name=\"filename\" type=\"file\" size=\"50\" maxlength=\"255\" class=\"inputbox\" /></td>\r\n    </tr>\r\n	<tr>\r\n	  <td width=\"50%\"><div align=\"right\">Caption: </div></td>\r\n      <td><input name=\"caption\" type=\"text\" size=\"50\" maxlength=\"100\" class=\"inputbox\" /></td>\r\n	</tr>\r\n	 <tr>\r\n      <td colspan=\"2\"><div align=\"center\">\r\n         <input type=\"submit\" name=\"Submit\" value=\"Upload Photo\" class=\"button\" />\r\n         <input type=\"reset\" name=\"Submit2\" value=\"Cancel\" onClick=\"window.close();\" class=\"button\" />\r\n      </div></td>\r\n    </tr>\r\n  </table>\r\n</form>', 25);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (17, 4, '{if \$noshow == false}\r\n<p>Use this box to change the the photo or the caption. If you just want to change the caption leave the filename field blank If you just want to change the file leave the caption field blank.</p>\r\n<form action=\"{\$editFormAction}\" method=\"post\" enctype=\"multipart/form-data\" name=\"form\" id=\"form\">\r\n  <p><div class=\"current\">Current Picture: <img src=\"thumbnail.php?pic={\$photo.ID}\" /></div> \r\n    New Picture: \r\n    <input name=\"filename\" type=\"file\" size=\"40\" maxlength=\"255\" class=\"inputbox\">\r\n  </p>\r\n  <p>\r\n  <p>  \r\n  <div class=\"current\">Current Caption: {\$photo.caption}</div>\r\n    New Caption: \r\n    <input name=\"caption\" type=\"text\" id=\"caption\" size=\"60\" maxlength=\"100\" class=\"inputbox\">\r\n</p>\r\n  <p align=\"center\">\r\n    <input type=\"submit\" name=\"Submit\" value=\"Submit\" class=\"button\">\r\n    <input type=\"reset\" name=\"Submit2\" value=\"Cancel\" onClick=\"window.close()\" class=\"button\">\r\n  </p>\r\n</form>\r\n{else}\r\n{\$message2}\r\n{/if}', 26);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (17, 5, '<h1>Change ownership</h1>\r\n<form name=\"form\" method=\"post\" action=\"{\$editFormAction}\">\r\nChange ownership to:\r\n<select name=\"owner\" class=\"inputbox\">\r\n    <option value=\"none\" selected>Please choose new owner</option>\r\n    {section name=\"loop\" loop=\$numpeople}\r\n        <option value=\"{\$people[loop].uname}\">{\$people[loop].uname}</option>\r\n    {/section}\r\n</select><br />\r\n<input type=\"submit\" name=\"Submit\" value=\"Submit\" class=\"button\"><input type=\"reset\" name=\"reset\" value=\"Cancel\" onClick=\"window.close()\" class=\"button\"><br />\r\n</form>', 27);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (17, 6, '<h1>Edit Article</h1>\r\n<form action=\"{\$editFormAction}\" method=\"post\" name=\"noteadd\" id=\"noteadd\" enctype=\"multipart/form-data\">\r\n					<noscript><p><b>Javascript must be enabled to use this form.</b></p></noscript>\r\n				   <table width=\"100%\"  border=\"0\" cellspacing=\"2\" cellpadding=\"2\" class=\"content\">\r\n                    <tr>\r\n                      <td><div align=\"right\"><span class=\"subtitle\">Title: </span></div></td>\r\n                      <td>\r\n                        <div align=\"left\"><input name=\"title\" type=\"text\" id=\"title\" size=\"50\" value=\"{\$article.title}\" class=\"inputbox\" /></div>\r\n                      </td>\r\n                    </tr>\r\n                    <tr>\r\n                      <td><div align=\"right\"><span class=\"subtitle\">Date of event (Leave blank if unsure): </span></div></td>\r\n                      <td>\r\n                        <div align=\"left\"><input name=\"dh\" type=\"text\" id=\"dh\" size=\"16\" value=\"{\$article.date_happen}\" class=\"inputbox\" /><button id=\"opencal\" name=\"opencal\">...</button></div>\r\n                      </td>\r\n                    </tr>\r\n{if \$article.pic != \"\" || \$article.pic != NULL}\r\n<tr><td colspan=\"2\"><div align=\"center\">Current Picture: <img src=\"thumbnail.php?pic={\$article.ID}&where=article\" /></div></td></tr>{/if}\r\n                    <tr>\r\n                      <td><div align=\"right\"><span class=\"subtitle\">Picture{if \$article.pic != \"\" || \$article.pic != NULL}\r\n (Leave blank if you don''t want to change it){/if}: </span></div></td>\r\n                      <td>\r\n                      <div align=\"left\"><input name=\"filename\" type=\"file\" size=\"50\" class=\"inputbox\" /></div>\r\n</td>                    </tr>\r\n					<tr>\r\n                      <td><div align=\"right\"><span class=\"subtitle\">Author:</span></div></td>\r\n                      <td>\r\n                       <div align=\"left\"> <input name=\"auth\" type=\"text\" id=\"auth\" size=\"50\" value=\"{\$article.author}\" class=\"inputbox\" /></div>\r\n                      </td>\r\n                    </tr>	\r\n					<tr>\r\n					 <td><div align=\"right\"><span class=\"subtitle\">Album to use (You may need to add the album):</span></div></td>\r\n					 <td>\r\n						<div align=\"left\"><select name=\"cat\" class=\"inputbox\">\r\n						  {if \$numalbum > 0}\r\n                            <option value=\"no\" selected>None</option>\r\n                           {else}\r\n                            <option value=\"no\" selected>No Albums for your patrol</option>\r\n                            {/if}\r\n                          {if \$numalbum > 0}\r\n						  {section name=albums loop=\$numalbum}\r\n						  	<option value=\"{\$albums[albums].ID}\" {if \$albums[albums].ID == \$article.album_id}selected{/if}>{\$albums[albums].album_name}</option>\r\n						  {/section}\r\n                         {/if}\r\n						</select></div>\r\n					</td>\r\n				  </tr>\r\n                  </table>\r\n				  <br />\r\n				   <h2 align=\"center\" class=\"subtitle\">Article: </h2>   \r\n                    <div align=\"center\">   \r\n                    <textarea id=\"story\" name=\"story\" style=\"width: 100%; height: 50em;\">{\$detail}</textarea>   \r\n</div>                    \r\n				  <div align=\"center\">\r\n					<input type=\"submit\" name=\"Submit\" value=\"Update\" class=\"button\" />&nbsp;\r\n                	<input type=\"button\" name=\"Submit2\" value=\"Cancel\" onClick=\"window.location=''index.php?page=mythings''\" class=\"button\" />\r\n				  </div>\r\n</form>\r\n<script type=\"text/javascript\">\r\n  {literal}\r\n  Calendar.setup(\r\n    {\r\n      inputField  : \"dh\",         // ID of the input field\r\n      ifFormat    : \"%Y-%m-%d\",    // the date format\r\n      button      : \"opencal\",       // ID of the button\r\n      {/literal}\r\n      date         : \"{\$article.date_happen}\"\r\n      {literal}\r\n    }\r\n  );\r\n  {/literal}\r\n</script>', 28);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (18, 1, '<h1>Forgotten Username or Password</h1>\r\n<div>So, you''ve forgotten your username or password? Silly you!!</div>\r\n<div>You can recieve your username and a new password by filling in the email address you registered with.</div>\r\n<form name=\"form1\" action=\"{\$editFormAction}\" method=\"post\">\r\nEmail: <input type=\"text\" name=\"email\" id=\"email\" size=\"50\" maxlength=\"100\" class=\"inputbox\" /><br />\r\n<input type=\"submit\" name=\"submit\" id=\"submit\" value=\"Submit\" class=\"button\" />\r\n</form>', 30);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (19, 1, '<h1>My Group</h1>\r\n<div>From here you can see everybody in your group who is registered on the website. You can then either send them a email, or a personal message</div>\r\n\r\n<table width=\"100%\" border=\"0\" cellpadding=\"4\" cellspacing=\"1\" class=\"table\">\r\n{if \$nummem > 1}\r\n<tr>\r\n    <th>Action</th>\r\n    <th>Username</th>\r\n    <th>Real Name</th>\r\n</tr>\r\n{section name=patrol loop=\$nummem}\r\n{if \$patrollist[patrol].uname != \$username}\r\n<tr>\r\n    <td class=\"row\" align=\"center\"><a href=\"index.php?page=pmmain&amp;action=typepm&amp;user={\$patrollist[patrol].uname}\"><img src=\"{\$templateinfo.imagedir}email.gif\" border=\"0\" alt=\"PM {\$patrollist[patrol].firstname}\" title=\"PM {\$patrollist[patrol].firstname}\" /> </a></td>\r\n    <td class=\"row\">{\$patrollist[patrol].uname}</td>\r\n    <td class=\"row\">{\$patrollist[patrol].firstname}&nbsp;{\$patrollist[patrol].lastname}</td>\r\n</tr>\r\n{/if}\r\n{/section}\r\n{else}\r\n<tr><td colspan=\"3\" class=\"row\">You are the only member of your group</td></tr>\r\n{/if}\r\n</table>\r\n{if \$nummem > 1}\r\n<input type=\"button\" name=\"pmall\" value=\"PM all users\" onclick=\"window.location=''index.php?page=mypatrol&amp;action=pmall''\" class=\"button\" />{/if}\r\n', 31);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (20, 1, '<h1>News Archive</h1>\r\n{if \$numnews > 0}\r\n<table width=\"100%\" border=\"0\" cellspacing=\"1\" cellpadding=\"4\" class=\"table\">\r\n		{section name=news loop=\$numnews}	\r\n			<tr>\r\n			<td class=\"row\">\r\n                <div align=\"center\"><b>{\$arcnews[news].title}</b></div>\r\n                <div align=\"center\" class=\"smalltext\"><b>{\$arcnews[news].event+\$timeoffset|date_format:\"%B %e, %Y %H:%M\"}</b></div>\r\n				<div align=\"center\">{\$arcnews[news].news}</div>\r\n			</td>\r\n			</tr>\r\n		{/section}\r\n </table>\r\n{else}\r\n<div>There are no news items</div>\r\n{/if}', 33);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (17, 8, '  <form action=\"{\$editFormAction}\" method=\"post\" enctype=\"multipart/form-data\"  name=\"form1\">\r\n    <div align=\"center\">Name of download:<br />\r\n      <input name=\"name\" type=\"text\" id=\"name\" size=\"60\" maxlength=\"50\" value=\"{\$down.name}\" class=\"inputbox\" /> \r\n      <br />\r\n      Description:      <br />\r\n      <textarea name=\"desc\" cols=\"50\" rows=\"5\" id=\"desc\" class=\"inputbox\">{\$down.descs}</textarea>      \r\n      <br />\r\n    Filename{if \$action==\"edit\"}(Leave blank if you don''t want to change the file){/if}:<br />\r\n    <input name=\"file\" type=\"file\" size=\"60\" class=\"inputbox\" />\r\n    <br />\r\n    Category that download must be under:<br />\r\n    <select name=\"cat\" class=\"inputbox\">\r\n        {section name=cats loop=\$numcats}\r\n            <option value=\"{\$cat[cats].id}\"{if \$cat[cats].id == \$down.cat} selected{/if}>{\$cat[cats].name}</option>\r\n        {/section}\r\n    </select> <br /><br />\r\n    <input type=\"submit\" name=\"Submit\" value=\"Submit\" class=\"button\" />\r\n    {if \$down.name == \"\"}<input type=\"submit\" name=\"Submit2\" value=\"Cancel\" class=\"button\" onClick=\"window.close();\" />\r\n    {else}\r\n    <input type=\"reset\" name=\"Submit2\" value=\"Cancel\" class=\"button\" onClick=\"history.go(-1);\" />\r\n    {/if}\r\n    </div>\r\n</form>', 34);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (17, 9, '<form name=\"News\" method=\"post\" action=\"{\$editFormAction}\">\r\n  <table width=\"100%\" border=\"0\" cellspacing=\"1\" cellpadding=\"4\" align=\"center\" class=\"table\">\r\n    <tr> \r\n      <th colspan=\"2\" class=\"bighead\"> \r\n        <div align=\"center\">News Item</div>\r\n      </th>\r\n    </tr> \r\n    <tr>\r\n        <td class=\"row\"><div align=\"right\">\r\n            Title:</div>\r\n        </td>\r\n        <td class=\"row\"><div align=\"left\">\r\n         <input type=\"text\" name=\"title\" id=\"title\" size=\"60\" value=\"{\$shownews.title}\" class=\"inputbox\"/></div>\r\n        </td>\r\n    </tr>\r\n    <tr valign=\"middle\"> \r\n      <td width=\"11%\" class=\"row\" valign=\"top\"><div align=\"right\">News Item:</div></td>\r\n      <td width=\"89%\" class=\"row\"> <div align=\"center\">\r\n      <textarea id=\"story\" name=\"story\" style=\"width: 100%; height: 25em;\">{\$shownews.news}</textarea></div>\r\n	  </td>\r\n    </tr>\r\n    <tr valign=\"middle\"> \r\n      <td colspan=\"2\" class=\"row\"> \r\n        <div align=\"center\"> \r\n        <input type=\"submit\" name=\"submit\" value=\"Submit\" class=\"button\" /> \r\n        {if \$action == \"edit\"}<input type=\"button\" name=\"Submit2\" value=\"Cancel\" onClick=\"window.location=''index.php?page=mythings''\" class=\"button\" />\r\n        {else}\r\n        <input type=\"button\" name=\"Submit2\" value=\"Cancel\" onClick=\"window.close();\" class=\"button\" />\r\n        {/if}\r\n	</div>\r\n      </td>\r\n    </tr>\r\n  </table>\r\n</form>', 35);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (21, 1, '<h1>Patrol Points</h1>\r\n<div align=\"center\">\r\n<table cellpadding=\"4\" cellspacing=\"1\" border=\"0\" class=\"table\">\r\n<tr>\r\n<th>Position</th>\r\n<th>Patrol</th>\r\n<th>Points</th>\r\n</tr>\r\n{section name=points loop=\$numpoints}\r\n<tr>\r\n<td align=\"right\" class=\"row\">{\$smarty.section.points.iteration}</td>\r\n<td align=\"center\" class=\"row\">{if \$patrolpoints[points].Patrolname==\$userpatrol}<h3>{\$patrolpoints[points].Patrolname}</h3>{else}{\$patrolpoints[points].Patrolname}{/if}</td>\r\n<td align=\"center\" class=\"row\">{\$patrolpoints[points].Points}</td>\r\n</tr>\r\n{/section}\r\n</table>\r\n</div>', 36);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (22, 1, '{literal}\r\n<script type=\"text/javascript\">\r\n<!--\r\nfunction showhide(id)\r\n{\r\n	var item = null;\r\n    if (document.getElementById)\r\n    {\r\n        item = document.getElementById(id);\r\n    }\r\n    else if (document.all)\r\n    {\r\n        item = document.all[id];\r\n    }\r\n    else if (document.layers)\r\n    {\r\n        item = document.layers[id];\r\n    }\r\n    if(document.form1.is_there_details[0].checked)\r\n    {\r\n        if (item && item.style)\r\n        {\r\n            item.style.display = \"\";\r\n        }\r\n        else if (item)\r\n        {\r\n            item.visibility = \"show\";\r\n        }\r\n    }\r\n    else\r\n    {\r\n        if (item && item.style)\r\n        {\r\n            item.style.display = \"none\";\r\n        }\r\n        else if (item)\r\n        {\r\n            item.visibility = \"hide\";\r\n        }\r\n    }\r\n}\r\n-->\r\n</script>\r\n{/literal}\r\n<h1 class=\"title\" align=\"center\">Add a event</h1>\r\n	<form method=\"post\" name=\"form1\" action=\"{\$editFormAction}\" id=\"form1\">\r\n	<table align=\"center\" width=\"100%\" cellpadding=\"2\" cellspacing=\"0\">\r\n	\r\n		<tr valign=\"baseline\">\r\n		  <td width=\"18%\" align=\"right\" ><div align=\"right\">Summary:</div></td>\r\n		  <td  align=\"left\" width=\"82%\"><input type=\"text\" name=\"summary\" size=\"32\" class=\"inputbox\" /></td>\r\n		</tr>\r\n		<tr valign=\"baseline\">\r\n		  <td  align=\"right\">Starting Date:</td>\r\n		  <td align=\"left\">\r\n<input name=\"sdate\" id=\"sdate\" type=\"text\" size=\"20\" maxlength=\"20\" class=\"inputbox\" /><button id=\"opencal\" name=\"opencal\" class=\"button\">...</button>\r\n            </td>\r\n		</tr>\r\n		<tr valign=\"baseline\">\r\n		  <td  align=\"right\">Ending Date:</td>\r\n		  <td align=\"left\"><input name=\"edate\" id=\"edate\" type=\"text\" size=\"20\" maxlength=\"20\" class=\"inputbox\" /><button id=\"opencal2\" name=\"opencal2\" class=\"button\">...</button>\r\n            </td>\r\n		</tr>\r\n		<tr valign=\"baseline\">\r\n		  <td align=\"right\">Do you wish to attach extra information to this event?</td>\r\n		  <td align=\"left\"><p>\r\n		    <label>\r\n		    <input type=\"radio\" name=\"is_there_details\" value=\"1\" checked=\"checked\" class=\"inputbox\" onclick=\"showhide(''moreinfo''); showhide(''moreinfohead'')\" />\r\n  Yes</label>\r\n		    <label>\r\n		    <input type=\"radio\" name=\"is_there_details\" value=\"0\" class=\"inputbox\" onclick=\"showhide(''moreinfo''); showhide(''moreinfohead'')\" />\r\n  No</label>\r\n		    <br />\r\n	      </p></td>\r\n	  </tr>\r\n		<tr valign=\"top\">\r\n		  <td  align=\"right\"><div id=\"moreinfohead\" style=\"display: none;\">Extra Information: </div></td>\r\n		  <td>\r\n                <div id=\"moreinfo\" style=\"display: '''';\"><textarea id=\"story\" name=\"story\" style=\"width: 100%; height: 25em;\"></textarea></div>\r\n        </td>\r\n	  </tr>\r\n		<tr valign=\"top\">\r\n		  <td colspan=\"2\" align=\"center\"><input name=\"Submit\" type=\"submit\" id=\"Submit\" value=\"Submit\" class=\"button\" />&nbsp;<input type=\"button\" name=\"Submit2\" value=\"Cancel\" onClick=\"window.location=''index.php?page=mythings''\" class=\"button\" />\r\n</td>\r\n		</tr>\r\n	  </table>\r\n	</form>\r\n    <script type=\"text/javascript\">\r\n  {literal}\r\n  Calendar.setup(\r\n    {\r\n      inputField  : \"sdate\",         // ID of the input field\r\n      ifFormat    : \"%Y-%m-%d\",    // the date format\r\n      button      : \"opencal\",       // ID of the button\r\n    }\r\n  );\r\n  Calendar.setup(\r\n    {\r\n      inputField  : \"edate\",         // ID of the input field\r\n      ifFormat    : \"%Y-%m-%d\",    // the date format\r\n      button      : \"opencal2\",       // ID of the button\r\n    }\r\n  );\r\n  {/literal}\r\n</script>', 37);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (24, 2, '<a href=\"index.php?page=forums\">{\$config.troopname} forums</a>\r\n<table width=\"100%\" cellspacing=\"1\" cellpadding=\"2\" class=\"table\">\r\n <tr>\r\n  <th colspan=\"6\" class=\"bighead\">{\$forum.name}</th>\r\n </tr>\r\n {if \$numtopics > 0}\r\n <tr>\r\n   <th class=\"smallhead\">&nbsp;</th>\r\n   <th class=\"smallhead\" width=\"35%\">Subject</th>\r\n   <th class=\"smallhead\" width=\"10%\">Posts</th>\r\n   <th class=\"smallhead\" width=\"10%\">Views</th>\r\n   <th class=\"smallhead\" width=\"20%\">Topic Started</th>\r\n   <th class=\"smallhead\" width=\"20%\">Last Post</th>\r\n </tr>\r\n  {section name=topics loop=\$limit}\r\n      <tr>\r\n        <td class=\"row\" align=\"center\">{if \$topics[topics].new == 1}<img src=\"{\$templateinfo.imagedir}newpost.gif\" alt=\"New Post\" />{else}<img src=\"{\$templateinfo.imagedir}oldpost.gif\" alt=\"No new posts\" />{/if}</td>\r\n        <td class=\"row\">{if \$userauths.read == 1}<a href=\"index.php?page=forums&amp;action=topic&amp;t={\$topics[topics].id}\">{\$topics[topics].subject}</a>{else}{\$topics[topics].subject}{/if}\r\n            <br/>\r\n            <span class=\"forum_desc\">{\$topics[topics].desc}</span>\r\n        </td>\r\n        <td class=\"row\">{\$topics[topics].numposts}</td>\r\n        <td class=\"row\">{\$topics[topics].numviews}</td>\r\n        <td class=\"row\" align=\"center\"><strong>{if \$topics[topics].userposted != \$uname && \$uname != \"Guest\"}<a href=\"index.php?page=pmmain&amp;action=typepm&amp;user={\$topics[topics].userposted}\" title=\"Send a PM to {\$topics[topics].userposted}\">{\$topics[topics].userposted}</a>{else}{\$topics[topics].userposted}{/if}</strong><br />\r\n              {\$topics[topics].dateposted+\$timeoffset|date_format:\"%B %e, %Y %H:%M\"}  </td>\r\n        <td class=\"row\" align=\"center\"><Strong>{if \$topics[topics].lastpost != \$uname && \$uname != \"Guest\"}<a href=\"index.php?page=pmmain&amp;action=typepm&amp;user={\$topics[topics].lastpost}\" title=\"Send a PM to {\$topics[topics].lastpost}\">{\$topics[topics].lastpost}</a>{else}{\$topics[topics].lastpost}{/if}</strong><br />\r\n              {\$topics[topics].lastdate+\$timeoffset|date_format:\"%B %e, %Y %H:%M\"}  </td>\r\n      </tr>\r\n  {/section}\r\n <tr>\r\n  <td class=\"row\" align=\"right\" colspan=\"6\">\r\n    <div style=\"text-align:center\">\r\n    {if \$prev}\r\n        <a href=\"index.php?page=forums&amp;start={\$prev_start}&amp;f={\$forum.id}\"> << Prev</a>\r\n    {/if}\r\n    {section name=pages loop=\$numpages}\r\n        &nbsp;\r\n        {if \$currentpage != \$smarty.section.pages.iteration}\r\n            <a href=\"index.php?page=forums&amp;start={\$num_per_page*\$smarty.section.pages.index}&amp;f={\$forum.id}\">\r\n        {else}\r\n            <span class=\"currpage\">\r\n        {/if}|Page {\$smarty.section.pages.iteration}|{if \$currentpage != \$smarty.section.pages.iteration}</a>{else}</span>{/if}&nbsp;\r\n    {/section}\r\n    {if \$next}\r\n        <a href=\"index.php?page=forums&amp;start={\$next_start}&amp;f={\$forum.id}\">Next >> </a>\r\n    {/if}\r\n    </div>\r\n  </td>\r\n  </tr>\r\n<tr>\r\n<td class=\"row\" align=\"center\" colspan=\"6\">\r\n<table width=\"75%\">\r\n<tr>\r\n<td class=\"row\" align=\"center\" valign=\"middle\"><img src=\"{\$templateinfo.imagedir}newpost.gif\" alt=\"New Post\" /></td>\r\n<td class=\"row\" align=\"left\" valign=\"middle\">New post in the topic</td>\r\n<td class=\"row\" width=\"10%\">&nbsp;</td>\r\n<td class=\"row\" align=\"center\" valign=\"middle\"><img src=\"{\$templateinfo.imagedir}oldpost.gif\" alt=\"No new posts\" /></td>\r\n<td class=\"row\" align=\"left\" valign=\"middle\">No new posts</td>\r\n</tr>\r\n</table>\r\n</td>\r\n</tr>\r\n{else}\r\n <tr>\r\n <td colspan=\"2\" class=\"row\" align=\"center\"><strong>No Topics in this forum</strong></td>\r\n{/if}\r\n</table>\r\n{if \$userauths.new==1 || \$userauths.mod==1}<input type=\"button\" value=\"New Topic\" class=\"button\" onclick=\"window.location=''index.php?page=forums&amp;action=new&amp;f={\$forum.id}''\" />&nbsp;{/if}\r\n{if \$userauths.mod==1}<input type=\"button\" value=\"Moderate Forum\" class=\"button\" onclick=\"window.location=''index.php?page=forums&amp;action=modf&amp;f={\$forum.id}''\" />{/if}', 41);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (24, 3, '<a href=\"index.php?page=forums\">{\$config.troopname} forums</a> >><a href=\"index.php?page=forums&amp;f={\$forum.id}\">{\$forum.name}</a> >> {\$topic.subject}\r\n<table width=\"100%\" cellspacing=\"1\" cellpadding=\"1\" class=\"table\">\r\n <tr>\r\n  <th class=\"bighead\">{\$topic.subject}</th>\r\n </tr>\r\n  {section name=posts loop=\$limit}\r\n      <tr>\r\n        <td class=\"row\">\r\n            <table width=\"100%\" cellspacing=\"1\" cellpadding=\"0\">\r\n             <tr>\r\n             <td rowspan=\"3\" valign=\"top\" align=\"center\" width=\"15%\"><strong>{if \$posts[posts].userposted != \$uname && \$uname != \"Guest\"}<a href=\"index.php?page=pmmain&amp;action=typepm&amp;user={\$posts[posts].userposted}\" title=\"Send a PM to {\$posts[posts].userposted}\">{\$posts[posts].userposted}</a>{else}{\$posts[posts].userposted}{/if}</strong>{if \$posts[posts].useravy != \"\"}<br /><img src=\"avatars/{\$posts[posts].useravy}\" alt=\"Avatar\" border=\"0\" />{/if}</td></tr>\r\n             <tr><th class=\"smallhead\" colspan=\"3\">{if \$posts[posts].subject != \"\"}{\$posts[posts].subject}{else}No Subject{/if} - {\$posts[posts].dateposted+\$timeoffset|date_format:\"%B %e, %Y %H:%M\"}</th></tr>\r\n             <tr><td colspan=\"3\">{\$posts[posts].posttext}<hr style=\"line\" />{\$posts[posts].sig}<br />{if \$posts[posts].edittime != 0}<span style=\"font-size:9px;font-style:italic;\">Last Edited: {\$posts[posts].edittime+\$timeoffset|date_format:\"%B %e, %Y %H:%M\"} by {\$posts[posts].edituser}</span>{/if}</td></tr>\r\n             <tr><th colspan=\"3\" class=\"smallhead\"><div align=\"left\">{if (\$userauths.edit == 1 && \$username == \$posts[posts].userposted) || \$userauths.mod == 1}<input type=\"button\" class=\"button\" value=\"Edit Post\" onclick=\"window.location=''index.php?page=forums&amp;action=edit&amp;p={\$posts[posts].id}&amp;t={\$topic.id}''\" />&nbsp;{/if}\r\n             {if \$limit > 1 && ((\$userauths.delete == 1 && \$username == \$posts[posts].userposted) || \$userauths.mod == 1)}<input type=\"button\" class=\"button\" value=\"Delete Post\" onclick=\"window.location=''index.php?page=forums&amp;action=delete&amp;p={\$posts[posts].id}&amp;t={\$topic.id}''\" />{/if}</div></th></tr>\r\n            </table>\r\n        </td>\r\n      </tr>\r\n  {/section}\r\n   <tr>\r\n  <td class=\"row\" align=\"right\" colspan=\"6\">\r\n    <div style=\"text-align:center\">\r\n    {if \$prev}\r\n        <a href=\"index.php?page=forums&amp;action=topic&amp;start={\$prev_start}&amp;t={\$topic.id}\"> << Prev</a>\r\n    {/if}\r\n    {section name=pages loop=\$numpages}\r\n        &nbsp;\r\n        {if \$currentpage != \$smarty.section.pages.iteration}\r\n            <a href=\"index.php?page=forums&amp;action=topic&amp;start={\$num_per_page*\$smarty.section.pages.index}&amp;t={\$topic.id}\">\r\n        {else}\r\n            <span class=\"currpage\">\r\n        {/if}|Page {\$smarty.section.pages.iteration}|{if \$currentpage != \$smarty.section.pages.iteration}</a>{else}</span>{/if}&nbsp;\r\n    {/section}\r\n    {if \$next}\r\n        <a href=\"index.php?page=forums&amp;action=topic&amp;start={\$next_start}&amp;t={\$topic.id}\">Next >> </a>\r\n    {/if}\r\n    </div>\r\n  </td>\r\n  </tr>\r\n</table>\r\n{if \$userauths.reply}<br /><input type=\"button\" class=\"button\" value=\"Reply to topic\"onclick=\"window.location=''index.php?page=forums&amp;action=post&amp;t={\$topic.id}''\" /><br />{/if}\r\n', 42);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (17, 10, '<h1 class=\"title\" align=\"center\">Edit Event</h1>\r\n	<form method=\"post\" name=\"form1\" action=\"{\$editFormAction}\">\r\n	<table align=\"center\" width=\"100%\" cellpadding=\"2\" cellspacing=\"0\">\r\n	\r\n		<tr valign=\"baseline\">\r\n		  <td width=\"18%\" align=\"right\" ><div align=\"right\">Summary:</div></td>\r\n		  <td  align=\"left\" width=\"82%\"><input type=\"text\" name=\"summary\" value=\"{\$item.summary}\" size=\"32\" class=\"inputbox\" /></td>\r\n		</tr>\r\n		<tr valign=\"baseline\">\r\n		  <td  align=\"right\">Starting Date:</td>\r\n		  <td align=\"left\"><input name=\"sdate\" id=\"sdate\" type=\"text\" size=\"20\" maxlength=\"20\" value=\"{\$item.startdate}\" class=\"inputbox\" /><button id=\"opencal\" name=\"opencal\" class=\"button\">...</button>\r\n            </td>\r\n		</tr>\r\n		<tr valign=\"baseline\">\r\n		  <td  align=\"right\">Ending Date:</td>\r\n		  <td align=\"left\"><input name=\"edate\" id=\"edate\" type=\"text\" size=\"20\" maxlength=\"20\" value=\"{\$item.enddate}\" class=\"inputbox\" /><button id=\"opencal2\" name=\"opencal2\" class=\"button\">...</button>\r\n            </td>\r\n		</tr>\r\n        		<tr valign=\"baseline\">\r\n		  <td align=\"right\">Do you wish to attach extra information to this event?</td>\r\n		  <td align=\"left\"><p>\r\n		    <label>\r\n		    <input type=\"radio\" name=\"is_there_details\" value=\"1\" {if \$item.detail == \"1\"}checked=\"checked\"{/if} class=\"inputbox\" />\r\n  Yes</label>\r\n		    <label>\r\n		    <input type=\"radio\" name=\"is_there_details\" value=\"0\" {if \$item.detail == \"0\"}checked=\"checked\"{/if} class=\"inputbox\" />\r\n  No</label>\r\n		    <br />\r\n	      </p></td>\r\n	  </tr>\r\n		<tr valign=\"top\">\r\n		  <td  align=\"right\"><div id=\"moreinfohead\" style=\"display: '''';\">Extra Information: </div></td>\r\n		  <td>\r\n                <div id=\"moreinfo\" style=\"display: '''';\"><textarea id=\"story\" name=\"story\" style=\"width: 100%; height: 25em;\">{\$detail}</textarea></div>\r\n        </td>\r\n	  </tr>\r\n		<tr valign=\"top\">\r\n		  <td colspan=\"2\" align=\"center\"><input name=\"Submit\" type=\"submit\" id=\"Submit\" value=\"Update Item\" class=\"button\" /><input type=\"button\" name=\"Submit2\" value=\"Cancel\" onClick=\"window.location=''index.php?page=mythings''\" class=\"button\" /></td>\r\n		</tr>\r\n	  </table>\r\n	</form>\r\n    <script type=\"text/javascript\">\r\n  {literal}\r\n  Calendar.setup(\r\n    {\r\n      inputField  : \"sdate\",         // ID of the input field\r\n      ifFormat    : \"%Y-%m-%d\",    // the date format\r\n      button      : \"opencal\",       // ID of the button\r\n      {/literal}\r\n      date: \"{\$item.startdate}\"\r\n      {literal}\r\n    }\r\n  );\r\n  Calendar.setup(\r\n    {\r\n      inputField  : \"edate\",         // ID of the input field\r\n      ifFormat    : \"%Y-%m-%d\",    // the date format\r\n      button      : \"opencal2\",       // ID of the button\r\n    {/literal}\r\n      date: \"{\$item.enddate}\"\r\n      {literal}\r\n    }\r\n  );\r\n  {/literal}\r\n</script>    \r\n    ', 38);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (24, 1, '{if \$numcats > 0}\r\n<table cellspacing=\"1\" cellpadding=\"2\" border=\"0\" width=\"100%\" class=\"table\">\r\n<tr>\r\n<th class=\"bighead\" colspan=\"5\">{\$config.troopname} Forums</th>\r\n</tr>\r\n{section name=cats loop=\$numcats}\r\n<tr>\r\n<th class=\"smallhead\" colspan=\"5\">\r\n {\$cats[cats].name}\r\n</th>\r\n</tr>\r\n{if \$cats[cats].numforums > 0}\r\n<tr>\r\n<th class=\"smallhead\" width=\"5%\">&nbsp;</th>\r\n<th class=\"smallhead\" width=\"45%\">Forum Name</th>\r\n<th class=\"smallhead\" width=\"10%\">Posts/Topics</th>\r\n<th class=\"smallhead\" width=\"10%\">Views</th>\r\n<th class=\"smallhead\" width=\"30%\">Last Post</th>\r\n</tr>\r\n{section name=floop loop=\$cats[cats].numforums}\r\n{if \$cats[cats].forums[floop].allowed == 1}\r\n<tr>\r\n <td class=\"row\" align=\"center\">{if \$cats[cats].forums[floop].new == 1}<img src=\"{\$templateinfo.imagedir}newpost.gif\" alt=\"New Post\" />{else}<img src=\"{\$templateinfo.imagedir}oldpost.gif\" alt=\"No new posts\" />{/if}</td>\r\n <td class=\"row\" valign=\"top\"><a href=\"index.php?page=forums&amp;f={\$cats[cats].forums[floop].id}\">{\$cats[cats].forums[floop].name}</a><br /><span class=\"desc\">{\$cats[cats].forums[floop].desc}</span></td>\r\n <td class=\"row\" valign=\"top\" align=\"center\">{\$cats[cats].forums[floop].numposts} in {\$cats[cats].forums[floop].numtopics}</td>\r\n <td class=\"row\" valign=\"top\" align=\"center\">{\$cats[cats].forums[floop].numviews}</td>\r\n <td class=\"row\" valign=\"top\">{if \$cats[cats].forums[floop].lasttopic}{if \$cats[cats].forums[floop].read == 1}<a href=\"index.php?page=forums&amp;action=topic&amp;t={\$cats[cats].forums[floop].lasttopic}\">{/if}{\$cats[cats].forums[floop].lastsubject}{if \$cats[cats].forums[floop].read == 1}</a>{/if}<br />\r\n {if \$cats[cats].forums[floop].lastpost != \$uname && \$uname != \"Guest\"}<a href=\"index.php?page=pmmain&amp;action=typepm&amp;user={\$cats[cats].forums[floop].lastpost}\" title=\"Send a PM to {\$cats[cats].forums[floop].lastpost}\">{\$cats[cats].forums[floop].lastpost}</a>{else}{\$cats[cats].forums[floop].lastpost}{/if}<br />\r\n {\$cats[cats].forums[floop].lastdate+\$timeoffset|date_format:\"%B %e, %Y %H:%M\"}{else}No Topics{/if}</td>\r\n</tr>\r\n{else}\r\n<tr>\r\n <td class=\"row\" align=\"center\"></td>\r\n <td class=\"row\" valign=\"top\" colspan=\"4\">{\$cats[cats].forums[floop].name}{if \$cats[cats].forums[floop].desc != \"\"}<br /><span class=\"desc\">{\$cats[cats].forums[floop].desc}</span>{/if}</td>\r\n</tr>\r\n{/if}\r\n{/section}\r\n{else}\r\n<td colspan=\"5\" class=\"row\">\r\n<div align=\"center\"><strong>There are no forums in this category</strong></div>\r\n</td>\r\n{/if}\r\n{/section}\r\n<tr>\r\n<td class=\"row\" align=\"center\" colspan=\"5\">\r\n<table width=\"75%\">\r\n<tr>\r\n<td class=\"row\" align=\"center\" valign=\"middle\"><img src=\"{\$templateinfo.imagedir}newpost.gif\" alt=\"New Post\" /></td>\r\n<td class=\"row\" align=\"left\" valign=\"middle\">New post in the forum</td>\r\n<td class=\"row\" width=\"10%\">&nbsp;</td>\r\n<td class=\"row\" align=\"center\" valign=\"middle\"><img src=\"{\$templateinfo.imagedir}oldpost.gif\" alt=\"No new posts\" /></td>\r\n<td class=\"row\" align=\"left\" valign=\"middle\">No new posts</td>\r\n</tr>\r\n</table>\r\n</td>\r\n</tr>\r\n</table>\r\n{else}\r\n<div align=\"center\"><strong>There are no categories</strong></div>\r\n{/if}', 40);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (24, 4, '<a href=\"index.php?page=forums\">{\$config.troopname} forums</a> >> <a href=\"index.php?page=forums&amp;f={\$forum.id}\">{\$forum.name}</a>{if !\$new} >> <a href=\"index.php?page=forums&amp;action=topic&amp;t={\$topic.id}\">{\$topic.subject}</a>{/if}\r\n<form action=\"{\$postaction}\" method=\"post\" name=\"posttopic\">\r\n<table width=\"100%\" cellspacing=\"1\" cellpadding=\"2\" class=\"table\">\r\n <tr>\r\n  {if !\$new && !\$edit}<th class=\"bighead\" colspan=\"2\">Post a Reply</th>\r\n  {elseif \$new && !\$edit}<th class=\"bighead\" colspan=\"2\">New Topic</th>\r\n  {elseif !\$new && \$edit}<th class=\"bighead\" colspan=\"2\">Edit Post</th>{/if}\r\n </tr>\r\n <tr>\r\n  <td class=\"row\" width=\"20%\">\r\n    <div align=\"right\">Subject:</div>\r\n  </td>\r\n  <td class=\"row\">\r\n    <input type=\"text\" style=\"width:100%\" class=\"inputbox\" name=\"subject\" id=\"subject\" {if \$edit}value=\"{\$post.subject}\"{/if} />\r\n  </td>\r\n </tr>\r\n {if \$new == true}\r\n  <tr>\r\n  <td class=\"row\" width=\"20%\">\r\n     <div align=\"right\">Description:</div>\r\n  </td>\r\n  <td class=\"row\">\r\n    <input type=\"text\" style=\"width:100%\"  class=\"inputbox\" name=\"desc\" id=\"desc\" />\r\n  </td>\r\n </tr>\r\n {/if}\r\n  <tr>\r\n  <td class=\"row\" valign=\"top\">\r\n    <div align=\"right\">Message:</div>\r\n  </td>\r\n    <td><textarea style=\"width:100%\" rows=\"30\" name=\"story\" id=\"story\" class=\"inputbox\"> {if \$edit}{\$post.posttext}{/if}</textarea></td>\r\n </tr>\r\n {if !\$edit}\r\n <tr>\r\n <td colspan=\"2\" class=\"row\" align=\"center\">\r\n   Do you want to be informed when the next person posts in this topic? <input type=\"radio\" name=\"next\" id=\"next\" value=\"1\" {if \$watch.notify == 1 || \$watch.notify == 0}checked=\"checked\"{/if}/>Yes&nbsp;<input type=\"radio\" name=\"next\" id=\"next\" value=\"0\" {if \$watch.notify != 1 && \$watch.notify != 0}checked=\"checked\"{/if}/>No<br />\r\n   Do you want to be informed everytime somebody posts in this topic? <input type=\"radio\" name=\"always\" id=\"always\" value=\"1\" {if \$watch.notify == 2}checked=\"checked\"{/if} />Yes&nbsp;<input type=\"radio\" name=\"always\" id=\"always\" value=\"0\" {if \$watch.notify != 2}checked=\"checked\"{/if} />No\r\n </td>\r\n </tr>\r\n {/if}\r\n <tr>\r\n <td colspan=\"2\" class=\"row\" align=\"center\">\r\n  <span style=\"text-align:center\"><input type=\"submit\" name=\"submit\" value=\"Submit\" class=\"button\" />&nbsp;<input type=\"reset\" name=\"reset\" value=\"Cancel\" class=\"button\" onclick=\"window.location=''{if !\$new}index.php?page=forums&amp;action=topic&amp;t={\$topic.id}{else}index.php?page=forums&amp;f={\$forum.id}{/if}''\" /></span>\r\n </td>\r\n </tr>\r\n</table>\r\n</form>\r\n{if !\$new && !\$edit}\r\n<br />\r\n<table width=\"100%\" cellspacing=\"1\" cellpadding=\"2\" class=\"table\">\r\n <tr>\r\n  <th class=\"bighead\">Topic review for {\$topic.subject}</th>\r\n </tr>\r\n  {section name=posts loop=\$numposts}\r\n      <tr>\r\n        <td class=\"row\">\r\n            <table width=\"100%\" cellspacing=\"1\" cellpadding=\"1\">\r\n             <tr><td><span style=\"font-weight:bold;\">Username: </span>{\$posts[posts].userposted}<br />\r\n             <span style=\"font-weight:bold;\">Date Posted: </span>{\$posts[posts].dateposted+\$timeoffset|date_format:\"%B %e, %Y %H:%M\"}\r\n             </td></tr>\r\n             <tr><td colspan=\"2\">{\$posts[posts].posttext}</td></tr>\r\n            </table>\r\n        </td>\r\n      </tr>\r\n  {/section}\r\n</table>\r\n{/if}', 43);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (23, 1, '<h1>{\$newsitem.title}</h1>\r\n<h2 align=\"center\">{\$newsitem.event+\$timeoffset|date_format:\"%B %e, %Y %H:%M\"}</h2>\r\n<br/>\r\n<div align=\"center\">\r\n{\$newsitem.news}</div>', 39);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (24, 6, '{if \$act == \"\"}\r\n<table width=\"100%\" cellspacing=\"1\" cellpadding=\"2\" class=\"table\">\r\n <tr>\r\n  <th class=\"bighead\" colspan=\"2\">Moderate {\$forum.name}</th>\r\n </tr>\r\n <tr>\r\n <th class=\"smallhead\" width=\"65%\">Topic Subject</th>\r\n <th class=\"smallhead\">Action</th>\r\n </tr>\r\n {section name=topics loop=\$numtopics}\r\n <tr>\r\n <td class=\"row\">{\$topics[topics].subject}</td>\r\n <td class=\"row\"><div align=\"center\">{if \$numforums > 1}<input type=\"button\" value=\"Move Topic\" onclick=\"window.location = ''index.php?page=forums&action=modf&a=move&t={\$topics[topics].id}&f={\$forum.id}''\" class=\"button\" />&nbsp;{/if}<input type=\"button\" value=\"Delete Topic\" onclick=\"window.location = ''index.php?page=forums&action=modf&a=del&t={\$topics[topics].id}&f={\$forum.id}''\" class=\"button\" /></div></td>\r\n </tr>\r\n {/section}\r\n</table>\r\n<a href=\"index.php?page=forums\">{\$config.troopname} forums</a> >><a href=\"index.php?page=forums&f={\$forum.id}\">Moderate {\$forum.name}</a>\r\n{elseif \$act==\"move\"}\r\n<div align=\"center\">Where do you want to move the topic to?<br />\r\n <form method=\"POST\" action=\"{\$editFormAction}\" name=\"form\">\r\n <select name=\"forum\" class=\"inputbox\">\r\n  {section name=loop loop=\$numforums}\r\n   {if \$forums[loop].id != \$forum.id}<option value=\"{\$forums[loop].id}\">{\$forums[loop].name}</option>{/if}\r\n  {/section}\r\n </select><br />\r\n <input type=\"Submit\" name=\"submit\" id=\"submit\" value=\"Move Topic\" class=\"button\" />&nbsp;<input type=\"button\" value=\"Cancel\" id=\"cancel\" name=\"cancel\" onclick=\"window.location = ''index.php?page=forums&action=modf&f={\$forum.id}''\" class=\"button\" />\r\n </form></div>\r\n{elseif \$act==\"del\"}\r\n<div align=\"center\">Are you sure you want to delete this topic?<br />\r\n <form method=\"POST\" action=\"{\$editFormAction}\" name=\"form\">\r\n <input type=\"Submit\" name=\"submit\" id=\"submit\" value=\"Yes\" class=\"button\" />&nbsp;<input type=\"button\" value=\"No\" id=\"cancel\" name=\"cancel\" onclick=\"window.location = ''index.php?page=forums&action=modf&f={\$forum.id}''\" class=\"button\" />\r\n </form></div>\r\n{/if}', 45);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (24, 7, '<div align=\"center\">Are you sure you want to delete this post?<br />\r\n <form method=\"POST\" action=\"{\$editFormAction}\" name=\"form\">\r\n <input type=\"Submit\" name=\"submit\" id=\"submit\" value=\"Yes\" class=\"button\" />&nbsp;<input type=\"button\" value=\"No\" id=\"cancel\" name=\"cancel\" onclick=\"window.location = ''index.php?page=forums&action=topic&t={\$tid}''\" class=\"button\" />\r\n </form></div>', 46);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (25, 1, '{literal}\r\n<script type=\"text/javascript\">\r\n<!--\r\nfunction deletepm(articleId, page) {\r\nif (confirm(\"This will delete the personal message. Continue?\"))\r\ndocument.location = \"index.php?page=pmmain&action=delete&old=\"+page+\"&id=\" + articleId;\r\n}\r\n//-->\r\n</script>\r\n{/literal}\r\n<table cellspacing=\"0\" cellpadding=\"2\" border=\"0\" width=\"100%\">\r\n<tr>\r\n<th colspan=\"2\" class=\"bighead\">{\$onpage}</th>\r\n</tr>\r\n<tr>\r\n<td align=\"center\" width=\"15%\"><input type=\"button\" class=\"button\" value=\"Inbox\" onclick=\"window.location=''index.php?page=pmmain''\" />&nbsp;<input type=\"button\" class=\"button\" value=\"Sentbox\" onclick=\"window.location=''index.php?page=pmmain&amp;action=sentbox''\" />&nbsp;<input type=\"button\" class=\"button\" value=\"Savebox\" onclick=\"window.location=''index.php?page=pmmain&amp;action=savebox''\" />&nbsp;<input type=\"button\" class=\"button\" value=\"Drafts\" onclick=\"window.location=''index.php?page=pmmain&amp;action=drafts''\" />&nbsp;<input type=\"button\" class=\"button\" value=\"New PM\" onclick=\"window.location=''index.php?page=pmmain&amp;action=typepm''\" /></td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\" valign=\"top\">\r\n{if \$numpm > 0}\r\n    <table cellspacing=\"1\" cellpadding=\"2\" class=\"table\" width=\"100%\">\r\n     <tr>\r\n     <th class=\"smallhead\"></th>\r\n     <th class=\"smallhead\">Subject</th>\r\n     <th class=\"smallhead\">{if \$onpage != \"Drafts\" && \$onpage != \"Sentbox\"}From{else}To{/if}</th>\r\n     <th class=\"smallhead\">{if \$onpage != \"Drafts\"}Date Sent{else}Date Saved{/if}</th>\r\n     <th class=\"smallhead\">Action</th>\r\n     </tr>\r\n     {section name=pmloop loop=\$numpm}\r\n     <tr>\r\n     <td {if \$pm[pmloop].readpm == 0 && \$onpage != \"Drafts\"}class=\"newrow\"{else}class=\"row\"{/if} width=\"3%\" align=\"center\">{if \$pm[pmloop].readpm == 0 && \$onpage != \"Drafts\"}<img src=\"{\$templateinfo.imagedir}newpm.gif\" alt=\"New\" border=\"0\" />{else}<img src=\"{\$templateinfo.imagedir}oldpm.gif\" alt=\"Read\" border=\"0\" />{/if}</td>\r\n      <td {if \$pm[pmloop].readpm == 0 && \$onpage != \"Drafts\"}class=\"newrow\"{else}class=\"row\"{/if} width=\"30%\"><a href=\"index.php?page=pmmain&amp;action=readpm&amp;id={\$pm[pmloop].id}\" title=\"{\$pm[pmloop].text|strip_tags|truncate}\">{\$pm[pmloop].subject}</a></td>\r\n      <td {if \$pm[pmloop].readpm == 0 && \$onpage != \"Drafts\"}class=\"newrow\"{else}class=\"row\"{/if} width=\"30%\" align=\"center\">{if \$onpage != \"Drafts\" && \$onpage != \"Sentbox\"}{\$pm[pmloop].fromuser}{else}{\$pm[pmloop].touser}{/if}</td>\r\n      <td {if \$pm[pmloop].readpm == 0 && \$onpage != \"Drafts\"}class=\"newrow\"{else}class=\"row\"{/if} width=\"25%\" align=\"center\">{\$pm[pmloop].date+\$timeoffset|date_format:\"%B %e, %Y %n %H:%M\"}</td>\r\n      <td {if \$pm[pmloop].readpm == 0 && \$onpage != \"Drafts\"}class=\"newrow\"{else}class=\"row\"{/if} align=\"center\"><input type=\"button\" class=\"button\" value=\"Delete\" onclick=\"deletepm({\$pm[pmloop].id}, ''{\$onpage}'');\" />\r\n      {if \$onpage == \"Inbox\"}<br /><input type=\"button\" class=\"button\" value=\"Move to Savebox\" onclick=\"window.location=''index.php?page=pmmain&amp;action=save&amp;id={\$pm[pmloop].id}''\" />{/if}{if \$onpage == \"Drafts\"}<br /><input type=\"button\" class=\"button\" value=\"Send PM\" onclick=\"window.location=''index.php?page=pmmain&amp;action=send&amp;id={\$pm[pmloop].id}''\" /><br /><input type=\"button\" class=\"button\" value=\"Edit PM\" onclick=\"window.location=''index.php?page=pmmain&amp;action=edit&amp;id={\$pm[pmloop].id}''\" />{/if}</td>\r\n     </tr>\r\n     {/section}\r\n    </table>\r\n{else}\r\nThe are no messages in the {\$onpage} folder\r\n{/if}\r\n</td>\r\n</tr>\r\n</table>', 48);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (25, 2, '{literal}\r\n<script type=\"text/javascript\">\r\n<!--\r\nfunction deletepm(articleId, page) {\r\nif (confirm(\"This will delete the personal message. Continue?\"))\r\ndocument.location = \"index.php?page=pmmain&action=delete&old=\"+page+\"&id=\" + articleId;\r\n}\r\n//-->\r\n</script>\r\n{/literal}\r\n<table cellspacing=\"0\" cellpadding=\"2\" border=\"0\" width=\"100%\">\r\n<tr>\r\n<th colspan=\"2\" class=\"bighead\">{\$pm.subject}</th>\r\n</tr>\r\n<tr>\r\n<td align=\"center\" width=\"15%\"><input type=\"button\" class=\"button\" value=\"Inbox\" onclick=\"window.location=''index.php?page=pmmain''\" />&nbsp;<input type=\"button\" class=\"button\" value=\"Sentbox\" onclick=\"window.location=''index.php?page=pmmain&amp;action=sentbox''\" />&nbsp;<input type=\"button\" class=\"button\" value=\"Savebox\" onclick=\"window.location=''index.php?page=pmmain&amp;action=savebox''\" />&nbsp;<input type=\"button\" class=\"button\" value=\"Drafts\" onclick=\"window.location=''index.php?page=pmmain&amp;action=drafts''\" />&nbsp;<input type=\"button\" class=\"button\" value=\"New PM\" onclick=\"window.location=''index.php?page=pmmain&amp;action=typepm''\" /></td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\" valign=\"top\">\r\n    <table cellspacing=\"1\" cellpadding=\"2\" width=\"100%\">\r\n     <tr>\r\n     <td align=\"left\"><span style=\"font-weight:bold\">{if !\$drafts && !\$sentbox}From:</span> {\$pm.fromuser}{else}To:</span> {\$pm.touser}{/if}</td>\r\n     </tr>\r\n     <tr>\r\n     <td align=\"left\"><span style=\"font-weight:bold\">{if !\$drafts}Date Sent:{else}Date Saved: {/if}</span> {\$pm.date+\$timeoffset|date_format:\"%B %e, %Y %H:%M\"}</td>\r\n     </tr>\r\n     <tr>\r\n     <td>\r\n     {if \$pm.type != 4 && \$pm.type != 2}\r\n        <input type=\"button\" class=\"button\" value=\"Reply to message\" name=\"reply\" id=\"reply\" onclick=\"window.location=''index.php?page=pmmain&amp;action=reply&amp;id={\$pm.id}'';\" /> &nbsp;\r\n     {/if}\r\n     {if \$pm.type == 4}\r\n        <input type=\"button\" class=\"button\" value=\"Send message\" name=\"send\" id=\"send\" onclick=\"window.location=''index.php?page=pmmain&amp;action=send&amp;id={\$pm.id}'';\" /> &nbsp;\r\n        <input type=\"button\" class=\"button\" value=\"Edit message\" name=\"edit\" id=\"edit\" onclick=\"window.location=''index.php?page=pmmain&amp;action=edit&amp;id={\$pm.id}'';\" /> &nbsp;\r\n     {/if}\r\n     <input type=\"button\" class=\"button\" value=\"Delete message\" name=\"delete\" id=\"delete\" onclick=\"deletepm({\$pm.id});\" /> &nbsp;\r\n     </td>\r\n     </tr>\r\n     <tr>\r\n     <th class=\"smallhead\">Message</th>\r\n  </tr>\r\n     <tr>\r\n     <td>{\$pm.text}</td>\r\n     </tr>\r\n    </table>\r\n</td>\r\n</tr>\r\n</table>', 49);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (26, 1, '<form name=\"relogons\" method=\"post\" action=\"{\$action}\">\r\n<table width=\"100%\" cellspacing=\"1\" cellpadding=\"2\">\r\n<tr>\r\n<th class=\"smallhead\" colspan=\"2\">You are required to logon to access this page</th>\r\n</tr>\r\n        <tr>\r\n        <td class=\"row\" width=\"50%\">\r\n            <div align=\"right\">Username:</div>\r\n        </td>\r\n        <td class=\"row\">\r\n            <div align=\"left\"><input type=\"text\" name=\"username2\" id=\"username2\" size=\"50\" maxlength=\"50\" class=\"inputbox\" /></div>\r\n        </td>\r\n        </tr>\r\n        <tr>\r\n        <td class=\"row\">\r\n			<div align=\"right\">Password</div>\r\n        </td>\r\n        <td class=\"row\">\r\n            <div align=\"left\"><input type=\"password\" name=\"password2\" id=\"password2\" size=\"50\" maxlength=\"50\" class=\"inputbox\" /></div>\r\n		</td>\r\n        </tr>\r\n        <tr>\r\n        <td class=\"row\" colspan=\"2\" align=\"center\">\r\n				<input type=\"submit\" name=\"relogon\" id=\"relogon\" value=\"Login\" class=\"button\" />\r\n        </td>\r\n        </tr>\r\n                <tr>\r\n        <td class=\"row\" colspan=\"2\" align=\"center\">\r\n            <a href=\"index.php?page=forgot\" style=\"font-size:9pt\">I forgot my username and password!</a><br />\r\n        </td>\r\n        </tr>\r\n        <tr>\r\n        <td class=\"row\" colspan=\"2\" align=\"center\">\r\n<a href=\"index.php?page=register\" style=\"font-size:9pt\">I''d like to register.</a>\r\n        </td>\r\n        </tr>\r\n</table>\r\n</form>', 50);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (25, 3, '<table cellspacing=\"0\" cellpadding=\"2\" border=\"0\" width=\"100%\">\r\n<tr>\r\n<th colspan=\"2\" class=\"bighead\">{if !\$editmode}New{else}Edit{/if} Personal Message</th>\r\n</tr>\r\n<tr>\r\n<td align=\"center\" width=\"15%\"><input type=\"button\" class=\"button\" value=\"Inbox\" onclick=\"window.location=''index.php?page=pmmain''\" />&nbsp;<input type=\"button\" class=\"button\" value=\"Sentbox\" onclick=\"window.location=''index.php?page=pmmain&amp;action=sentbox''\" />&nbsp;<input type=\"button\" class=\"button\" value=\"Savebox\" onclick=\"window.location=''index.php?page=pmmain&amp;action=savebox''\" />&nbsp;<input type=\"button\" class=\"button\" value=\"Drafts\" onclick=\"window.location=''index.php?page=pmmain&amp;action=drafts''\" />&nbsp;<input type=\"button\" class=\"button\" value=\"New PM\" onclick=\"window.location=''index.php?page=pmmain&amp;action=typepm''\" /></td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\" valign=\"top\">\r\n<form action=\"{\$postaction}\" method=\"post\" name=\"posttopic\">    \r\n    <table cellspacing=\"1\" cellpadding=\"2\" width=\"100%\">\r\n     <tr>\r\n     <td align=\"left\">To: <input type=\"text\" size=\"50\" class=\"inputbox\" name=\"touser\" id=\"touser\" value=\"{\$editpm.touser}\" /></td>\r\n     </tr>\r\n     <tr>\r\n     <td align=\"left\">Subject: <input type=\"text\" size=\"50\" class=\"inputbox\" name=\"subject\" id=\"subject\" value=\"{\$editpm.subject}\" /></td>\r\n     </tr>\r\n     <tr>\r\n     <th class=\"smallhead\">Message</th>\r\n     <tr>\r\n     <td><textarea style=\"width:100%\" rows=\"30\" name=\"story\" id=\"story\" class=\"inputbox\">{\$editpm.text}</textarea></td>\r\n     </tr>\r\n      <tr>\r\n    <td colspan=\"2\" class=\"row\" align=\"center\">\r\n  <span style=\"text-align:center\"><input type=\"submit\" name=\"submit\" value=\"Send PM\" class=\"button\" />&nbsp;<input type=\"submit\" name=\"submit\" value=\"Save PM\" class=\"button\" />&nbsp;<input type=\"reset\" name=\"reset\" value=\"Cancel\" class=\"button\" {if !\$editmode}onclick=\"window.location=''index.php?page=pmmain''\"{else}onclick=\"window.location=''index.php?page=pmmain&action=drafts''\"{/if} /></span>\r\n </td>\r\n </tr>\r\n</table>\r\n </form>\r\n</td>\r\n</tr>\r\n</table>', 51);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (27, 1, '{if \$numadva > 0 }\r\n    <a name=\"top\"></a><h1>Award Badges</h1>\r\n    <div align=\"left\">\r\n    This is a list of all award badges and their requirements<br />\r\n    {section name=num loop=\$numadva}\r\n     - <a href=\"#{\$advan[num].advancement|replace:'' '':''''}\">{\$advan[num].advancement}</a><br />\r\n    {/section}\r\n    </div>\r\n    <table width=\"100%\"  border=\"0\" cellspacing=\"1\" cellpadding=\"4\" class=\"table\">\r\n        {section name=num loop=\$numadva}\r\n          {if \$advan[num].numitems > 0}\r\n          <tr><th colspan=\"2\" class=\"bighead\"><a id=\"{\$advan[num].advancement|replace:'' '':''''}\" name=\"{\$advan[num].advancement|replace:'' '':''''}\"></a>{\$advan[num].advancement}</th></tr>\r\n          <tr>\r\n            <th width=\"83%\" scope=\"col\"  class=\"smallhead\">Requirement</th>\r\n          </tr>\r\n          {section name=numreq loop=\$advan[num].numitems}\r\n              <tr valign=\"top\" align=\"center\">\r\n                <td class=\"row\"><strong>{\$advan[num].items[numreq].item}</strong><br />\r\n                <span class=\"comment\">{\$advan[num].items[numreq].description}</span></td>\r\n              </tr>\r\n          {/section}\r\n          <tr>\r\n          <td colspan=\"2\" class=\"row\">\r\n            <a href=\"#top\">Back to top</a>\r\n          </td>\r\n          </tr>\r\n          {/if}\r\n         {/section}\r\n          <tr>\r\n          <td colspan=\"2\" class=\"row\">\r\n            <a href=\"index.php?page=advancements\">Back to award schemes</a>\r\n          </td>\r\n          </tr>\r\n    </table>\r\n{else}\r\n<h1>Award Schemes</h1>\r\n<div align=\"center\">\r\n<ul>\r\n{section name=scheme loop=\$numschemes}\r\n<li><a href=\"index.php?page=advancements&amp;scheme={\$schemes[scheme].id}\">{\$schemes[scheme].name} Award Scheme</a></li>\r\n {/section}\r\n </ul></div>\r\n{/if}', 52);";
$sql[] = "INSERT INTO `{$database['prefix']}pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES (1, 2, '    {if \$album_id != ''''}\r\n    {if \$numphotos > 0}\r\n    <div align=\"center\">{if \$patrolpages != 1}<a href=\"index.php?page=photos\">{else}<a href=\"index.php?page=patrolpages&amp;patrol={\$patrolname}&amp;content=photos\">{/if}Back to album list</a></div>\r\n    <div align=\"center\">\r\n    {if \$prev && \$patrolpages != 1}\r\n        <a href=\"index.php?page=photos&amp;start={\$prev_start}&amp;album={\$album_id}\"> << Prev</a></font>\r\n    {elseif \$prev && \$patrolpages == 1}\r\n        <a href=\"index.php?page=patrolpages&amp;patrol={\$patrolname}&amp;content=photos&amp;start={\$prev_start}&amp;album={\$album_id}\"> << Prev</a></font>\r\n    {/if}\r\n            {section name=pages loop=\$numpages}\r\n                &nbsp;\r\n                {if \$currentpage != \$smarty.section.pages.iteration && \$patrolpages != 1}\r\n                    <a href=\"index.php?page=photos&amp;start={\$num_per_page*\$smarty.section.pages.index}&amp;album={\$album_id}\">\r\n                {elseif \$patrolpages == 1 && \$currentpage != \$smarty.section.pages.iteration}\r\n                    <a href=\"index.php?page=patrolpages&amp;patrol={\$patrolname}&amp;content=photos&amp;start={\$num_per_page*\$smarty.section.pages.index}&amp;album={\$album_id}\">\r\n                {else}\r\n                    <span class=\"currpage\">\r\n                {/if}|Page {\$smarty.section.pages.iteration}|{if \$currentpage != \$smarty.section.pages.iteration}</a>{else}</span>{/if}&nbsp;\r\n            {/section}\r\n            {if \$next && \$patrolpages != 1}\r\n                <a href=\"index.php?page=photos&amp;start={\$next_start}&amp;album={\$album_id}\">Next >> </a>\r\n            {elseif \$next && \$patrolpages == 1}\r\n                <a href=\"index.php?page=patrolpages&amp;patrol={\$patrolname}&amp;content=photos&amp;start={\$next_start}&amp;album={\$album_id}\">Next >> </a>\r\n            {/if}\r\n            </div>\r\n            \r\n            \r\n        <table style=\"width:{\$config.photox*\$config.photos_per_row}px\" cellspacing=\"1\" cellpadding=\"4\" class=\"table\" align=\"center\">\r\n            <tr>\r\n            <th colspan=\"2\" class=\"smallhead\">{\$view_album_name} (Page {\$currentpage} of {\$numpages}){\$limit}</th>\r\n            </tr>\r\n            <tr style=\"height:{\$config.photoy}px\">\r\n            {section name=photos loop=\$limit}\r\n                {if \$photo[photos].filename != \"\"}<td class=\"row\" style=\"width:{\$config.photox}px\"><div align=\"center\"><img src=\"getphoto.php?pic={\$photo[photos].ID}&amp;maxsizex={\$config.photox}&amp;maxsizey={\$config.photoy}\" /><br />\r\n                <strong>Date Posted: {\$photo[photos].date+\$timeoffset|date_format:\"%B %e, %Y\"}</strong><br />\r\n                {\$photo[photos].caption}\r\n                </div></td>{/if}\r\n            {if (\$smarty.section.photos.iteration % \$config.photos_per_row == 0) && (\$smarty.section.photos.iteration != \$limit)}</tr><tr style=\"height:{\$config.photoy}px\">{/if}\r\n        {/section}\r\n        </tr></table>\r\n        \r\n        \r\n        <div align=\"center\">\r\n            {if \$prev && \$patrolpages != 1}\r\n                <a href=\"index.php?page=photos&amp;subcat=troop&amp;start={\$prev_start}&amp;album={\$album_id}\" class=\"style1\"> << Prev</a>\r\n            {elseif \$prev && \$patrolpages == 1}\r\n                <a href=\"index.php?page=patrolpages&amp;patrol={\$patrolname}&amp;content=photos&amp;start={\$prev_start}&amp;album={\$album_id}\"> << Prev</a></font>\r\n            {/if}\r\n            {section name=pages loop=\$numpages}\r\n                &nbsp;\r\n                {if \$currentpage != \$smarty.section.pages.iteration && \$patrolpages != 1}\r\n                    <a href=\"index.php?page=photos&amp;start={\$num_per_page*\$smarty.section.pages.index}&amp;album={\$album_id}\" class=\"style1\">\r\n                {elseif \$patrolpages == 1 && \$currentpage != \$smarty.section.pages.iteration}\r\n                    <a href=\"index.php?page=patrolpages&amp;patrol={\$patrolname}&amp;content=photos&amp;start={\$num_per_page*\$smarty.section.pages.index}&amp;album={\$album_id}\">\r\n                {else}\r\n                    <span class=\"currpage\">\r\n                {/if}|Page {\$smarty.section.pages.iteration}|{if \$currentpage != \$smarty.section.pages.iteration}</a>{else}</span>{/if}&nbsp;\r\n            {/section}\r\n            {if \$next && \$patrolpages != 1}\r\n                <a href=\"index.php?page=photos&amp;subcat=troop&amp;start={\$next_start}&amp;album={\$album_id}\" class=\"style1\">Next >> </a>\r\n            {elseif \$next && \$patrolpages == 1}\r\n                <a href=\"index.php?page=patrolpages&amp;patrol={\$patrolname}&amp;content=photos&amp;start={\$next_start}&album={\$album_id}\">Next >> </a>\r\n            {/if}\r\n        </div>\r\n        {/if}\r\n    {/if}', 53);";


