  'INSERT INTO hive_cplink VALUES (1, \'PHP Manual\', \'http://www.php.net/manual/en/\', 1, 1)',
  'INSERT INTO hive_cplink VALUES (2, \'MySQL Manual\', \'http://www.mysql.com/doc/en/\', 2, 1)',
  'INSERT INTO hive_setting VALUES (1, 1, \'Application name\', \'This is the name of the program that will be running. It will be viewable on every page.\', \'appname\', \'HiveMail\', \'text\', 2)',
  'INSERT INTO hive_setting VALUES (2, 9, \'Open for registration\', \'Turn this off if you do not want to accept new users.\', \'regopen\', 1, \'yesno\', 1)',
  'INSERT INTO hive_setting VALUES (3, 9, \'Require new user validation\', \'If this is turned on, users will not be able to use HiveMail&trade; until you activate their account. Otherwise, after registering the user\\\'s account will be activated immediately.\', \'moderate\', 0, \'yesno\', 2)',
  'INSERT INTO hive_setting VALUES (4, 1, \'Default skin\', \'This is the default skin that will be used when a user is not logged in or when a user first registers.\', \'defaultskin\', 1, \'select_skin\', 5)',
  'INSERT INTO hive_setting VALUES (5, 1, \'Domain names\', \'The domain name HiveMail&trade; is running on, with a preceding \\\'@\\\' character.<br />To specify multiple domain names, put each one on its own line.<br />The first domain name on the list is the primary one.<br /><br />For example:<br />@yourmail.com<br />@example.net\', \'domainname\', \'@yourmail.com\', \'area\', 4)',
  'INSERT INTO hive_setting VALUES (6, 2, \'Threshold for storage gauge\', \'The space gauge will not show up for users until they hit this minimum percentage.<br />It is recommended to set this to a high value, such as 80. Do not include \\\'%\\\'.\', \'minpercentforgauge\', 80, \'text\', 6)',
  'INSERT INTO hive_setting VALUES (7, 7, \'Date format\', \'The format for displaying date.<br />For example: \\"F j, Y\\" will display July 1, 2002, \\"m-d-Y\\" will display 07-01-2002.<br />For more possible combinations, please see the <a href=\\"http://www.php.net/manual/en/function.date.php\\" target=\\"_blank\\">PHP manual</a>.\', \'dateformat\', \'m-d-Y\', \'text\', 2)',
  'INSERT INTO hive_setting VALUES (8, 7, \'Time format\', \'The format for displaying time.<br />For example: \\"H:i\\" will display 15:49, \\"h:i A\\" will display 03:49 PM.<br />For more possible combinations, please see the <a href=\\"http://www.php.net/manual/en/function.date.php\\" target=\\"_blank\\">PHP manual</a>.\', \'timeformat\', \'h:i A\', \'text\', 3)',
  'INSERT INTO hive_setting VALUES (9, 5, \'Use enhanced top menu\', \'Enable the advanced menu for the control panel. Please note that this menu might not work on some browsers.\', \'cp_topmenu\', 0, \'yesno\', 1)',
  'INSERT INTO hive_setting VALUES (97, 17, \'Path to the mail messages\', \'Please enter the path to where the mail is kept on your system. If you are accessing an mbox-formatted account, enter the complete path to the file in which messages are saved. In the case of MH or Maildir, only enter the directory in which the files are kept.\', \'fg_path\', \'\', \'text\', 2)',
  'INSERT INTO hive_setting VALUES (11, 18, \'Mail server\', \'The server that holds the POP3 account. If you are not sure what the mail server is, contact your host and ask them\', \'pop3_server\', \'\', \'text\', 2)',
  'INSERT INTO hive_setting VALUES (12, 18, \'Server port\', \'The port that\\\'s used to connect to the mail server above, usually this is 110.\', \'pop3_port\', 110, \'text\', 3)',
  'INSERT INTO hive_setting VALUES (13, 18, \'Username\', \'The username that is used to log on to the mail server and connect to the POP3 account.\', \'pop3_username\', \'\', \'text\', 4)',
  'INSERT INTO hive_setting VALUES (14, 18, \'Password\', \'The password that is used to log on to the mail server and connect to the POP3 account.\', \'pop3_password\', \'\', \'text\', 5)',
  'INSERT INTO hive_setting VALUES (100, 4, \'Use IMAP functions\', \'If PHP on this server is compiled with the IMAP extension, HiveMail&trade; can use it to connect to outside POP3 accounts. Turning this option on will also allow your users to connect to POP3 servers that require SSL authorization, something that otherwise would not be possible.\', \'pop3_useimap\', 0, \'yesno\', 8)',
  'INSERT INTO hive_setting VALUES (16, 6, \'Sender Name\', \'The name that\\\'s sent as the \\\'From\\\' header when sending bounces or other messages through the system.<br />This can be an email address only (e.g mail@example.com) or a name with the email address enclosed in < and > (e.g Mail Delivery System &lt;mail@example.com&gt;).\', \'smtp_errorfrom\', \'Mail Delivery System\', \'text\', 1)',
  'INSERT INTO hive_setting VALUES (17, 5, \'Enable expand/collapse\', \'Turn on the [+] and [-] icons that expand and collapse tables in the control panel.\', \'cp_plus\', 0, \'yesno\', 2)',
  'INSERT INTO hive_setting VALUES (18, 2, \'Maximum messages per page\', \'Set this to the maximum number of mail messages users can request to see per page.\', \'maxperpage\', 50, \'text\', 1)',
  'INSERT INTO hive_setting VALUES (19, 1, \'Program location\', \'The complete URL (without any trailing slashes) of the program, e.g: http://www.example.com/webmail.\', \'appurl\', \'hxxp://www.yourmail.com/\', \'text\', 3)',
  'INSERT INTO hive_setting VALUES (20, 7, \'Time zone offset\', \'The time (in hours) this server is offset from GMT. Please select the most appropriate option.<br />For a complete list of time zones, please <a href=\\"http://greenwichmeantime.com/info/timezone.htm\\" target=\\"_blank\\">click here</a>.\', \'timeoffset\', 2, \'timezone\', 1)',
  'INSERT INTO hive_setting VALUES (22, 3, \'Last time POP3 was checked\', \'Do not touch.\', \'pop3_lastrun\', 1103750833, \'text\', 0)',
  'INSERT INTO hive_setting VALUES (23, 18, \'Check the account every:\', \'The frequency for checking the POP3 account for messages, in minutes.<br />Set this to 0 to check the account whenever a user visits the system (not advisable).\', \'pop3_runevery\', 2, \'text\', 6)',
  'INSERT INTO hive_setting VALUES (24, 18, \'Using cron job script?\', \'If you are using the cron script, set this to Yes. If this is set to Yes, email will not be checked from within the program but only using the cron job script.<br />Note: You need to set up the script yourself, this is not automatic!\', \'pop3_cron\', 0, \'yesno\', 1)',
  'INSERT INTO hive_setting VALUES (25, 9, \'Send new users a greeting message\', \'Turn this on to send a welcome email to all new users.<br />The message and subject of this email can be edited in the<br />signup_welcome_subject and signup_welcome_message templates.\', \'sendgreeting\', 1, \'yesno\', 6)',
  'INSERT INTO hive_setting VALUES (26, 9, \'Reserved names\', \'Text in this setting (one entry per line) will not be allowed in usernames or real names. This can contain wildcards. Examples:<br />\\nadmin* - name beginning with admin<br />\\n*hive* - name containing hive<br />\\n*site - name ending in site<br />\\nweb*master - name beginning with web and ending in master (including webmaster)\', \'reservedtext\', \'admin*\\n*master\', \'area\', 4)',
  'INSERT INTO hive_setting VALUES (33, 2, \'Maximum size of sound files (in bytes)\', \'This is the maximum file size of custom sound files users can upload. 1024 bytes = 1kb.\', \'maxsoundfile\', 102400, \'text\', 2)',
  'INSERT INTO hive_setting VALUES (34, 10, \'Self-referenced attachments\', \'Sometimes messages contain attachments that are used inside the message itself (background images, sounds, etc.). This option allows you to enable this self-reference, but unfortunately this behavior is sometimes used by viruses. This is why we give you the option to disable this feature altogether.\', \'allowcid\', 0, \'yesno\', 1)',
  'INSERT INTO hive_setting VALUES (29, 9, \'Notification of new users\', \'Whenever a new user registers, an email notification will be sent to these addresses.<br />Separate address with space or new line.<br />The message and subject of this email can be edited in the<br />signup_notify_subject and signup_notify_message templates.\', \'newuseremail\', \'\', \'area\', 7)',
  'INSERT INTO hive_setting VALUES (35, 4, \'Maximum messages to process\', \'This is the maximum number of messages that are processed every time a connection is made to a POP3 account.\', \'pop3_maxmsgs\', 50, \'text\', 9)',
  'INSERT INTO hive_setting VALUES (30, 5, \'Enable enhanced template tree\', \'Whether or not the dynamic template tree will be used in the template editor. This feature may only work in certain browsers.\', \'cp_templatetree\', 0, \'yesno\', 3)',
  'INSERT INTO hive_setting VALUES (31, 18, \'Leave messages on mailbox\', \'If this is set to yes, messages will be left on the server even after HiveMail&trade; downloads them. We strongly recommended that you leave this option turned off.\', \'pop3_nodelete\', 0, \'yesno\', 7)',
  'INSERT INTO hive_setting VALUES (32, 3, \'HiveMail&trade; Version\', \'Don\\\'t touch!\', \'versionnum\', \'1.3\', \'text\', 0)',
  'INSERT INTO hive_setting VALUES (36, 1, \'Use redirection messages\', \'HiveMail&trade; displays redirection messages when users perform an action that requires processing. You can disable these messages here, and then your users will be redirected immediately, without any confirmation for their action.\', \'useredirect\', 1, \'yesno\', 7)',
  'INSERT INTO hive_setting VALUES (38, 8, \'Upload attachments in safe mode\', \'If your server is running in Safe Mode or if open_basedir restrictions are in effect, enable this option and update the setting below.\', \'safeupload\', 0, \'yesno\', 1)',
  'INSERT INTO hive_setting VALUES (39, 8, \'Temporary director for attachments\', \'If you enable uploads in Safe Mode, this must be set to the folder in which the attachments will be <i>temporarily</i> stored. PHP must have write permission in this folder, i.e CHMOD 0777.\', \'tmppath\', \'/tmp\', \'text\', 2)',
  'INSERT INTO hive_setting VALUES (41, 9, \'Terms of Service\', \'If the service you offer has any terms, enter them in this box. When users sign up, they will be required to agree to these terms.\', \'termsofservice\', \'\', \'area\', 5)',
  'INSERT INTO hive_setting VALUES (42, 9, \'Registration code check\', \'Turning this on will force users to enter a random number, as it appears in an image. This protection method drastically reduces the chances of having a machine create new accounts automatically.\', \'regcodecheck\', 1, \'yesno\', 8)',
  'INSERT INTO hive_setting VALUES (43, 9, \'Use the same effect for all numbers\', \'If you enable the registration code check above, users will be presented with a series of random images. Each image uses a different effect to \\"hide\\" the number from machines. However, you have the option to use the same effect on all numbers. This will make it somewhat easier for users to enter the number, but at the same time also make it easier for automatic bots to detect the number.\', \'regcodesameset\', 1, \'yesno\', 9)',
  'INSERT INTO hive_setting VALUES (44, 10, \'Open links in frames\', \'If you would like to open outside links in new windows with a top frame, that will allow users to return to the message they were reading, enable this option. Otherwise new links will open in their own window.\', \'framelinks\', 1, \'yesno\', 2)',
  'INSERT INTO hive_setting VALUES (45, 10, \'Allow manual bouncing\', \'Turn this on to give users the option to bounce messages back to their sender, so it looks like they never reached their mailbox.\', \'allowbouncing\', 0, \'yesno\', 3)',
  'INSERT INTO hive_setting VALUES (46, 11, \'DNS Blocking Lists\', \'If you wish to use anti-spam DNS blocking lists, please input each server you would like e-mail checked against. One entry per line.<br />\\nE.g. relays.osirusoft.com\', \'dnsbls\', \'\', \'area\', 4)',
  'INSERT INTO hive_setting VALUES (47, 11, \'Global blocked senders\', \'Any addresses you enter here will be blocked for all users, <i>even</i> if they are defined as \\"safe\\" for a user. Please use with care!<br />Wildcards may be used. Specify one email address per line. Use only the domain name (e.g \\"domain.com\\") to block all messages from it.\', \'globalblock\', \'\', \'area\', 2)',
  'INSERT INTO hive_setting VALUES (48, 11, \'Global safe senders\', \'Addresses that you add here will never be blocked, for any user in the system, <i>even</i> if the user would like to block it. Useful, for example, if you don\\\'t want users to ignore emails from the webmaster.<br />Wildcards may be used. Specify one email address per line. Use only the domain name (e.g \\"domain.com\\") to block all messages from it.\', \'globalsafe\', \'\', \'area\', 3)',
  'INSERT INTO hive_setting VALUES (49, 5, \'Display quick links\', \'Whether or not to show the list of quick links in the control panel navigation. You can edit those links <a href=\\"cplink.php\\">here</a>.\', \'cp_links\', 1, \'yesno\', 4)',
  'INSERT INTO hive_setting VALUES (50, 1, \'Maintenance mode\', \'During maintenance mode, users can only login and logout - they cannot use any other functions. Useful for when you upgrading the program or performing back-ups. Administrators are unaffected by this setting.\', \'maintain\', 0, \'yesno\', 6)',
  'INSERT INTO hive_setting VALUES (51, 12, \'Store messages in files\', \'Turn this on if you would like to store the message data in the file-system, and not in the database. If this is enabled, you must set the other options in this section as well for this feature to work correctly. You can ignore the rest of the options if this is disabled.\', \'flat_use\', 0, \'yesno\', 1)',
  'INSERT INTO hive_setting VALUES (52, 12, \'Directory to save files in\', \'The full path to the directory in which the message files will be stored. It is <b>STRONGLY</b> recommended that this directory will not be below the \\"web root\\", so users will not be able to access the files directly. PHP must have write permission in this folder, i.e CHMOD 0777.<br /><b>NOTE:</b> If you change this setting, you must rename the original directory to reflect the change. The script will <b>not</b> do this for you.\', \'flat_path\', \'\', \'text\', 2)',
  'INSERT INTO hive_setting VALUES (53, 3, \'File prefix\', \'The message files will be named \\"&lt;prefix&gt;&lt;random hash&gt;.dat\\". You can set the &lt;prefix&gt; in this option, for example \\"message_\\".\', \'flat_prefix\', \'message_\', \'text\', 3)',
  'INSERT INTO hive_setting VALUES (54, 1, \'Display skin menu on login\', \'Whether or not to show the skin selection menu on the login screen, so users can choose a different skin while logging into their account.\', \'skinonlogin\', 1, \'yesno\', 8)',
  'INSERT INTO hive_setting VALUES (55, 1, \'Usergroup permissions for skin menu\', \'If you have turned on the skin menu above, please select the usergroup whose skin permissions will be used to determine which skins to show.\', \'skinonlogingroup\', 2, \'select_usergroup\', 9)',
  'INSERT INTO hive_setting VALUES (56, 2, \'Automatic trash collection maximum value\', \'Limit the number of days for users to automatically empty their trash can.\', \'maxemptybindays\', 30, \'text\', 3)',
  'INSERT INTO hive_setting VALUES (92, 6, \'Enable message bounces\', \'If this is turned on, messages that were sent to non-existent users will be bounced back to the sender. Otherwise, the messages will not be processed and the sender will not be notified of the error.\', \'bounceback\', 1, \'yesno\', 2)',
  'INSERT INTO hive_setting VALUES (58, 13, \'Enable GZIP compression\', \'Whether or not to use GZIP compression, which would reduce the size of HiveMail&trade; generated pages and respectively reduce the loading time of those pages.\', \'gzip_use\', 0, \'yesno\', 1)',
  'INSERT INTO hive_setting VALUES (59, 13, \'Level of compression\', \'The level of GZIP compression, from 1 to 9. The higher the level is, the better the compression rate but the slower it takes to compress the pages. 0 means no compression at all.\', \'gzip_level\', 1, \'text\', 2)',
  'INSERT INTO hive_setting VALUES (60, 9, \'Require secondary email\', \'If you have enabled manual validation of new users (see above), new registrants will be required to enter a secondary email address through which they will be notified when their account is activated. You can cancel this requirement by turning off this option.\', \'reg_requirealtemail\', -1, \'altemail\', 3)',
  'INSERT INTO hive_setting VALUES (61, 14, \'Server name\', \'The hostname or IP address of the SMTP server you would like to use. Leave this empty to use PHP\\\'s built-in functions.\', \'smtp_host\', \'\', \'text\', 1)',
  'INSERT INTO hive_setting VALUES (62, 14, \'Server port\', \'The port that\\\'s used to connect to the SMTP server above, usually this is either 25, 465, 24 or 209.\', \'smtp_port\', 25, \'text\', 2)',
  'INSERT INTO hive_setting VALUES (63, 14, \'HELO greeting\', \'The \\\'greeting\\\' that will be used when connecting to the SMTP server. This setting is not very important, and is typically the same as your domain name or hostname.\', \'smtp_helo\', \'\', \'text\', 3)',
  'INSERT INTO hive_setting VALUES (64, 14, \'Use authentication\', \'Whether or not the SMTP server requires authentication. If you turn this on, you must specify a username and password in	the settings below.\', \'smtp_auth\', 0, \'yesno\', 4)',
  'INSERT INTO hive_setting VALUES (65, 14, \'Username\', \'The username that will be used for authenticating to the SMTP server. If the server requires a username and password, the setting above (Use authentication) must be turned on.\', \'smtp_user\', \'\', \'text\', 5)',
  'INSERT INTO hive_setting VALUES (66, 14, \'Password\', \'The password that will be used for authenticating to the SMTP server.\', \'smtp_pass\', \'\', \'text\', 6)',
  'INSERT INTO hive_setting VALUES (71, 1, \'Allow login from domains\', \'If set, logins will <b>only</b> be allowed from these domains. To specify multiple domain names, put each one on its own line.<br />\\n<br />\\ne.g. example.com would allow logins from users who login at mail.example.com, while users who fill out a login form at example2.net will be rejected<br />\\n<br />\\nLeaving this box blank allows logins from any domain.\', \'logindomains\', \'\', \'area\', 10)',
  'INSERT INTO hive_setting VALUES (72, 1, \'Banned IP addresses or hostnames\', \'IP addresses or hostnames which you wish to ban from using HiveMail&trade;. Wildcards may be used. One entry per line.<br />\\n<br />\\ne.g. 127.* would ban all users with IP addresses beginning with 127.<br />\\n*.example.com would ban all users in the example.com domain.\', \'bannedips\', \'\', \'area\', 11)',
  'INSERT INTO hive_setting VALUES (73, 14, \'Enable keep-alive\', \'HiveMail&trade; can open one connection to the SMTP server when sending multiple messages, which reduces the load on the server and improves performance. However, some SMTP server do not support this feature and will not allow HiveMail&trade; to stay connected for a very long, and as a result messages may not be sent correctly. If you are experiencing such probelms, please disable this option.\', \'smtp_persistent\', 1, \'yesno\', 7)',
  'INSERT INTO hive_setting VALUES (74, 15, \'Enable vBulletin integration\', \'Turn this on to enable the integration with vBulletin. If this is enabled please be sure to fill in the rest of the settings in this category.\', \'vb_use\', 0, \'yesno\', 1)',
  'INSERT INTO hive_setting VALUES (75, 15, \'Location of vBulletin files\', \'Please enter the complete absolute path to the vBulletin program files. This is <i>not</i> the URL to your board! If you are not sure what the path to the files is, please contact your host.\', \'vb_filepath\', \'\', \'text\', 2)',
  'INSERT INTO hive_setting VALUES (76, 15, \'URL to forums\', \'The complete URL of your forums, same as the one you input in vBulletin\\\'s control panel. Please do not add a trailing slash.\', \'vb_url\', \'\', \'text\', 3)',
  'INSERT INTO hive_setting VALUES (77, 15, \'Allow regular registration\', \'Enable this option if you want to allow users to sign up for a HiveMail&trade; account only, without referring them to vBulletin.\', \'vb_allownormal\', 1, \'yesno\', 4)',
  'INSERT INTO hive_setting VALUES (78, 3, \'Last expiry check\', \'\', \'last_expiry_check\', 1103750831, \'text\', 0)',
  'INSERT INTO hive_setting VALUES (79, 3, \'Current messages subfolder\', \'DO NOT ALTER\', \'flat_curfolder\', \'7a20bdab0509dfdcd7f7e1f556e63e4f\', \'text\', 0)',
  'INSERT INTO hive_setting VALUES (80, 3, \'Current message count in subfolder\', \'DO NOT ALTER\', \'flat_curcount\', 123, \'text\', 0)',
  'INSERT INTO hive_setting VALUES (81, 4, \'Maximum size of processed messages\', \'The maximum accumulated size of all messages that are proccesed per session, in megabytes. Please note that if one message alone is larger than this limit, it will be processed alone after all other messages were cleared (unless it is bigger than maximum allowed size - see below). Set this to 0 to have messages processed in the order of their arrival without any size limits.\', \'pop3_maxsize\', 2, \'text\', 10)',
  'INSERT INTO hive_setting VALUES (82, 1, \'Cookie domain\', \'Domain for which cookies will be set. Leave blank to use automatic setting.<br /><br /><b>Note:</b> All domains must contain at least 2 periods (e.g. example.com should be written as .example.com)\', \'cookiedomain\', \'\', \'text\', 12)',
  'INSERT INTO hive_setting VALUES (83, 1, \'Cookie Path\', \'Path for which cookies will be set.\', \'cookiepath\', \'/\', \'text\', 13)',
  'INSERT INTO hive_setting VALUES (84, 2, \'Maximum online users\', \'Use this setting to control how many users can simultaneously use the system (concurrent users are determined from averages over 15 minute periods), set to 0 to disable this feature.<br /> You can set user groups to allow users to use the system regardless of this limit.\', \'maxonline\', 0, \'text\', 14)',
  'INSERT INTO hive_setting VALUES (85, 6, \'Catch-all user\', \'Messages that are sent to non-existent users will be forwarded to this user (please only enter the username, and not the full email address!). If you would like to bounce such messages back to the sender, please leave this setting empty.\', \'catchalluser\', \'\', \'text\', 3)',
  'INSERT INTO hive_setting VALUES (86, 11, \'Ignore messages by address\', \'If you would like to not process any messages that are sent to certain addresses, write down those addresses here. This can be a list of names only (i.e \\\'nobody\\\') or full email address (i.e \\\'nobody@domain.com\\\'). Wildcards may be used. Separate addresses and names with space or a new line.\', \'ignore_addys\', \'nobody postmaster\', \'area\', 5)',
  'INSERT INTO hive_setting VALUES (90, 3, \'Default user options\', \'Don\\\'t touch!\', \'defuseroptions\', \'a:2:{i:0;d:1953562450;i:1;d:49;}\', \'text\', 0)',
  'INSERT INTO hive_setting VALUES (88, 1, \'Inbox file name\', \'If you change the name of the file index.php, please enter the new name here so HiveMail&trade; will know where it is located. Don\\\'t forget the extension, i.e home.php.\', \'indexname\', \'index.php\', \'text\', 14)',
  'INSERT INTO hive_setting VALUES (91, 11, \'Check for SpamAssassin flags\', \'If <a href=\\"http://www.spamassassin.org/\\" target=\\"_blank\\">SpamAssassin</a> is installed on the server and it is checking incoming messages, turn this on to filter messages that were marked as spam.\', \'checkassassin\', 0, \'yesno\', 6)',
  'INSERT INTO hive_setting VALUES (93, 3, \'Last atomic clock check\', \'\', \'last_atomic_clock_check\', 0, \'text\', 0)',
  'INSERT INTO hive_setting VALUES (94, 3, \'Atomic clock time difference\', \'\', \'atomic_clock_time_diff\', 0, \'text\', 0)',
  'INSERT INTO hive_setting VALUES (95, 7, \'Automatically correct time\', \'HiveMail&trade; will contact this time server to check the accurate time every 24 hours. This will allow the program to automatically correct the time of the server HiveMail&trade; is running on, based on the time from that was received from the time server. Leave this field empty to disable this feature. A full list of available time servers is available <a href=\\"http://www.bldrdoc.gov/timefreq/service/time-servers.html\\" target=\\"_blank\\">here</a>.\', \'time_server\', \'\', \'text\', 4)',
  'INSERT INTO hive_setting VALUES (96, 3, \'Email gateway type\', \'Do not modify!\', \'gatewaytype\', \'pop3\', \'text\', 0)',
  'INSERT INTO hive_setting VALUES (98, 17, \'Format of the mailbox\', \'Choose the format of the mailbox HiveMail&trade; is going to access. If are you not sure which format is the correct one, please contact your host.\', \'fg_format\', \'mbox\', \'mboxselect\', 1)',
  'INSERT INTO hive_setting VALUES (99, 1, \'New-line character\', \'Possible values: \\\\r\\\\n (default), \\\\n.\', \'crlf\', \'\\\\r\\\\n\', \'text\', 15)',
  'INSERT INTO hive_setting VALUES (101, 9, \'Maximum IP Usage\', \'Set this to a whole number to limit the number\\nof times any single IP address can be used for registration. Set it to 0 to disable.\\n(<i>Remember</i>, this may not prevent users from registering multiple times if they are using a service which provides a dynamic IP address!\\nAdditionally, it may prevent innocent users from registering if they happen to use the same ISP as other users.)\', \'reg_maxipusage\', 0, \'text\', 10)',
  'INSERT INTO hive_setting VALUES (102, 2, \'Send Password to Secondary Email\', \'If you set this option to Yes, instead of allowing a user to reset his own password, the password will be sent to the secondary email address he provided on registration. (Therefore, if you do not require a secondary email address, this option will not work.)\', \'sendpwtoalt\', 0, \'yesno\', 5)',
  'INSERT INTO hive_setting VALUES (103, 11, \'Maximum message size\', \'Messages that are larger than this value (in megabytes) will not be processed by HiveMail&trade;, but instead sent back to the sender with a message saying that the email was too large. Set the value to 0 to disable this feature.\', \'maxprocsize\', \'\', \'text\', 1)',
  'INSERT INTO hive_setting VALUES (104, 5, \'Display announcements\', \'Turn this on to display announcements from Mail on the control panel home page.\', \'cp_showannouncements\', 1, \'yesno\', 5)',
  'INSERT INTO hive_setting VALUES (105, 3, \'Last time announcements were updated\', \'Do not touch!\', \'cp_lastannouncementcheck\', 0, \'text\', 0)',
  'INSERT INTO hive_setting VALUES (106, 3, \'Announcements cache\', \'\', \'cp_announcementcache\', \'\', \'text\', 0)',
  'INSERT INTO hive_setting VALUES (107, 7, \'Use 24-hour format\', \'Turn this on to use 24-hour format for calendar events.\', \'cal_use24\', 0, \'yesno\', 5)',
  'INSERT INTO hive_setting VALUES (108, 3, \'Last time subscriptions were audited\', \'Do not modify!\', \'subs_lastcheck\', 0, \'text\', 0)',
  'INSERT INTO hive_setting VALUES (109, 19, \'Enable HivePOP3\', \'Enable the functions and features of the HivePOP3 server.\', \'hivepop_enabled\', 0, \'yesno\', 1)',
  'INSERT INTO hive_setting VALUES (110, 19, \'Server address\', \'The address that users need to connect to in order to read their mail via the POP3 protocl.\', \'hivepop_serveraddr\', \'\', \'text\', 2)',
  'INSERT INTO hive_setting VALUES (111, 19, \'Server port\', \'The port number that should be used to connect to the HivePOP3 server.\', \'hivepop_serverport\', \'\', \'text\', 3)',
  'INSERT INTO hive_setting VALUES (112, 19, \'Allow duplicate storing\', \'Allow users to store incoming messages in their default mailbox as well as in the HivePOP3 account?\', \'hivepop_allowdupe\', 1, \'yesno\', 4)',
  'INSERT INTO hive_setting VALUES (113, 19, \'Path to message files\', \'Enter the absolute path to the folder that contains the message and data files.\', \'hivepop_path\', \'\', \'text\', 5)',
  'INSERT INTO hive_settinggroup VALUES (1, \'General Options\', \'General options that don\\\'t fit into any other groups.\', 1)',
  'INSERT INTO hive_settinggroup VALUES (2, \'User Options\', \'Options that deal with user settings.\', 2)',
  'INSERT INTO hive_settinggroup VALUES (3, \'Internal Variables\', \'Do NOT edit.\', 0)',
  'INSERT INTO hive_settinggroup VALUES (4, \'POP3 Options\', \'pop3\', 5)',
  'INSERT INTO hive_settinggroup VALUES (16, \'Pipe Gateway\', \'pipe\', 0)',
  'INSERT INTO hive_settinggroup VALUES (17, \'File Gateway\', \'file\', 0)',
  'INSERT INTO hive_settinggroup VALUES (18, \'POP3 Gateway\', \'pop3\', 0)',
  'INSERT INTO hive_settinggroup VALUES (5, \'Admin Control Panel\', \'Settings that only effect this control panel.\', 14)',
  'INSERT INTO hive_settinggroup VALUES (6, \'Email Settings\', \'Configuration values for sending email from the software.\', 10)',
  'INSERT INTO hive_settinggroup VALUES (7, \'Time and Date\', \'Set the time zone offset of the server and change display formats.\', 8)',
  'INSERT INTO hive_settinggroup VALUES (8, \'Safe Mode\', \'If PHP is running in Safe Mode you should modify these settings to make sure the program operates fine.\', 11)',
  'INSERT INTO hive_settinggroup VALUES (9, \'Registration\', \'\', 3)',
  'INSERT INTO hive_settinggroup VALUES (10, \'Email Reading\', \'\', 4)',
  'INSERT INTO hive_settinggroup VALUES (11, \'Email Processing\', \'\', 7)',
  'INSERT INTO hive_settinggroup VALUES (12, \'Message Storage\', \'storage\', 0)',
  'INSERT INTO hive_settinggroup VALUES (13, \'GZIP Compression\', \'\', 12)',
  'INSERT INTO hive_settinggroup VALUES (14, \'SMTP Settings\', \'\', 9)',
