INSERT INTO `ibf_skin_templates` VALUES (283, 1, 'skin_msg', '<!-- inbox folder -->\n<script language=\'JavaScript\' type="text/javascript">\n<!--\nvar ie  = document.all  ? 1 : 0;\n//var ns4 = document.layers ? 1 : 0;\nfunction hl(cb)\n{\n   if (ie)\n   {\n	   while (cb.tagName != "TR")\n	   {\n		   cb = cb.parentElement;\n	   }\n   }\n   else\n   {\n	   while (cb.tagName != "TR")\n	   {\n		   cb = cb.parentNode;\n	   }\n   }\n   cb.className = \'hlight\';\n}\nfunction dl(cb) {\n   if (ie)\n   {\n	   while (cb.tagName != "TR")\n	   {\n		   cb = cb.parentElement;\n	   }\n   }\n   else\n   {\n	   while (cb.tagName != "TR")\n	   {\n		   cb = cb.parentNode;\n	   }\n   }\n   cb.className = \'dlight\';\n}\nfunction cca(cb) {\n   if (cb.checked)\n   {\n	   hl(cb);\n   }\n   else\n   {\n	   dl(cb);\n   }\n}\n	   \nfunction CheckAll(cb) {\n	var fmobj = document.mutliact;\n	for (var i=0;i<fmobj.elements.length;i++) {\n		var e = fmobj.elements[i];\n		if ((e.name != \'allbox\') && (e.type==\'checkbox\') && (!e.disabled)) {\n			e.checked = fmobj.allbox.checked;\n			if (fmobj.allbox.checked)\n			{\n			   hl(e);\n			}\n			else\n			{\n			   dl(e);\n			}\n		}\n	}\n}\nfunction CheckCheckAll(cb) {	\n	var fmobj = document.mutliact;\n	var TotalBoxes = 0;\n	var TotalOn = 0;\n	for (var i=0;i<fmobj.elements.length;i++) {\n		var e = fmobj.elements[i];\n		if ((e.name != \'allbox\') && (e.type==\'checkbox\')) {\n			TotalBoxes++;\n			if (e.checked) {\n				TotalOn++;\n			}\n		}\n	}\n	if (TotalBoxes==TotalOn) {fmobj.allbox.checked=true;}\n	else {fmobj.allbox.checked=false;}\n}\nfunction select_read() {	\n	var fmobj = document.mutliact;\n	for (var i=0;i<fmobj.elements.length;i++) {\n		var e = fmobj.elements[i];\n		if ((e.type==\'hidden\') && (e.value == 1) && (! isNaN(e.name) ))\n		{\n			eval("fmobj.msgid_" + e.name + ".checked=true;");\n			hl(e);\n		}\n	}\n}\nfunction unselect_all() {	\n	var fmobj = document.mutliact;\n	for (var i=0;i<fmobj.elements.length;i++) {\n		var e = fmobj.elements[i];\n		if (e.type==\'checkbox\') {\n			e.checked=false;\n			dl(e);\n		}\n	}\n}\n//-->\n</script>\n<div class="pformstrip">$dirname</div>\n<table width="100%" style="padding:6px">\n<tr>\n <td valign="middle">\n  <!-- LEFT -->\n  <table style="width:250px" cellspacing="1" class="tableborder">\n   <tr>\n	<td class=\'row1\' align=\'left\' colspan=\'3\'>{$info[\'full_messenger\']}</td>\n   </tr>\n   <tr>\n	<td align=\'left\' valign=\'middle\' class=\'row2\' colspan=\'3\'><img src=\'{$ibforums->vars[\'img_url\']}/bar_left.gif\' border=\'0\' width=\'4\' height=\'11\' align=\'middle\' alt=\'\' /><img src=\'{$ibforums->vars[\'img_url\']}/bar.gif\' border=\'0\' width=\'{$info[\'img_width\']}\' height=\'11\' align=\'middle\' alt=\'\' /><img src=\'{$ibforums->vars[\'img_url\']}/bar_right.gif\' border=\'0\' width=\'4\' height=\'11\' align=\'middle\' alt=\'\' /></td>\n   </tr>\n   <tr>\n	 <td class=\'row1\' width=\'33%\' align=\'left\' valign=\'middle\'>0%</td>\n	 <td class=\'row1\' width=\'33%\' align=\'center\' valign=\'middle\'>50%</td>\n	 <td class=\'row1\' width=\'33%\' align=\'right\' valign=\'middle\'>100%</td>\n   </tr>\n  </table>\n </td>\n <!-- RIGHT -->\n <td align="right" valign="bottom" style="line-height:100%;">\n  $pages<br /><br />\n  <a href=\'javascript:select_read()\'>{$ibforums->lang[\'pmpc_mark_read\']}</a> :: <a href=\'javascript:unselect_all()\'>{$ibforums->lang[\'pmpc_unmark_all\']}</a><br /><br />\n  <form action="{$ibforums->base_url}CODE=01&amp;act=Msg" name=\'jump\' method="post">\n  <b>{$ibforums->lang[\'goto_folder\']}: </b>&nbsp; $vdi_html <input type=\'submit\' name=\'submit\' value=\'{$ibforums->lang[\'goto_submit\']}\' class=\'forminput\' />\n  </form>\n </td>\n</tr>\n</table>\n<!-- INBOX TABLE -->\n  \n<form action="{$ibforums->base_url}CODE=06&amp;act=Msg" name=\'mutliact\' method="post">\n<div class="tableborder">\n  <table cellpadding=\'4\' cellspacing=\'1\' width="100%">\n  <tr>\n	<th width=\'5%\'  class=\'titlemedium\'>&nbsp;</th>\n	<th width=\'35%\' class=\'titlemedium\'><a href=\'{$ibforums->base_url}act=Msg&amp;CODE=01&amp;VID={$info[\'vid\']}&amp;sort=title&amp;st={$ibforums->input[\'st\']}\'><b>{$ibforums->lang[\'message_title\']}</b></a></th>\n	<th width=\'30%\' class=\'titlemedium\'><a href=\'{$ibforums->base_url}act=Msg&amp;CODE=01&amp;VID={$info[\'vid\']}&amp;sort=name&amp;st={$ibforums->input[\'st\']}\'><b>{$ibforums->lang[\'message_from\']}</b></a></th>\n	<th width=\'25%\' class=\'titlemedium\'><a href=\'{$ibforums->base_url}act=Msg&amp;CODE=01&amp;VID={$info[\'vid\']}&amp;sort={$info[\'date_order\']}&amp;st={$ibforums->input[\'st\']}\'><b>{$ibforums->lang[\'message_date\']}</b></a></th>\n	<th width=\'5%\'  class=\'titlemedium\'><input name="allbox" type="checkbox" value="Check All" onclick="CheckAll();" /></th>\n  </tr>', 'inbox_table_header', '$dirname, $info, $vdi_html="", $pages=""', 1035406235, 0);
