INSERT INTO `ibf_skin_templates` VALUES (446, 1, 'skin_topic', '<script language=\'javascript\' type=\'text/javascript\'>\n    <!--\n    \n    function link_to_post(pid)\n    {\n    	temp = prompt( "{$ibforums->lang[\'tt_prompt\']}", "{$ibforums->base_url}showtopic={$ibforums->input[\'t\']}&view=findpost&p=" + pid );\n    	return false;\n    }\n    \n    function delete_post(theURL) {\n       if (confirm(\'{$ibforums->lang[\'js_del_1\']}\')) {\n          window.location.href=theURL;\n       }\n       else {\n          alert (\'{$ibforums->lang[\'js_del_2\']}\');\n       } \n    }\n    \n    function PopUp(url, name, width,height,center,resize,scroll,posleft,postop) {\n    if (posleft != 0) { x = posleft }\n    if (postop  != 0) { y = postop  }\n    if (!scroll) { scroll = 1 }\n    if (!resize) { resize = 1 }\n    if ((parseInt (navigator.appVersion) >= 4 ) && (center)) {\n      X = (screen.width  - width ) / 2;\n      Y = (screen.height - height) / 2;\n    }\n    if (scroll != 0) { scroll = 1 }\n    var Win = window.open( url, name, \'width=\'+width+\',height=\'+height+\',top=\'+Y+\',left=\'+X+\',resizable=\'+resize+\',scrollbars=\'+scroll+\',location=no,directories=no,status=no,menubar=no,toolbar=no\');\n	}\n	\n	function ShowHide(id1, id2) {\n	  if (id1 != \'\') expMenu(id1);\n	  if (id2 != \'\') expMenu(id2);\n	}\n	\n	function expMenu(id) {\n	  var itm = null;\n	  if (document.getElementById) {\n		itm = document.getElementById(id);\n	  } else if (document.all){\n		itm = document.all[id];\n	  } else if (document.layers){\n		itm = document.layers[id];\n	  }\n	\n	  if (!itm) {\n	   // do nothing\n	  }\n	  else if (itm.style) {\n		if (itm.style.display == "none") { itm.style.display = ""; }\n		else { itm.style.display = "none"; }\n	  }\n	  else { itm.visibility = "show"; }\n	}\n    //-->\n    </script>\n    \n<a name=\'top\'></a>\n<!--IBF.FORUM_RULES-->\n<table width="100%" cellpadding="0" cellspacing="0" border="0">\n<tr>\n <td align=\'left\' width="20%" nowrap="nowrap">{$data[\'TOPIC\'][\'SHOW_PAGES\']}&nbsp;{$data[\'TOPIC\'][\'go_new\']}</td>\n <td align=\'right\' width="80%">{$data[TOPIC][REPLY_BUTTON]}<a href=\'{$ibforums->base_url}act=Post&amp;CODE=00&amp;f={$data[FORUM][\'id\']}\' title=\'{$ibforums->lang[\'start_new_topic\']}\'><{A_POST}></a>{$data[TOPIC][POLL_BUTTON]}</td>\n</tr>\n</table>\n<br />\n<div class="tableborder">\n    <div class=\'maintitle\'><{CAT_IMG}>&nbsp;<b>{$data[\'TOPIC\'][\'title\']}</b>{$data[\'TOPIC\'][\'description\']}</div>\n	<!--{IBF.POLL}-->\n	<div align=\'right\' class=\'postlinksbar\'>\n	  <strong><!--{IBF.START_NEW_POLL}--><a href=\'{$ibforums->base_url}act=Track&amp;f={$data[\'FORUM\'][\'id\']}&amp;t={$data[\'TOPIC\'][\'tid\']}\'>{$ibforums->lang[\'track_topic\']}</a> |\n	  <a href=\'{$ibforums->base_url}act=Forward&amp;f={$data[\'FORUM\'][\'id\']}&amp;t={$data[\'TOPIC\'][\'tid\']}\'>{$ibforums->lang[\'forward\']}</a> |\n	  <a href=\'{$ibforums->base_url}act=Print&amp;client=printer&amp;f={$data[\'FORUM\'][\'id\']}&amp;t={$data[\'TOPIC\'][\'tid\']}\'>{$ibforums->lang[\'print\']}</a></strong>\n	</div>', 'PageTop', '$data', 1035406235, 0);
INSERT INTO `ibf_skin_templates` VALUES (447, 1, 'skin_topic', '<!--Begin Msg Number {$post[\'pid\']}-->\n    <table width=\'100%\' border=\'0\' cellspacing=\'1\' cellpadding=\'3\'>\n    <tr>\n      <td valign=\'middle\' class=\'row4\' width="1%"><a name=\'entry{$post[\'pid\']}\'></a><span class=\'{$post[\'name_css\']}\'>{$author[\'name\']}</span></td>\n        <td class=\'row4\' valign=\'top\' width="99%">\n        \n        <!-- POSTED DATE DIV -->\n        \n        <div align=\'left\' class=\'row4\' style=\'float:left;padding-top:4px;padding-bottom:4px\'>\n        {$post[\'post_icon\']}<span class=\'postdetails\'><b><a title="{$ibforums->lang[\'tt_link\']}" href="#" onclick="link_to_post({$post[\'pid\']}); return false;" style="text-decoration:underline">{$ibforums->lang[\'posted_on\']}</a></b> {$post[\'post_date\']}</span>\n        </div>\n        \n        <!-- REPORT / DELETE / EDIT / QUOTE DIV -->\n        \n        <div align=\'right\'>\n        {$post[\'report_link\']}{$post[\'delete_button\']}{$post[\'edit_button\']}<a href=\'{$ibforums->base_url}act=Post&amp;CODE=06&amp;f={$ibforums->input[f]}&amp;t={$ibforums->input[t]}&amp;p={$post[\'pid\']}\'><{P_QUOTE}></a>\n      </div>\n      \n      </td>\n    </tr>\n    <tr>\n      <td valign=\'top\' class=\'{$post[\'post_css\']}\'>\n        <span class=\'postdetails\'>{$author[\'avatar\']}<br /><br />\n        {$author[\'title\']}<br />\n        {$author[\'member_rank_img\']}<br /><br />\n        {$author[\'member_group\']}<br />\n        {$author[\'member_posts\']}<br />\n        {$author[\'member_number\']}<br />\n        {$author[\'member_joined\']}<br /><br />\n        {$author[\'warn_text\']} {$author[\'warn_minus\']}{$author[\'warn_img\']}{$author[\'warn_add\']}</span><br />\n        <!--$ author[field_1]-->\n        <img src=\'{$ibforums->vars[\'img_url\']}/spacer.gif\' alt=\'\' width=\'160\' height=\'1\' /><br /> \n      </td>\n      <td width=\'100%\' valign=\'top\' class=\'{$post[\'post_css\']}\'>\n        <!-- THE POST {$post[\'pid\']} -->\n        <div class=\'postcolor\'>{$post[\'post\']} {$post[\'attachment\']}</div>\n        {$post[\'signature\']}\n        <!-- THE POST -->\n      </td>\n    </tr>\n    <tr>\n      <td class=\'darkrow3\' align=\'left\'><b>{$post[\'ip_address\']}</b></td>\n      <td class=\'darkrow3\' nowrap="nowrap" align=\'left\'>\n      \n        <!-- PM / EMAIL / WWW / MSGR -->\n      \n        <div align=\'left\' class=\'darkrow3\' style=\'float:left;width:auto\'>\n        {$author[\'addresscard\']}{$author[\'message_icon\']}{$author[\'email_icon\']}{$author[\'website_icon\']}{$author[\'integ_icon\']}{$author[\'icq_icon\']}{$author[\'aol_icon\']}{$author[\'yahoo_icon\']}{$author[\'msn_icon\']}\n        </div>\n        \n        <!-- REPORT / UP -->\n         \n        <div align=\'right\'>\n         <a href=\'javascript:scroll(0,0);\'><img src=\'{$ibforums->vars[\'img_url\']}/p_up.gif\' alt=\'Top\' border=\'0\' /></a>\n        </div>\n      </td>\n    </tr>\n    </table>\n    <div class=\'darkrow1\' style=\'height:5px\'><!-- --></div>', 'RenderRow', '$post, $author', 1035406235, 0);
INSERT INTO `ibf_skin_templates` VALUES (448, 1, 'skin_topic', '<!--IBF.TOPIC_ACTIVE-->\n      <div class="activeuserstrip" align="center">&laquo; <a href=\'{$ibforums->base_url}showtopic={$data[TOPIC][\'tid\']}&amp;view=old\'>{$ibforums->lang[\'t_old\']}</a> &#0124; <strong><a href=\'{$ibforums->base_url}showforum={$data[\'FORUM\'][\'id\']}\'>{$data[\'FORUM\'][\'name\']}</a></strong> &#0124; <a href=\'{$ibforums->base_url}showtopic={$data[TOPIC][\'tid\']}&amp;view=new\'>{$ibforums->lang[\'t_new\']}</a> &raquo;</div>\n</div>\n \n<br />\n<table width="100%" cellpadding="0" cellspacing="0" border="0">\n<tr>\n <td align=\'left\' width="20%" nowrap="nowrap"><!--IBF.TOPIC_OPTIONS_CLOSED-->{$data[TOPIC][SHOW_PAGES]}</td>\n <td align=\'right\' width="80%">{$data[TOPIC][REPLY_BUTTON]}<!--IBF.QUICK_REPLY_CLOSED--><a href=\'{$ibforums->base_url}act=Post&amp;CODE=00&amp;f={$data[FORUM][\'id\']}\' title=\'{$ibforums->lang[\'start_new_topic\']}\'><{A_POST}></a>{$data[TOPIC][POLL_BUTTON]}</td>\n</tr>\n</table>\n<!--IBF.QUICK_REPLY_OPEN-->\n<!--IBF.TOPIC_OPTIONS_OPEN-->\n<br />\n<!--IBF.MOD_PANEL-->\n<div align=\'right\'>{$data[FORUM][\'JUMP\']}</div>\n<!--IBF.MULTIMOD-->\n<br />', 'TableFooter', '$data', 1035406235, 0);
