CREATE TABLE favorites (id smallint(4) NOT NULL auto_increment, userid int(8) NOT NULL default '0', photo int(11) NOT NULL default '0', PRIMARY KEY  (id), KEY parent (photo)) TYPE=MyISAM;
CREATE TABLE laston (cat smallint(4) NOT NULL default '0', userid mediumint(9) NOT NULL default '0', laston int(11) unsigned default NULL, PRIMARY KEY  (cat,userid)) TYPE=MyISAM;
CREATE TABLE useralbums (id smallint(4) NOT NULL auto_increment,albumname varchar(255) NOT NULL default '',parent int(5) NOT NULL default '0',description varchar(255) default NULL,isprivate varchar(4) NOT NULL default 'no',password VARCHAR(16) NOT NULL,PRIMARY KEY (id),KEY parent(parent)) TYPE=MyISAM;
CREATE TABLE categories (id smallint(4) NOT NULL auto_increment,catname varchar(255) NOT NULL default '',description varchar(255) default NULL,catorder tinyint(4) NOT NULL default '0',thumbs varchar(4) NOT NULL default 'yes',parent int(5) NOT NULL default '0',header varchar(200) default NULL,footer varchar(200) default NULL,headtags varchar(200) default NULL,private varchar(5) NOT NULL default 'no',ugnoview VARCHAR(255) NULL,ugnoupload VARCHAR(255) NULL,ugnopost VARCHAR(255) NULL,ugnoanno VARCHAR(255) NULL,PRIMARY KEY (id),KEY parent(parent)) TYPE=MyISAM;
CREATE TABLE comments (id int(11) NOT NULL auto_increment,username tinytext NOT NULL,userid mediumint(9) NOT NULL default '0',date int(15) NOT NULL default '0',rating tinyint(4) default NULL,comment text NOT NULL,photo int(11) NOT NULL default '0',cat smallint(4) NOT NULL default '0',PRIMARY KEY (id),KEY photo(photo),KEY cat(cat)) TYPE=MyISAM;
CREATE TABLE perpage (id int(11) NOT NULL auto_increment,perpage tinyint(3) NOT NULL default '0',PRIMARY KEY (id)) TYPE=MyISAM;
CREATE TABLE photos (id int(10) NOT NULL auto_increment,user varchar(50) NOT NULL default '',userid int(8) NOT NULL default '0',cat int(11) NOT NULL default '0',date int(15) NOT NULL default '0',title varchar(75) NULL default '',description text NULL,keywords varchar(150) default NULL,bigimage varchar(100) NOT NULL default '',width smallint(6) default NULL,height smallint(4) default NULL,filesize mediumint(9) default '0',views int(10) NOT NULL default '0',medwidth int(12) default '0',medheight smallint(6) default '0',medsize mediumint(9) default '0',approved tinyint(2) NOT NULL default '0',lastpost int(15) NOT NULL default '0',rating int(2) NOT NULL default '0',watermarked varchar(5) NOT NULL default 'no',PRIMARY KEY (id),KEY userid(userid),KEY cat(cat),KEY bigimage(bigimage,cat)) TYPE=MyISAM;
CREATE TABLE settings (id int(11) NOT NULL auto_increment,title varchar(255) NOT NULL default '',varname varchar(25) NOT NULL default '',description varchar(150) NOT NULL default '',setting varchar(200) NOT NULL default '',optionorder int(3) NOT NULL default '0',section int(3) NOT NULL default '0', PRIMARY KEY (id)) TYPE=MyISAM;
CREATE TABLE sort (sortid int(15) NOT NULL auto_increment, name varchar(35) NOT NULL default '', code varchar(75) NOT NULL default '', PRIMARY KEY (sortid)) TYPE=MyISAM;
CREATE TABLE admingroups (id int(3) NOT NULL auto_increment,name varchar(35) NOT NULL default '',PRIMARY KEY (id)) TYPE=MyISAM;
CREATE TABLE sortmemb (sortid int(15) NOT NULL auto_increment,name varchar(35) NOT NULL default '',code varchar(75) NOT NULL default '',PRIMARY KEY (sortid)) TYPE=MyISAM;
CREATE TABLE ecards (id int(25) NOT NULL auto_increment,fromname varchar(100) NOT NULL default '',toname varchar(100) NOT NULL default '',subject varchar(150) NOT NULL default '',message text NOT NULL,photoid int(25) NOT NULL default '0',date int(25) NOT NULL default '0',PRIMARY KEY (id)) TYPE=MyISAM;
CREATE TABLE notify (id int(15) NOT NULL auto_increment,userid int(25) NOT NULL default '0',photo int(25) NOT NULL default '0',PRIMARY KEY (id),KEY photo(photo)) TYPE=MyISAM;
CREATE TABLE users (userid int(25) NOT NULL auto_increment,usergroupid int(5) NOT NULL default '0',username varchar(100) NOT NULL default '',password varchar(100) NOT NULL default '',email varchar(150) NOT NULL default '',homepage varchar(250) default NULL,icq varchar(50) default NULL,aim varchar(100) default NULL,yahoo varchar(100) default NULL,joindate int(25) NOT NULL default '0',posts int(20) NOT NULL default '0',birthday date NOT NULL default '0000-00-00',ipaddress varchar(75) default NULL,location varchar(250) default NULL,interests varchar(250) default NULL,occupation varchar(250) default NULL,bio varchar(250) default NULL,site VARCHAR(30) default NULL,offset SMALLINT(2) UNSIGNED DEFAULT '0' NOT NULL,views int(10) NOT NULL default '0',PRIMARY KEY (userid)) TYPE=MyISAM;
CREATE TABLE ipcache (id int(10) NOT NULL auto_increment,userid INT(25) NOT NULL, ipaddr VARCHAR(75) NOT NULL, date INT(11) UNSIGNED, type VARCHAR(4) NOT NULL, photo INT(10) NOT NULL, PRIMARY KEY (id)) TYPE=MyISAM;