INSERT INTO celeste_template VALUES (26, 'post_form', '{header}\r\n<script language="javascript">\r\nvar text = "";\r\n\r\nfunction getActiveText(selectedtext) { \r\n  text = (document.all) ? document.selection.createRange().text : document.getSelection();\r\n  if (selectedtext.createTextRange) {	\r\n    selectedtext.caretPos = document.selection.createRange().duplicate();	\r\n  }\r\n	return true;\r\n}\r\n\r\n</script>\r\n{post_adv_script}\r\n<TABLE border=0 cellPadding=0 cellSpacing=0 width="{_width}" align=center bgColor={_borderc}>\r\n<form method=post name=FORM action=""  target="" enctype="multipart/form-data" >\r\n  <TR>\r\n    <TD>\r\n      <TABLE border=0 cellPadding=4 cellSpacing=1 width="100%">\r\n        <TR bgcolor="{_inc}"> \r\n          <TD colSpan=2 height="20">\r\n            <b>{pagetitle}</b> [ <a href="index.php?{url}&prog={thisprog}&fid={forumid}&postMode={opp_mode}">{opp_mode_name}</a> ]\r\n          </TD>\r\n        </TR>\r\n        <TR bgColor={_inc}>\r\n          <TD width="15%"><b>Topic</b></TD>\r\n          <TD> \r\n            <INPUT class=subject maxLength=60 name=title size=60 value="{title}">\r\n          </TD>\r\n        </TR>\r\n{icon_list}\r\n{poll_form}\r\n<TR bgColor={_inc}> \r\n          <TD vAlign=top><B>Content</B><br /><br /><br />\r\n<b>Forum Rules:</b><br /><br />\r\n<font class=bartext>\r\nCeleste Tag - <b>{cetaginfo}</b><br />\r\nImage   Tag - <b>{imageinfo}</b><br />\r\nHTML Code - <b>{htmlinfo}</b><br />\r\nSmile    Tag - <b>{smilesinfo}</b><br />\r\n</font>\r\n</TD>\r\n          <TD>\r\n{post_adv_font}\r\n{post_adv_toolsbar}\r\n<TEXTAREA cols=80 name=content rows=12 wrap=VIRTUAL onfocus=getActiveText(this) onclick=getActiveText(this)  onchange=getActiveText(this)>{content}</TEXTAREA>\r\n<br />\r\n[<a href="javascript:openScript(\'help/showsmiles.html\',500,400)">Show all smiles</a>]<br />{post_adv_showsmiles}</TD>\r\n        </TR>\r\n\r\n        <TR bgColor={_inc}> \r\n          <TD><B>Required User Rating to read content</B></TD>\r\n          <TD><input type=text name=requirerating value=\'{requirerating}\'>\r\n     </TR>\r\n{upload_form}\r\n        <TR bgColor={_inc}> \r\n          <TD vAlign=top><B>Options:</B></TD>\r\n          <TD>\r\n            <INPUT name=emailnotice type=checkbox  value="checked" {emailnoticecheck}> Email Notice: Inform me when got replies<BR>\r\n            <INPUT name=cetag type=checkbox  value="checked" {cetagcheck}> Enable Celeste Tags<BR>\r\n            <INPUT name=smiles type=checkbox  value="checked" {smilescheck}> Enable Smile Tags<BR>\r\n            <INPUT name=showsign type=checkbox value="checked" {showsigncheck}> Show my signature in post<BR>\r\n            <INPUT name=autoParseURL type=checkbox  value="checked" {autoparseurlcheck}> Enable auto URL parse<BR>\r\n            <INPUT name=autoParseIMG type=checkbox  value="checked" {autoparseimgcheck}> Enable auto Image parse<BR>\r\n          </TD>\r\n        </TR>\r\n      </TABLE>\r\n    </TD>\r\n  </TR>\r\n</TABLE>\r\n<br>\r\n<table width="{_width}" border="0" cellspacing="0" cellpadding="0" align=center>\r\n  <tr>\r\n    <td bgcolor="{_borderc}">\r\n      <table width="100%" border="0" cellspacing="1" cellpadding="0">\r\n        <tr> \r\n          <td bgcolor="{_inc}" height="20"> <b>{pagetitle}</b></td>\r\n        </tr>\r\n        <tr> \r\n          <td height="14" bgcolor="{_inc}">\r\n            <div align="center"><br>\r\n              <br>\r\n<script>\r\ncnt = 0;\r\nfunction checkCnt() {\r\n  cnt++;\r\n  if (cnt==1) return true;\r\n  alert(\'Submission Processing. Please Wait\');\r\n  return false;\r\n}\r\n\r\nfunction preview(a){\r\n var w1=window.open(\'\',\'preview\',\'width=500,height=450,top=0,left=0,scrollbars=yes\');\r\n a.target=\'preview\';\r\n a.action=\'index.php?prog=topic::preview&fid={forumid}\';\r\n a.submit();\r\n a.target=\'\';\r\n a.action=\'\';\r\n}\r\n</script>\r\n\r\n<INPUT type="submit" value=" Submit " name="Submit"  onclick="return checkCnt();" accesskey="s"> \r\n<input type="button" name="Submit" value="Preview" onClick="javascript:preview(document.FORM)">\r\n\r\n    <input type=hidden name=parentid value={parentid}>\r\n               <br>\r\n               <br>\r\n            </div>\r\n          </td>\r\n        </tr>\r\n      </table>\r\n    </td>\r\n  </tr>\r\n</form>\r\n</table>\r\n<br>\r\n{post_review}\r\n{footer}')
INSERT INTO celeste_template VALUES (27, 'success', '<html>\r\n<head>\r\n<title>Please stand by... </title>\r\n<meta http-equiv="refresh" content="{time}; url={url}">\r\n<link type="text/css" href="images/style.css" rel="stylesheet">\r\n</head>\r\n<body bgcolor=\'{_pagebgc}\'>\r\n<table cellpadding=\'0\' cellspacing=\'0\' border=\'0\' width="97%" align=\'center\' height=\'85%\'>\r\n  <tr align=\'center\' valign=\'middle\'>\r\n    <td>\r\n    <table cellpadding=\'10\' cellspacing=\'0\' border=\'0\' width="70%" align=\'center\'>\r\n    <tr>\r\n       <td valign=\'middle\' align=\'center\' bgcolor=\'{_inc}\'>\r\n        {msg}\r\n        <br><br>\r\n        Please wait while we transfer you...\r\n        <br><br>\r\n        (<a href=\'{url}\'>Or click here if you do not wish to wait</a>)\r\n       </td>\r\n    </tr>\r\n    </table>\r\n    </td>\r\n  </tr>\r\n</table>\r\n</body>\r\n</html>\r\n')
