INSERT INTO celeste_template VALUES (29, 'post_adv_script', '<script>\r\nfunction AddText(NewCode) {\r\n	if (document.FORM.content.createTextRange && document.FORM.content.caretPos) {\r\n		var caretPos = document.FORM.content.caretPos;\r\n		caretPos.text = caretPos.text.charAt(caretPos.text.length - 1) == \' \' ? NewCode + \' \' : NewCode;\r\n	} \r\n  else {\r\n		document.FORM.content.value+=NewCode\r\n	}\r\n	setfocus();\r\n}\r\n\r\nfunction setfocus() {\r\n  document.FORM.content.focus();\r\n}\r\n\r\nfunction email() {\r\n	AddTxt="[email]"+text+"[/email]";\r\n	AddText(AddTxt);\r\n}\r\n\r\nfunction bold() {\r\n	AddTxt="[b]"+text+"[/b]";\r\n	AddText(AddTxt);	\r\n}\r\n\r\nfunction italicize() {\r\n	AddTxt="[i]"+text+"[/i]";\r\n	AddText(AddTxt);\r\n}\r\n\r\nfunction quoteme() {\r\n	AddTxt="[quote]"+text+"[/quote]";\r\n	AddText(AddTxt);\r\n}\r\n\r\nfunction showcolor(color) {\r\n	AddTxt="[color="+color+"]"+text+"[/color]";\r\n	AddText(AddTxt);\r\n	document.FORM.color.value = "";\r\n}\r\nfunction showfont(font) {\r\n	AddTxt="[font="+font+"]"+text+"[/font]";\r\n	AddText(AddTxt);\r\n	document.FORM.color.value = "";\r\n}\r\nfunction showsize(size) {\r\n	AddTxt="[size="+size+"]"+text+"[/size]";\r\n	AddText(AddTxt);\r\n	document.FORM.color.value = "";\r\n}\r\n\r\nfunction center() {\r\n	AddTxt="[center]"+text+"[/center]";\r\n	AddText(AddTxt);\r\n}\r\n\r\nfunction hyperlink() {\r\n	AddTxt="[url]"+text+"[/url]";\r\n	AddText(AddTxt);\r\n}\r\n\r\nfunction image() {\r\n	AddTxt="[img]"+text+"[/img]";\r\n	AddText(AddTxt);\r\n}\r\n\r\nfunction showcode() {\r\n	AddTxt="[code]"+text+"[/code]";\r\n	AddText(AddTxt);\r\n}\r\n\r\nfunction underline() {\r\n	AddTxt="[u]"+text+"[/u]";\r\n	AddText(AddTxt);\r\n}\r\n\r\nfunction setswf() {\r\n	AddTxt="[flash]"+text+"[/flash]";\r\n	AddText(AddTxt);\r\n}\r\n\r\nfunction addsmile(str) {\r\n  document.FORM.content.value += \' \'+str+\' \'; \r\n}\r\n\r\nfunction list() {\r\n	AddTxt=text + "[list]\\r\\n[*]\\r\\n[*]\\r\\n[*]\\r\\n[/list]";\r\n	AddText(AddTxt); \r\n}\r\n\r\n</script>\0')
INSERT INTO celeste_template VALUES (30, 'post_adv_toolsbar', '<img alt=Bold onClick=bold() src="images/adv_tools/bold.gif">\r\n<img alt=italicize onClick=italicize() src="images/adv_tools/italicize.gif">\r\n<img alt=underline onClick=underline() src="images/adv_tools/underline.gif">\r\n<img alt=center onClick=center() src="images/adv_tools/center.gif" >\r\n<img alt=hyperlink  onClick=hyperlink() src="images/adv_tools/url.gif" >\r\n<img alt=email  onClick=email() src="images/adv_tools/email.gif" >\r\n<img alt=image onClick=image() src="images/adv_tools/image.gif" >\r\n<img alt=Flash onClick=setswf() src="images/adv_tools/swf.gif">\r\n<img alt=Code onClick=showcode() src="images/adv_tools/code.gif">\r\n<img alt=Quote onClick=quoteme() src="images/adv_tools/quote.gif">\r\n<img alt=List onClick=list() src="images/adv_tools/list.gif"><br>\0')
