INSERT INTO celeste_template VALUES (56, 'user_cp_menu', '<table cellpadding=\'0\' cellspacing=\'1\' border=\'0\' width=\'{_width}\' bgcolor=\'{_borderc}\' align=\'center\'>\r\n<tr>\r\n<td>\r\n<table cellspacing=\'0\' cellpadding=\'4\' border=\'0\' align=\'center\' width=\'100%\'>\r\n<tr height=16\'>\r\n<td bgcolor=\'{_caterowc}\' valign=\'middle\' align=\'center\' width=\'16%\' height=\'16\'><a href=\'index.php?prog=ucp\' class=caterow>Summary</a></td>\r\n<td bgcolor=\'{_caterowc}\' valign=\'middle\' align=\'center\' width=\'16%\' height=\'16\'><a href=\'index.php?prog=ucp::profile\' class=caterow>Profile</a></td>\r\n<td bgcolor=\'{_caterowc}\' valign=\'middle\' align=\'center\' width=\'16%\' height=\'16\'><a href=\'index.php?prog=ucp::favorites\' class=caterow>My Favorites</a></td>\r\n<td bgcolor=\'{_caterowc}\' valign=\'middle\' align=\'center\' width=\'16%\' height=\'16\'><a href=\'index.php?prog=ucp::preference\' class=caterow>Preference</a></td>\r\n<td bgcolor=\'{_caterowc}\' valign=\'middle\' align=\'center\' width=\'16%\' height=\'16\'><a href=\'index.php?prog=ucp::pm\' class=caterow>P.M. Box</a></td>\r\n<td bgcolor=\'{_caterowc}\' valign=\'middle\' align=\'center\' width=\'16%\' height=\'16\'><a href=\'index.php?prog=ucp::notepad\' class=caterow>Note Pad</a></td>\r\n</tr>\r\n</table>\r\n</td>\r\n</tr>\r\n</table>\0')
INSERT INTO celeste_template VALUES (57, 'user_cp', '{header}\r\n<br>\r\n{user_cp_menu}\r\n<table cellpadding=\'0\' cellspacing=\'1\' border=\'0\' width=\'{_width}\' bgcolor=\'{_borderc}\' align=\'center\'>\r\n<tr>\r\n<td>\r\n<table cellspacing=\'0\' cellpadding=\'4\' border=\'0\' align=\'center\' width=\'100%\'>\r\n<tr>\r\n<td bgcolor=\'{_topicrowc}\' colspan=\'2\' class=topicrow>Welcome to your control panel</td>\r\n</tr>\r\n<tr>\r\n<td bgcolor=\'{_inc}\' colspan=\'2\' class=smalltext><b>{username}</b>, this is where you\'ll be able to control all of your personal and board options to make your visit to our forums more enjoyable. Please select an option from one of the menu tabs above. If this is your first visit you may wish to look at the Control Panel overview below.<br><br></td>\r\n</tr>\r\n<tr>\r\n<td bgcolor=\'{_caterowc}\' colspan=\'2\' class=topicrow>Here is a brief summary of your user account</td>\r\n</tr>\r\n<tr>\r\n<td bgcolor=\'{_inc}\' nowrap>Your registered email address</td>\r\n<td bgcolor=\'{_inc}\' width=80%>{email}</td>\r\n</tr>\r\n<tr>\r\n<td bgcolor=\'{_inc}\' nowrap>Total number of posts to date</td>\r\n<td bgcolor=\'{_inc}\' width=80% >{posts}</td>\r\n</tr>\r\n<tr>\r\n<td bgcolor=\'{_inc}\' nowrap>You registered on</td>\r\n<td bgcolor=\'{_inc}\' width=80% >{joindate}</td>\r\n</tr>\r\n<tr>\r\n<td bgcolor=\'{_inc}\' nowrap>Average number of posts per day</td>\r\n<td bgcolor=\'{_inc}\' width=80% >{avgpost}</td>\r\n</tr>\r\n<tr>\r\n<td bgcolor=\'{_inc}\' nowrap>Your total user rating gained</td>\r\n<td bgcolor=\'{_inc}\' width=80% >{rating}</td>\r\n</tr>\r\n<tr>\r\n<td bgcolor=\'{_caterowc}\' colspan=\'2\' class=topicrow>An overview of your personal Control Panel</td>\r\n</tr>\r\n<tr>\r\n<td bgcolor=\'{_inc}\' colspan=\'2\'><b><a href="index.php?prog=ucp::profile">Profile</a> :</b> This is where you can amend your personal information including website / ICQ / avatars / email / password etc.<br><br>\r\n<b><a href="index.php?prog=ucp::favourites">My Favorites</a> :</b> This is where you can view all favorte topics collected before.<br><br>\r\n<b><a href="index.php?prog=ucp::preference">Preference</a> :</b> This is where you can change personal preferences to find an easy way  use our system. <br><br>\r\n<b><a href="index.php?prog=ucp::pm">P.M. Box</a> :</b> This is where you can send / read / reply your private messages.<br><br>\r\n<b><a href="index.php?prog=ucp::notepad">Note Pad</a> :</b> This is your simple note pad in this forum. jot down anything you like.\r\n</td>\r\n</tr>\r\n\r\n</table></td></tr></table>\r\n{footer}\0')
INSERT INTO celeste_template VALUES (67, 'user_cp_preference', '{header}\r\n<br>\r\n<form method=post name=FORM enctype="multipart/form-data" >\r\n{user_cp_menu}\r\n<table cellpadding=\'0\' cellspacing=\'1\' border=\'0\' width=\'{_width}\' bgcolor=\'{_borderc}\' align=\'center\'>\r\n<tr>\r\n<td>\r\n<table cellspacing=\'0\' cellpadding=\'4\' border=\'0\' align=\'center\' width=\'100%\'>\r\n<tr>\r\n<td bgcolor=\'{_topicrowc}\' colspan=\'2\' class=topicrow>Board Preference</td>\r\n</tr>\r\n\r\n<tr>\r\n<td bgcolor=\'{_caterowc}\' colspan=\'2\' class=topicrow>Advanced Preference List</td>\r\n</tr>\r\n\r\n<tr>\r\n<td bgcolor=\'{_inc}\'>\r\n<table width=75% cellspacing=2 cellpadding=6 align=center>\r\n\r\n<tr>\r\n<td bgcolor=\'{_inc}\' nowrap>My prefer way of reading threads : </td>\r\n<td bgcolor=\'{_inc}\' width=70%><select name=readmode ><option value=0>Default</option><option value=1 {flatreadmode}>Flat</option><option value=2 {threadedreadmode}>Threaded</option></select></td>\r\n</tr>\r\n\r\n<tr>\r\n<td bgcolor=\'{_inc}\' nowrap>My prefer way of making post :</td>\r\n<td bgcolor=\'{_inc}\' width=70%><select name=postmode ><option value=0>Simple</option><option value=1 {postmode}>Advanced</option></select></td>\r\n</tr>\r\n\r\n<tr>\r\n<td bgcolor=\'{_inc}\' nowrap>My email address is public : </td>\r\n<td bgcolor=\'{_inc}\' width=70%><select name=publicemail ><option value=0>NO</option><option value=1 {publicemail}>YES</option></select></td>\r\n</tr>\r\n\r\n<tr>\r\n<td bgcolor=\'{_inc}\' nowrap>Pop up a new window when I get new P.M. :</td>\r\n<td bgcolor=\'{_inc}\' width=70%><select name=pmpopup ><option value=0>NO</option><option value=1 {pmpopup}>YES</option></select></td>\r\n</tr>\r\n\r\n<tr>\r\n<td bgcolor=\'{_inc}\' nowrap>Show other people\'s signatures when I am reading :</td>\r\n<td bgcolor=\'{_inc}\' width=70%><select name=showothersign ><option value=0>NO</option><option value=1 {showothersign}>YES</option></select></td>\r\n</tr>\r\n\r\n<tr>\r\n<td bgcolor=\'{_inc}\' nowrap>Show my signature after my post defaultly :</td>\r\n<td bgcolor=\'{_inc}\' width=70%><select name=showsign ><option value=0>NO</option><option value=1 {showsign}>YES</option></select></td>\r\n</tr>\r\n\r\n<tr>\r\n<td bgcolor=\'{_inc}\' nowrap>Enable Celeste Tag in my post defaultly :</td>\r\n<td bgcolor=\'{_inc}\' width=70%><select name=cetag ><option value=0>NO</option><option value=1 {cetag}>YES</option></select></td>\r\n</tr>\r\n\r\n<tr>\r\n<td bgcolor=\'{_inc}\' nowrap>Enable Smiles Tag in my post defaultly :</td>\r\n<td bgcolor=\'{_inc}\' width=70%><select name=smiles ><option value=0>NO</option><option value=1 {smiles}>YES</option></select></td>\r\n</tr>\r\n\r\n\r\n<tr>\r\n<td bgcolor=\'{_inc}\' nowrap>Inform me by email defaultly when my post gets reply  :</td>\r\n<td bgcolor=\'{_inc}\' width=70%><select name=emailnotice ><option value=0>NO</option><option value=1 {emailnotice}>YES</option></select></td>\r\n</tr>\r\n\r\n<tr>\r\n<td bgcolor=\'{_inc}\' nowrap>Let me review old post when i am posting :</td>\r\n<td bgcolor=\'{_inc}\' width=70%><select name=showpostonreply ><option value=0>NO</option><option value=1 {showpostonreply}>YES</option></select></td>\r\n</tr>\r\n\r\n<tr>\r\n<td bgcolor=\'{_inc}\' nowrap>Automatically parse URLs in my post into Celeste Tag :</td>\r\n<td bgcolor=\'{_inc}\' width=70%><select name=parseurl ><option value=0>NO</option><option value=1 {parseurl}>YES</option></select></td>\r\n</tr>\r\n\r\n<tr>\r\n<td bgcolor=\'{_inc}\' nowrap>Automatically parse images in my post into Celeste Tag :</td>\r\n<td bgcolor=\'{_inc}\' width=70%><select name=parseimg ><option value=0>NO</option><option value=1 {parseimg}>YES</option></select></td>\r\n</tr>\r\n</table>\r\n\r\n</td></tr>\r\n\r\n</table></td></tr></table>\r\n<br><br>\r\n<table cellpadding=\'0\' cellspacing=\'1\' border=\'0\' width=\'{_width}\' bgcolor=\'{_borderc}\' align=\'center\'>\r\n<tr>\r\n<td>\r\n<table cellspacing=\'0\' cellpadding=\'4\' border=\'0\' align=\'center\' width=\'100%\'>\r\n<tr>\r\n<td bgcolor=\'{_topicrowc}\' class=topicrow>Save Preference</td>\r\n</tr>\r\n<tr>\r\n<td bgcolor=\'{_inc}\'>\r\n            <div align="center"><br>\r\n              <br>\r\n<script>\r\ncnt = 0;\r\nfunction checkCnt() {\r\n  cnt++;\r\n  if (cnt==1) return true;\r\n  alert(\'Submission  Processing. Please Wait\');\r\n  return false;\r\n}\r\n</script>\r\n              <input type="submit" name="submit" value=" Save Change " onclick="return checkCnt();" accesskey="s">\r\n              <input type="hidden" name="step" value=2>\r\n               <br>\r\n               <br>\r\n            </div>\r\n\r\n</td>\r\n</tr>\r\n</table>\r\n</td></tr></table>\r\n</form>\r\n{footer}\0')
INSERT INTO celeste_template VALUES (58, 'user_cp_profile', '{header}\r\n<br>\r\n{user_cp_menu}\r\n<table cellpadding=\'0\' cellspacing=\'1\' border=\'0\' width=\'{_width}\' bgcolor=\'{_borderc}\' align=\'center\'>\r\n<tr>\r\n<td>\r\n<table cellspacing=\'0\' cellpadding=\'4\' border=\'0\' align=\'center\' width=\'100%\'>\r\n<tr>\r\n<td bgcolor=\'{_topicrowc}\' colspan=\'2\' class=topicrow>Your Profile options</td>\r\n</tr>\r\n<tr>\r\n<td bgcolor=\'{_inc}\' colspan=\'2\' class=smalltext><b>{username}</b>, this is where you can modify your personal information to allow others to find out a little more about you. This is also where you can set up your avatar. An avatar is a little image that is displayed under your name when you make a post.<br><br></td>\r\n</tr>\r\n<tr>\r\n<td bgcolor=\'{_caterowc}\' colspan=\'2\'><a href=\'index.php?prog=ucp::editprofile\' class=caterow>&#187; Edit your profile</a></td>\r\n</tr>\r\n<tr>\r\n<td bgcolor=\'{_inc}\' colspan=\'2\'><br>This is where you can edit or create your personal profile. It\'ll allow you to create a signature to leave under each message you post. It\'ll also allow you to enter any contact information, such as your website address, ICQ number, etc.<br><br></td>\r\n</tr>\r\n\r\n<tr>\r\n<td bgcolor=\'{_caterowc}\' colspan=\'2\'><a href=\'index.php?prog=ucp::avatar\' class=caterow>&#187; Avatar Options</a></td>\r\n</tr>\r\n\r\n<tr>\r\n<td bgcolor=\'{_inc}\' colspan=\'2\'><br>This is where you can choose your avatar to use when making a post. This is completely optional, but many members use them as a fun way to express themselves.<br><br></td>\r\n</tr>\r\n\r\n<tr>\r\n<td bgcolor=\'{_caterowc}\' colspan=\'2\'><a href=\'index.php?prog=ucp::account\' class=caterow>&#187; Account Options</a></td>\r\n</tr>\r\n\r\n<tr>\r\n<td bgcolor=\'{_inc}\' colspan=\'2\'><br>You can change your essential registration data here, like Email / password.<br><br></td>\r\n</tr>\r\n</table></td></tr></table>\r\n{footer}\0')
