INSERT INTO celeste_template VALUES (72, 'user_cp_favorites', '{header}\r\n<script language="JavaScript">\r\nfunction Checkall(form)\r\n{\r\nvar i;\r\nfor ( i = 0; i < form.elements.length; i++) form.elements[i].checked = true;\r\n}\r\nfunction UnCheckall(form)\r\n{\r\nvar i;\r\nfor ( i = 0; i < form.elements.length; i++) form.elements[i].checked = false;\r\n}\r\n</script><br>\r\n<form method=post name=FORM>\r\n{user_cp_menu}\r\n<TABLE border=0 cellPadding=0 cellSpacing=0 width="{_width}" align=center>\r\n  <TR>\r\n    <TD bgColor={_borderc}>\r\n      <TABLE border=0 cellPadding=4 cellSpacing=1 width="100%">\r\n        <TR bgColor={_topicrowc}> \r\n          <TD>&nbsp;</td>\r\n          <TD align=center colspan=2 class=topicrow>Topic ( Click Icon to read in new window )</TD>\r\n          <TD align=center class=topicrow> Forum </TD>\r\n          <TD align=center noWrap class=topicrow>Author</TD>\r\n          <TD align=center noWrap class=topicrow>Hits</td>\r\n          <TD align=center noWrap class=topicrow>Posts</TD>\r\n          <TD align=center noWrap class=topicrow>Del</TD>\r\n        </TR>\r\n{topiclist}\r\n</TABLE>\r\n</TABLE>\r\n<br>\r\n<table border=0 cellpadding=0 cellspacing=0 width="{_width}" align=center>\r\n<tr>\r\n<td>&nbsp; [ <a href="javascript:Checkall(this.FORM)"><b>Delete All</b><a> | <a href="javascript:UnCheckall(this.FORM)"><b>Reset</b></a> ]&nbsp; &nbsp;</td>\r\n<td align=right><input type=submit value=\'Delete\'><input type=hidden name=action value=del></td>\r\n</tr>\r\n</table>\r\n\r\n<br><br>\r\n</form>\r\n{footer}\0')
