INSERT INTO celeste_template VALUES (80, 'topic_search', '{header}\r\n<FORM method=post>\r\n\r\n<table cellpadding=\'0\' cellspacing=\'1\' border=\'0\' width=\'{_width}\' bgcolor=\'{_borderc}\' align=\'center\'>\r\n<tr>\r\n<td>\r\n<table cellspacing=\'0\' cellpadding=\'4\' border=\'0\' align=\'center\' width=\'100%\'>\r\n<tr>\r\n<td bgcolor=\'{_topicrowc}\' colspan=\'2\' class=topicrow>Search Forum</td>\r\n        <TR bgColor={_inc}>\r\n          <TD align=right width="45%"><B>Keywords</B>&nbsp;&nbsp;&nbsp;&nbsp;</TD>\r\n          <TD><INPUT class=subject maxLength=40 name=keyword size=40><br><font class=smalltext>Separate your search terms with spaces.</font></TD>\r\n        </TR>\r\n        <TR bgColor={_inc}>\r\n          <TD align=right width="45%"><B>Search Option</B>&nbsp;&nbsp;&nbsp;&nbsp;</TD>\r\n          <TD><select name=searchoption><option value=0>whole string</option><option value=1>and</option><option value=2>or</option></select></font></TD>\r\n        </TR>\r\n        <TR bgColor={_inc}>\r\n          <TD align=right width="45%"><B>Author</B>&nbsp;&nbsp;&nbsp;&nbsp;</TD>\r\n          <TD><INPUT class=subject maxLength=15 name=username size=15>&nbsp;<input type=checkbox name=exactname checked>Match exact name?</TD>\r\n        </TR>\r\n        <TR bgColor={_inc}>\r\n          <TD align=right width="45%"><B>Search In</B>&nbsp;&nbsp;&nbsp;&nbsp;</TD>\r\n          <TD><select name=target><option value=0 > Topic Only </option><option value=1 > Post Title </option><option value=2 > Post Content </option></select>\r\n          </TD>\r\n        </TR>\r\n        <TR bgColor={_inc}>\r\n          <TD align=right width="45%"><B>Search forum</B>&nbsp;&nbsp;&nbsp;&nbsp;</TD>\r\n          <TD>\r\n<select name=forumid style=\'width=230\'>{forumlist}</select></TD>\r\n        </TR>\r\n        <TR bgColor={_inc}>\r\n          <TD align=right width="45%"><B>Posting date</B>&nbsp;&nbsp;&nbsp;&nbsp;</TD>\r\n          <TD>\r\n          <SELECT  name=timeline>\r\n            <OPTION value=0>any date</OPTION>\r\n            <OPTION value=1>yesterday</OPTION>\r\n            <OPTION value=7>a week ago</OPTION>\r\n            <OPTION value=14>2 weeks ago</OPTION>\r\n            <OPTION value=30>a month ago</OPTION>\r\n            <OPTION value=60>2 months ago</OPTION>\r\n            <OPTION value=90>3 months ago</OPTION>\r\n            <OPTION value=180>6 months ago</OPTION>\r\n            <OPTION value=365>a year ago</OPTION>\r\n          </SELECT>&nbsp;\r\n          <select  name=neworold>\r\n            <option value=0>and newer</option>\r\n            <option value=1>and older</option>\r\n          </select>\r\n          </TD>\r\n        </TR>\r\n        <TR bgColor={_inc}>\r\n          <TD align=right width="45%"><B>Elite Topic?</B>&nbsp;&nbsp;&nbsp;&nbsp;</TD>\r\n          <TD><INPUT name=iselite type=checkbox value=1></TD>\r\n        </TR>\r\n        <TR bgColor={_inc}>\r\n          <TD align=right width="45%"><B>Sort Results By.</B>&nbsp;&nbsp;&nbsp;&nbsp;</TD>\r\n          <TD>\r\n          <SELECT  name=orderby>\r\n            <OPTION value=1>posting date</OPTION>\r\n            <OPTION value=2>number of views</OPTION>\r\n            <OPTION value=3>number of replies</OPTION>\r\n            <OPTION value=4>author name</OPTION>\r\n          </SELECT> &nbsp;  In\r\n          <select  name=order>\r\n            <option value=desc>descending order</option>\r\n            <option value=asc>ascending order</option>\r\n          </select>\r\n\r\n          </TD>\r\n        </TR>\r\n      </TABLE>\r\n    </TD>\r\n  </TR>\r\n</TABLE>\r\n<br><br>\r\n<table cellpadding=\'0\' cellspacing=\'1\' border=\'0\' width=\'{_width}\' bgcolor=\'{_borderc}\' align=\'center\'>\r\n<tr>\r\n<td>\r\n<table cellspacing=\'0\' cellpadding=\'4\' border=\'0\' align=\'center\' width=\'100%\'>\r\n<tr>\r\n<td bgcolor=\'{_topicrowc}\' class=topicrow>Perform Search</td>\r\n</tr>\r\n<tr>\r\n<td bgcolor=\'{_inc}\'>\r\n            <div align="center"><br>\r\n              <br>\r\n<script>\r\ncnt = 0;\r\nfunction checkCnt() {\r\n  cnt++;\r\n  if (cnt==1) return true;\r\n  alert(\'Submission  Processing. Please Wait\');\r\n  return false;\r\n}\r\n</script>\r\n              <input type="submit" name="submit" value=" Search " onclick="return checkCnt();" accesskey="s">&nbsp; &nbsp;\r\n              <input type="reset"  value=" Reset ">\r\n               <br>\r\n               <br>\r\n            </div>\r\n\r\n</td>\r\n</tr>\r\n</table>\r\n</td></tr></table>\r\n</form>\r\n\r\n{footer}\0')
