INSERT INTO celeste_template VALUES (162, 'contact_list', '<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.0 Transitional//EN">\r\n<HTML><HEAD><TITLE>{pagetitle} - {_title}</TITLE>\r\n<meta http-equiv="Content-Type" content="text/html; charset={_charset}">\r\n<meta name="GENERATOR" content="Celeste 1.0">\r\n<LINK href="images/style.css" rel=stylesheet type=text/css>\r\n</HEAD>\r\n\r\n<script>\r\n// buddy, auto choose\r\n// y10k, 2003,1,17\r\nfunction addBuddy(username) {\r\n  if(opener.document.FORM) {\r\n    // parent exists\r\n    if(opener.document.FORM.reciever.value.length == 0) {\r\n      opener.document.FORM.reciever.value = username;\r\n    } else {\r\n      opener.document.FORM.reciever.value += \',\' + username;\r\n    }\r\n  } else {\r\n    window.open(\'index.php?prog=ucp::pm::edit&username=\'+username);\r\n    window.close();\r\n  }\r\n}\r\n\r\nfunction Checkall(form)\r\n{\r\n  var i;\r\n  for ( i = 0; i < form.elements.length; i++) form.elements[i].checked = true;\r\n}\r\nfunction UnCheckall(form)\r\n{\r\n  var i;\r\n  for ( i = 0; i < form.elements.length; i++) form.elements[i].checked = false;\r\n}\r\n</script>\r\n\r\n<body bgcolor="{_pagebgc}" leftmargin="0" topmargin="0" marginwidth="0" marginheight="0">\r\n\r\n<table width=95% cellpadding=0 cellspacing=1 border=0 align=center bgcolor={_borderc}><tr>\r\n<form method=post name=\'FORM\'><td>\r\n<table width=100% cellpadding=5 cellspacing=0 border=0><tr><td bgcolor={_topicrowc} align=center colspan=2  class=topicrow>\r\n<a href="index.php" class=topicrow>{_title}</a> -> {pagetitle}\r\n</td></tr>\r\n<tr><td bgcolor={_caterowc} align=center><a href=\'index.php?prog=ucp::pm::clist&type={type}&sort=username\' title=\'Sort by Username\'>Username</a></td>\r\n<td bgcolor={_caterowc} align=center><a href=\'index.php?prog=ucp::pm::clist&type={type}&sort=online\' title=\'Sort by Online/Offline\'>Del/Info</a></td></tr>\r\n<!-- list begin -->\r\n{contact_list}\r\n<!-- list end -->\r\n</tr>\r\n<tr><td bgcolor={_caterowc} align=center colspan=2><input type=text name=buddy><input type=submit name=\'add_buddy\' value=\'ADD !\'></td></tr>\r\n<tr><td bgcolor={_caterowc} align=right colspan=2>[<a href=\'javascript:Checkall(this.FORM)\'>Delete All</a>] &nbsp;[<a href=\'javascript:UnCheckall(this.FORM)\'>Reset</a>] &nbsp;<input type=submit name=\'delete_submit\' value=\'DELETE !\' onClick=\'return confirm("Are you sure to delete the selected buddy?\\nThis task cannot undo.")\'>&nbsp;</td></tr>\r\n</table></td></tr></table>\r\n</form>\r\n<center>\r\nPowered by: Celeste 2003 beta 1&nbsp;&nbsp;\r\n<font class=smalltext>{bench}</font>\r\n</center>\r\n</body>\r\n</html>')
