  require_once("./libs/requires.php"); require_once("$CONFIG[MWCHAT_Libs]/header.php"); if ($Type == "commands") { $LangInclude = Load_Language("defs.php", "en"); require_once($LangInclude); echo "</CENTER>\n"; $hModules = opendir("$CONFIG[MWCHAT_Modules]"); $rgFiles = array(); while($szSrtFile = readdir($hModules)) { array_push($rgFiles, $szSrtFile); } sort($rgFiles); reset($CONFIG[MWCHAT_Command_Aliases]); while(list($szRealCmd, $szAliasCmd) = each($CONFIG[MWCHAT_Command_Aliases])) { $rgHelpAliases[$szRealCmd] = $szAliasCmd; } echo "<H3>$LOCALE[HELP_COMMAND_Title]:</H3>\n"; echo "<B>$LOCALE[HELP_COMMAND_Type]</B><BR><BR>"; $szCounter = "1"; while(list($szNull, $szFilename) = each($rgFiles)) { if ($szFilename != "." && $szFilename != ".." && $szFilename != "index.php" && $szFilename != ".htaccess") { $szFilename = preg_split("/.php/", $szFilename); $szLocaleStrTitle = "DEFS_". $szFilename[0]; $szLocaleStrDesc = "HELP_COMMAND_". $szFilename[0]; $szTmpAliasName = $szFilename[0]; if (isset($rgHelpAliases[$szTmpAliasName])) { $szAliasName = ", /$rgHelpAliases[$szTmpAliasName]"; } echo "<FONT COLOR=\"$CONFIG[Color_StatusValues]\"><B>/$szFilename[0] $szAliasName</B></FONT COLOR> $LOCALE[$szLocaleStrTitle]<BR>&nbsp;&nbsp;&nbsp;<font size='2'>$LOCALE[$szLocaleStrDesc]</font size><BR><BR>\n"; unset($szAliasName); } } closedir($hModules); } else { echo "  </CENTER><FONT SIZE=4><B>$LOCALE[HELP_Title]:</B></FONT SIZE> <FONT COLOR=\"$CONFIG[Color_StatusValues]\"><I>" . strtolower($Query) . "</I></FONT COLOR><BR><BR>\n"; $szHelpLocale = "HELP_" . $Query; if (!isset($LOCALE[$szHelpLocale])) { $LOCALE[$szHelpLocale] = $LOCALE[HELP_Unavailable]; } echo "$LOCALE[$szHelpLocale]\n"; } echo "  <CENTER>\n"; echo "   <BR><BR><HR><A HREF=\"#\" onClick=\"self.close()\">$LOCALE[COMMON_Click_Close]</A>\n"; echo "  </CENTER>\n"; require_once("$CONFIG[MWCHAT_Libs]/footer.php"); ?>
