  require_once("./libs/requires.php"); $STATUS[Focus] = "Login.Username"; if (isset($Logout)) { ChatLog($LOCALE[ERROR_Logout]); Remove_User($Username); Error("ERROR_Logout"); } $FooterJava = "  <SCRIPT LANGUAGE=\"JavaScript\" TYPE=\"text/JavaScript\">\n"; $FooterJava .= "   <!--\n"; $FooterJava .= "     document.Login.Resolution.value=screen.width;\n"; $FooterJava .= "     if (window != top) top.location.href = location.href;\n"; $FooterJava .= "     document.Login.JavaScript.value='JS_ENABLED';\n"; $FooterJava .= "   // -->\n"; $FooterJava .= "  </SCRIPT>\n"; if ($ErrorMessage) { $ErrorMessage = rawurldecode($ErrorMessage); $ErrorMessage = base64_decode($ErrorMessage); if (!isset($LOCALE[$ErrorMessage])) { $LOCALE[$ErrorMessage] = "An unknown error occured. EX: $ErrorMessage"; } if ($ErrorMessage == "ERROR_PHPV") { $LOCALE[$ErrorMessage] = $LOCALE[$ErrorMessage] . ": $CONFIG[PHP_Version]"; } ChatLog("$LOCALE[ERROR_Title] - $LOCALE[$ErrorMessage]"); if ($ErrorMessage == "ERROR_License") { header("Location: license.php?ErrorMessage=$ErrorMessage&Lang=$Lang"); exit; } $szErrMsg = "     <H4>\n"; $szErrMsg .= "       $LOCALE[ERROR_Title](" . HelpLink($ErrorMessage) . "): <FONT COLOR=\"$CONFIG[Color_Foreground]\">$LOCALE[$ErrorMessage]</FONT COLOR></A>\n"; $szErrMsg .= "     </H4>\n"; } require_once("$CONFIG[MWCHAT_Libs]/header.php"); echo "    <FORM AUTOCOMPLETE=\"OFF\" NAME=Login ACTION=\"login.php\" METHOD=POST>\n"; echo "$szErrMsg"; echo "     <BR><BR><TABLE>\n"; echo "      <TR>\n"; echo "       <TD>\n"; echo "        <FONT COLOR=\"$CONFIG[Color_Foreground]\">\n"; echo "          <B>$LOCALE[INDEX_Name]</B> (" . HelpLink('Username') . "):\n"; echo "        </FONT COLOR>\n"; echo "       </TD>\n"; echo "       <TD>\n"; echo "        <INPUT TYPE=TEXT NAME=Username TABINDEX=1 MAXLENGTH=10 SIZE=21>\n"; echo "       </TD>\n"; echo "      </TR>\n"; if ($CONFIG[Locales] == "true") { echo "      <TR>\n"; echo "       <TD>\n"; echo "        <FONT COLOR=\"$CONFIG[Color_Foreground]\">\n"; echo "          <B>$LOCALE[INDEX_Language]</B> (" . HelpLink('Language') . "):\n"; echo "        </FONT COLOR>\n"; echo "       </TD>\n"; echo "       <TD>\n"; echo "        <FONT COLOR=\"$CONFIG[Color_Foreground]\">\n"; echo "          <SELECT TABINDEX=3 NAME=Lang>\n"; reset($CONFIG[Locales_Types]); while(list($ArrayPointer, $LocaleType) = each($CONFIG[Locales_Types])) { $Languages = explode(":", $LocaleType); if ($Languages[0] == $CONFIG[Locales_Default] and !isset($Lang)) { echo "            <OPTION SELECTED VALUE=\"$Languages[0]\">$Languages[1]</OPTION>\n"; } elseif ($Languages[0] == $Lang) { echo "            <OPTION SELECTED VALUE=\"$Languages[0]\">$Languages[1]</OPTION>\n"; } else { echo "            <OPTION VALUE=\"$Languages[0]\">$Languages[1]</OPTION>\n"; } } echo "          </SELECT>\n"; echo "        </FONT COLOR>\n"; echo "       </TD>\n"; echo "      </TR>\n"; } echo "      <TR ALIGN=CENTER>\n"; echo "       <TD COLSPAN=2>\n"; echo "        <INPUT TYPE=SUBMIT TABINDEX=4 VALUE=\"$LOCALE[COMMON_Login_Button]\">\n"; echo "       </TD>\n"; echo "      </TR>\n"; echo "      <INPUT TYPE=HIDDEN NAME=\"Action\" VALUE=\"start_login\">\n"; echo "      <INPUT TYPE=HIDDEN NAME=\"JavaScript\">\n"; echo "      <INPUT TYPE='HIDDEN' NAME='Resolution' VALUE='800'>\n"; echo "     </TABLE>\n"; echo "    </FORM>\n"; require_once("$CONFIG[MWCHAT_Libs]/footer.php"); ?>
