<?PHP
  function Post_Public($szPostUser, $szPostMsg) { global $Room; global $CONN; global $STATUS; $szPostMsg = Profanity_Check($szPostMsg, "clean"); $szPostMsg = "<FONT COLOR=\"$STATUS[TextColor]\">$szPostMsg</FONT COLOR>"; $szPostMsg = addslashes($szPostMsg); $rgUsers_SELECT = db_query(Validate(10), $CONN); while($rgUsers = db_fetch($rgUsers_SELECT)) { $rgEncryptMsg = Encryption("encode", $szPostMsg, $rgUsers[username], ""); $rgText_INSERT = db_query("INSERT INTO chat_text VALUES(NULL, '$rgUsers[username]', '$szPostUser', 'no', '$Room', '$rgEncryptMsg[0]', '$rgEncryptMsg[1]', NULL)", $CONN); } } ?>
