<?PHP
  function TimeStamp($Username) { global $CONFIG; global $CONN; $rgLobby_SELECT = db_query(Validate(3), $CONN); while($rgTimes = db_fetch($rgLobby_SELECT)) { $szUserTime_Year = substr($rgTimes[timestamp], 0, 4); $szUserTime_Month = substr($rgTimes[timestamp], 4, 2); $szUserTime_Day = substr($rgTimes[timestamp], 6, 2); $szUserTime_Hour = substr($rgTimes[timestamp], 8, 2); $szUserTime_Min = substr($rgTimes[timestamp], 10, 2); $szUserTime_Sec = substr($rgTimes[timestamp], 12, 2); $szCurrentTime = date("YmdHis", time()); $szTime_Check = date ("YmdHis", mktime (($szUserTime_Hour + $CONFIG[Room_Expire_Hours]),($szUserTime_Min + $CONFIG[Room_Expire_Minutes]),($szUserTime_Sec + $CONFIG[Room_Expire_Seconds]),$szUserTime_Month,$szUserTime_Day,$szUserTime_Year)); if ($szTime_Check < $szCurrentTime) { Remove_User($Username); Error("ERROR_Expired"); } } return("true"); } ?>
