  if ($STATUS[Registered] == "yes") { Post_System("<U><B>$LOCALE[DIALOG_Available_Ignored]:</B></U>", "syntax", $Room); $STATUS[Ignored] = ereg_replace("\*\*", "\*", $STATUS[Ignored]); $rgIgnored = explode("\*", $STATUS[Ignored]); while (list($szNull, $szUser) = each($rgIgnored)) { if ($szUser != "") { $szPrinted = "yes"; $szUser = ereg_replace("\*", "", $szUser); Post_System("<I>$szUser $LOCALE[DIALOG_Is_Ignored]</I>", "syntax", $Room); } } if ($szPrinted != "yes") { Post_System("<I>$LOCALE[DIALOG_No_Ignored]</I>", "syntax", $Room); } } else { Post_System("<B>$LOCALE[DIALOG_Syntax_Error]</B> - /$rgMatches[1] $LOCALE[COMMON_Registered_Only]", "syntax", $Room); } ?>
