  require_once("./libs/requires.php"); require_once("$CONFIG[MWCHAT_Libs]/security.php"); TimeStamp($Username); require_once("$CONFIG[MWCHAT_Libs]/header.php"); echo "  </CENTER>\n"; if ($STATUS[Admin] == "yes" and $Action == "viewlogs") { echo "  <TABLE BORDER=0 CELLSPACING=5 CELLPADDING=5>\n"; echo "   <TR>\n"; echo "     <TD>\n"; echo "        <B><U>$LOCALE[COMMON_Username]</B></U>\n"; echo "     </TD>\n"; echo "     <TD>\n"; echo "        <B><U>$LOCALE[PRINT_IP]</B></U>\n"; echo "     </TD>\n"; echo "     <TD>\n"; echo "        <B><U>$LOCALE[PRINT_Agent]</B></U>\n"; echo "     </TD>\n"; echo "     <TD>\n"; echo "        <B><U>$LOCALE[PRINT_Time]</B></U>\n"; echo "     </TD>\n"; echo "     <TD>\n"; echo "        <B><U>$LOCALE[PRINT_Misc]</B></U>\n"; echo "     </TD>\n"; echo "   </TR>\n"; $rgLog_SELECT = db_query(Validate(45), $CONN); while($rgData = db_fetch($rgLog_SELECT)) { if (!empty($User)) { if ($User == $rgData[username]) { echo "<TR><TD>$rgData[username]</TD><TD>$rgData[ip]</TD><TD>$rgData[agent]</TD><TD>$rgData[logtime]</TD><TD>$rgData[misc]</TD></TR>\n"; } } else { echo "<TR><TD>$rgData[username]</TD><TD>$rgData[ip]</TD><TD>$rgData[agent]</TD><TD>$rgData[logtime]</TD><TD>$rgData[misc]</TD></TR>\n"; } } echo "</TABLE>\n"; } elseif ($STATUS[Registered] == "yes" and $Action == "print") { $rgText_SELECT = db_query(Validate(1003), $CONN); while($rgData = db_fetch($rgText_SELECT)) { if (preg_match("/-/", $rgData[from_username])) { $rgData[from_username] = "[$LOCALE[COMMON_System_Message]]"; } $rgChatMsg = Encryption("decode", $rgData[message], $Username, $rgData[encrypt_vector]); $rgChatMsg[0] = stripslashes($rgChatMsg[0]); $rgChatMsg[0] = strip_tags($rgChatMsg[0]); if ($rgData[is_private] == "yes") { if ($rgData[from_username] == $rgData[to_username]) { echo "  $rgData[from_username] --> $rgData[private_info]: $rgChatMsg[0]<BR>\n"; } else { echo "  $rgData[from_username] --> $rgData[to_username]: $rgChatMsg[0]<BR>\n"; } } else { echo "  $rgData[from_username