<?PHP
  require_once("./libs/requires.php"); require_once("$CONFIG[MWCHAT_Libs]/security.php"); TimeStamp($Username); $STATUS[Silentheader] = "true"; require_once("$CONFIG[MWCHAT_Libs]/header.php"); echo "  <H2>\n"; echo "    $LOCALE[WHOIS_Title]\n"; echo "  </H2>\n"; echo "  <HR>\n"; echo "  </CENTER>\n"; if ($STATUS[Registered] == "yes" or $STATUS[Admin] == "yes") { $rgRegistered_SELECT = db_query(Validate(1001), $CONN); $szRegistered = db_numrows($rgRegistered_SELECT); if ($szRegistered == "0") { echo "  <BR><BR> <CENTER><B>$Query $LOCALE[WHOIS_No_Profile]</B></CENTER> <BR><BR><BR><BR>\n"; } while($rgInfo = db_fetch($rgRegistered_SELECT)) { if (!$rgInfo[occupation]) { $rgInfo[occupation] = "$LOCALE[WHOIS_Not_Specified]"; } if (!$Status) { $Status = "$LOCALE[WHOIS_Not_Specified]"; } if (!$rgInfo[location]) { $rgInfo[location] = "$LOCALE[WHOIS_Not_Specified]"; } if (!$rgInfo[hobbies]) { $rgInfo[hobbies] = "$LOCALE[WHOIS_Not_Specified]"; } echo "  <TABLE BORDER=0 CELLPADDING=5 CELLSPACING=10>\n"; echo "   <TR>\n"; echo "     <TD>\n"; echo "        <FONT COLOR=\"$CONFIG[Color_StatusName]\"><B>$LOCALE[COMMON_Username]</B> (" . HelpLink('Username') . "):</FONT COLOR>\n"; echo "        <FONT COLOR=\"$CONFIG[Color_StatusValues]\">$rgInfo[username]</FONT COLOR>\n"; echo "     </TD>\n"; echo "     <TD>\n"; echo "       <FONT COLOR=\"$CONFIG[Color_StatusName]\"><B>$LOCALE[COMMON_Status]</B> (" . HelpLink('Status') . "):</FONT COLOR>\n"; echo "       <FONT COLOR=\"$CONFIG[Color_StatusValues]\"> ". rawurldecode($Status) . "</FONT COLOR>\n"; echo "     </TD>\n"; echo "   </TR>\n"; echo "   <TR>\n"; echo "     <TD>\n"; echo "        <FONT COLOR=\"$CONFIG[Color_StatusName]\"><B>$LOCALE[COMMON_Job]</B> (" . HelpLink('Occupation') . "):</FONT COLOR>\n"; echo "        <FONT COLOR=\"$CONFIG[Color_StatusValues]\">$rgInfo[occupation]</FONT COLOR>\n"; echo "     </TD>\n"; echo "     <TD>\n"; echo "        <FONT COLOR=\"$CONFIG[Color_StatusName]\"><B>$LOCALE[COMMON_Hobbies]</B> (" . HelpLink('Hobbies') . "):</FONT COLOR>\n"; echo "        <FONT COLOR=\"$CONFIG[Color_StatusValues]\">$rgInfo[hobbies]</FONT COLOR>\n"; echo "     </TD>\n"; echo "   </TR>\n"; echo "   <TR>\n"; echo "     <TD>\n"; echo "        <FONT COLOR=\"$CONFIG[Color_StatusName]\"><B>$LOCALE[COMMON_YOB]</B> (" . HelpLink('YOB') . "):</FONT COLOR>\n"; echo "        <FONT COLOR=\"$CONFIG[Color_StatusValues]\">$rgInfo[age]</FONT COLOR>\n"; echo "     </TD>\n"; echo "     <TD>\n"; echo "        <FONT COLOR=\"$CONFIG[Color_StatusName]\"><B>$LOCALE[COMMON_Location]</B> (" . HelpLink('Location') . "):</FONT COLOR>\n"; echo "        <FONT COLOR=\"$CONFIG[Color_StatusValues]\">$rgInfo[location]</FONT COLOR>\n"; echo "     </TD>\n"; echo "   </TR>\n"; echo "   <TR>\n"; echo "     <TD COLSPAN=2>\n"; echo "        <FONT COLOR=\"$CONFIG[Color_StatusName]\"><B>$LOCALE[COMMON_Comments]</B> (" . HelpLink('Comments') . "):</FONT COLOR><BR>\n"; echo "        <TEXTAREA WRAP=VIRTUAL ROWS=10 COLS=30 NAME=Comments TABINDEX=15>$rgInfo[comments]</TEXTAREA>\n"; echo "     </TD>\n"; echo "   </TR>\n"; echo "  </TABLE>\n"; break; } echo "  <CENTER>\n"; } else { echo "  <BR><BR> <CENTER><B>/whois $LOCALE[COMMON_Registered_Only]</B></CENTER> <BR><BR><BR><BR>\n"; } echo "  <HR><CENTER><A HREF=\"#\" onClick=\"self.close()\">$LOCALE[COMMON_Click_Close]</A></CENTER>\n"; require_once("$CONFIG[MWCHAT_Libs]/footer.php"); ?>
